import React, { useRef, useEffect, useState } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import $ from 'jquery';
import 'datatables.net';
import 'datatables.net-dt/css/dataTables.dataTables.min.css';
import Breadcrumbs from '../Breadcrumbs';
import commonData from '../../data/commonData.json';

const ProjectLevel = () => {
  const navigate = useNavigate();
  const { accountId: urlAccountId } = useParams();
  const location = useLocation();
  const tableRef = useRef(null);

  const [projects, setProjects] = useState([]);
  const [filteredProjects, setFilteredProjects] = useState([]);
  const [columnFilters, setColumnFilters] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [activeMonth, setActiveMonth] = useState(null);
  const [activeYear, setActiveYear] = useState(null);
  const [activeAccountId, setActiveAccountId] = useState(null);
  const [activeAccountName, setActiveAccountName] = useState('Loading...');
  const [activeSbu, setActiveSbu] = useState(null);
  const [activeProjectType, setActiveProjectType] = useState(null);

  const BACKEND_URL = 'http://localhost:8081';

  const formatCurrency = (value = 0) =>
    value.toLocaleString(commonData.currencySettings.locale, {
      style: 'currency',
      currency: commonData.currencySettings.currency,
      minimumFractionDigits: commonData.currencySettings.minimumFractionDigits,
      maximumFractionDigits: commonData.currencySettings.maximumFractionDigits
    });

  const getMonthName = (monthNumber) => {
    const d = new Date();
    d.setMonth(monthNumber - 1);
    return d.toLocaleString('en-US', { month: 'long' });
  };

  // Fetch project data
  useEffect(() => {
    const fetchProjectData = async (month, year, accId, projectType) => {
      setLoading(true);
      setError(null);
      try {
        const payload = { month, year, accId, projectType };
        const resp = await fetch(`${BACKEND_URL}/api/project`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(payload)
        });
        if (!resp.ok) {
          const errText = await resp.text();
          throw new Error(errText || resp.statusText);
        }
        const data = await resp.json();
        const list = Array.isArray(data) ? data : [data].filter(Boolean);
        setProjects(list);
        // initialize account name
        if (list.length && list[0].accountName) {
          setActiveAccountName(list[0].accountName);
        }
      } catch (e) {
        console.error('Error fetching project data:', e);
        setError(`Failed to load project data: ${e.message}`);
      } finally {
        setLoading(false);
      }
    };

    let month = location.state?.month || sessionStorage.getItem('lastFetchedProjectMonth');
    let year = location.state?.year || sessionStorage.getItem('lastFetchedProjectYear');
    const accId = urlAccountId;
    let projectType = location.state?.projectType || sessionStorage.getItem('lastFetchedProjectType');
    let sbu = location.state?.sbu || sessionStorage.getItem('lastFetchedProjectSbu');
    let accName = location.state?.accountName || sessionStorage.getItem('lastFetchedProjectAccountName');

    month = month ? parseInt(month, 10) : null;
    year = year ? parseInt(year, 10) : null;

    if (month && year && accId) {
      setActiveMonth(month);
      setActiveYear(year);
      setActiveAccountId(accId);
      if (accName) setActiveAccountName(accName);
      setActiveProjectType(projectType);
      setActiveSbu(sbu);

      sessionStorage.setItem('lastFetchedProjectMonth', month.toString());
      sessionStorage.setItem('lastFetchedProjectYear', year.toString());
      sessionStorage.setItem('lastFetchedProjectAccountName', accName || '');
      sessionStorage.setItem('lastFetchedProjectType', projectType || '');
      sessionStorage.setItem('lastFetchedProjectSbu', sbu || '');

      fetchProjectData(month, year, accId, projectType);
    } else {
      setError('Missing month, year, or account ID. Please navigate here with proper parameters.');
      setLoading(false);
    }
  }, [location.state, urlAccountId]);

  // Utility to get unique filter options for a column
  const getUniqueOptions = (columnKey) => {
    if (!projects.length) return ['All'];
    let values = projects.map((p) => p[columnKey]);
    if (columnKey === 'revenue') {
      values = values.map((v) => formatCurrency(v));
    }
    const unique = [...new Set(values.map((v) => String(v)))].sort();
    return ['All', ...unique];
  };

  // Apply filters to projects
  const applyFilters = (data, filters) => {
    return data.filter((project) => {
      return Object.entries(filters).every(([key, val]) => {
        if (!val || val === 'All') return true;
        let projVal = project[key];
        if (key === 'revenue') projVal = formatCurrency(projVal);
        return String(projVal) === String(val);
      });
    });
  };

  // Update filteredProjects when projects or filters change
  useEffect(() => {
    setFilteredProjects(applyFilters(projects, columnFilters));
  }, [projects, columnFilters]);

  // Initialize DataTable on filteredProjects change
  useEffect(() => {
    const $tbl = $(tableRef.current);
    if (!loading) {
      if ($.fn.DataTable.isDataTable($tbl)) {
        $tbl.DataTable().destroy();
      }
      $tbl.DataTable({
        paging: true,
        searching: true,
        ordering: true,
        info: true,
        autoWidth: false
      });
    }
    return () => {
      if ($.fn.DataTable.isDataTable($tbl)) {
        $tbl.DataTable().destroy();
      }
    };
  }, [loading, filteredProjects]);

  const handleFilterChange = (columnKey, value) => {
    setColumnFilters((prev) => ({
      ...prev,
      [columnKey]: value === 'All' ? null : value
    }));
  };

  const handleDownloadData = () => {
    if (!filteredProjects.length) return;
    const headers = [
      'Project ID',
      'Project Name',
      'Total Associates',
      'Company Hours',
      'Client Hours',
      'Variance Hours',
      'Revenue'
    ];
    const rows = filteredProjects.map((p) => [
      p.projectId,
      p.projectName,
      p.totalAssociatesCount,
      p.totalCompanyHours,
      p.totalClientHours,
      p.varianceHours,
      formatCurrency(p.revenue)
    ]);
    const csv = [
      headers.join(','),
      ...rows.map((r) => r.map((c) => `"${c}"`).join(','))
    ].join('\n');
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    const monthName = getMonthName(activeMonth);
    a.href = url;
    a.download = `Projects_Account_${activeAccountId}_${monthName}_${activeYear}.csv`;
    document.body.appendChild(a);
    a.click();
    a.remove();
    URL.revokeObjectURL(url);
  };

  // Breadcrumb path
  const breadcrumbPath = [
    { name: 'PMO Dashboard', page: '' },
    { name: 'Revenue Forecast - Early View', page: 'upload' },
    ...(activeSbu
      ? [{ name: `${activeSbu} SBU Level`, page: 'sbu', state: { month: activeMonth, year: activeYear, sbu: activeSbu } }]
      : []),
    { name: 'Account Level', page: 'accounts', state: { month: activeMonth, year: activeYear, sbu: activeSbu } },
    ...(activeProjectType
      ? [
          {
            name: `${activeProjectType} Project Type`,
            page: `accounts/${activeAccountId}/project-types`,
            state: { month: activeMonth, year: activeYear, sbu: activeSbu, accId: activeAccountId }
          }
        ]
      : []),
    { name: `Projects (${activeAccountName})`, page: `accounts/${activeAccountId}/projects` }
  ];

  if (loading) {
    return (
      <div className="min-vh-100 d-flex justify-content-center align-items-center">
        <div className="spinner-border text-primary" role="status" />
        <p className="ms-3 text-primary">Loading Project Data...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-vh-100 d-flex flex-column justify-content-center align-items-center text-danger">
        <p className="fs-4">Error: {error}</p>
        <button
          className="btn btn-primary mt-3"
          onClick={() =>
            navigate('/accounts', {
              state: { month: activeMonth, year: activeYear, sbu: activeSbu }
            })
          }
        >
          Go to Account Level
        </button>
      </div>
    );
  }

  return (
    <div
      className="min-vh-100 d-flex flex-column align-items-center px-3 py-4"
      style={{
        background: 'linear-gradient(to bottom right, #f5f7fa, #e9ecef)',
        fontFamily: "'Poppins', sans-serif'"
      }}
    >
      <div
        className="card shadow p-4 p-md-5 w-100"
        style={{
          maxWidth: '1200px',
          backgroundColor: '#ffffffdd',
          borderRadius: '1rem'
        }}
      >
        <Breadcrumbs path={breadcrumbPath} />

        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2 className="mb-0 fw-semibold text-dark">
            üìÅ Project Level Overview {activeAccountName && `for ${activeAccountName}`}
          </h2>
          {projects.length > 0 && (
            <button
              className="btn btn-outline-success rounded-pill px-3"
              onClick={handleDownloadData}
              title="Download Project Data"
            >
              Download Data
            </button>
          )}
        </div>

        <div className="table-responsive rounded">
          <table
            ref={tableRef}
            className="display table table-hover align-middle table-borderless mb-0"
            style={{ width: '100%' }}
          >
            <thead
              className="text-white"
              style={{ background: 'linear-gradient(to right, #3b82f6, #2563eb)' }}
            >
              <tr>
                <th>
                  Project ID
                  <select
                    className="form-select form-select-sm mt-1"
                    onChange={(e) => handleFilterChange('projectId', e.target.value)}
                    value={columnFilters.projectId || 'All'}
                  >
                    {getUniqueOptions('projectId').map((opt) => (
                      <option key={opt} value={opt}>
                        {opt}
                      </option>
                    ))}
                  </select>
                </th>
                <th>
                  Project Name
                  <select
                    className="form-select form-select-sm mt-1"
                    onChange={(e) => handleFilterChange('projectName', e.target.value)}
                    value={columnFilters.projectName || 'All'}
                  >
                    {getUniqueOptions('projectName').map((opt) => (
                      <option key={opt} value={opt}>
                        {opt}
                      </option>
                    ))}
                  </select>
                </th>
                <th>
                  Total Associates
                  <select
                    className="form-select form-select-sm mt-1"
                    onChange={(e) => handleFilterChange('totalAssociatesCount', e.target.value)}
                    value={columnFilters.totalAssociatesCount || 'All'}
                  >
                    {getUniqueOptions('totalAssociatesCount').map((opt) => (
                      <option key={opt} value={opt}>
                        {opt}
                      </option>
                    ))}
                  </select>
                </th>
                <th>
                  Company Hours
                  <select
                    className="form-select form-select-sm mt-1"
                    onChange={(e) => handleFilterChange('totalCompanyHours', e.target.value)}
                    value={columnFilters.totalCompanyHours || 'All'}
                  >
                    {getUniqueOptions('totalCompanyHours').map((opt) => (
                      <option key={opt} value={opt}>
                        {opt}
                      </option>
                    ))}
                  </select>
                </th>
                <th>
                  Client Hours
                  <select
                    className="form-select form-select-sm mt-1"
                    onChange={(e) => handleFilterChange('totalClientHours', e.target.value)}
                    value={columnFilters.totalClientHours || 'All'}
                  >
                    {getUniqueOptions('totalClientHours').map((opt) => (
                      <option key={opt} value={opt}>
                        {opt}
                      </option>
                    ))}
                  </select>
                </th>
                <th>
                  Variance Hours
                  <select
                    className="form-select form-select-sm mt-1"
                    onChange={(e) => handleFilterChange('varianceHours', e.target.value)}
                    value={columnFilters.varianceHours || 'All'}
                  >
                    {getUniqueOptions('varianceHours').map((opt) => (
                      <option key={opt} value={opt}>
                        {opt}
                      </option>
                    ))}
                  </select>
                </th>
                <th>
                  Revenue
                  <select
                    className="form-select form-select-sm mt-1"
                    onChange={(e) => handleFilterChange('revenue', e.target.value)}
                    value={columnFilters.revenue || 'All'}
                  >
                    {getUniqueOptions('revenue').map((opt) => (
                      <option key={opt} value={opt}>
                        {opt}
                      </option>
                    ))}
                  </select>
                </th>
                <th className="text-center">View</th>
              </tr>
            </thead>
            <tbody>
              {filteredProjects.map((project) => (
                <tr key={project.projectId}>
                  <td>{project.projectId}</td>
                  <td>{project.projectName}</td>
                  <td>{project.totalAssociatesCount}</td>
                  <td>{project.totalCompanyHours}</td>
                  <td>{project.totalClientHours}</td>
                  <td>{project.varianceHours}</td>
                  <td>{formatCurrency(project.revenue)}</td>
                  <td className="text-center">
                    <button
                      className="btn btn-sm btn-outline-primary rounded-circle"
                      onClick={() =>
                        navigate(`/projects/${project.projectId}/associates`, {
                          state: {
                            projectId: project.projectId,
                            projectName: project.projectName,
                            accountId: project.accountId,
                            accountName: project.accountName,
                            month: activeMonth,
                            year: activeYear,
                            sbu: activeSbu,
                            projectType: activeProjectType
                          }
                        })
                      }
                      title="View Associates"
                    >
                      üîç
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default ProjectLevel;
