import React, { useRef, useEffect, useState } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import $ from 'jquery';
import 'datatables.net';
import 'datatables.net-dt/css/dataTables.dataTables.min.css';
import Breadcrumbs from '../Breadcrumbs';
import commonData from '../../data/commonData.json';

const ProjectLevel = () => {
  const navigate = useNavigate();
  const { accountId: urlAccountId } = useParams();
  const location = useLocation();
  const tableRef = useRef(null);

  // Raw and filtered data
  const [projects, setProjects] = useState([]);
  const [filteredProjects, setFilteredProjects] = useState([]);

  // Initialize filters for each data column to "no filter"
  const initialFilters = {
    projectId: null,
    projectName: null,
    totalAssociatesCount: null,
    totalCompanyHours: null,
    totalClientHours: null,
    varianceHours: null,
    revenue: null
  };
  const [columnFilters, setColumnFilters] = useState(initialFilters);

  // UI state
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Context state
  const [activeMonth, setActiveMonth] = useState(null);
  const [activeYear, setActiveYear] = useState(null);
  const [activeAccountId, setActiveAccountId] = useState(null);
  const [activeAccountName, setActiveAccountName] = useState('Loading...');
  const [activeSbu, setActiveSbu] = useState(null);
  const [activeProjectType, setActiveProjectType] = useState(null);

  const BACKEND_URL = 'http://localhost:8081';

  // Formatting helpers
  const formatCurrency = (val = 0) =>
    val.toLocaleString(commonData.currencySettings.locale, {
      style: 'currency',
      currency: commonData.currencySettings.currency,
      minimumFractionDigits: commonData.currencySettings.minimumFractionDigits,
      maximumFractionDigits: commonData.currencySettings.maximumFractionDigits
    });

  const getMonthName = (num) => {
    const d = new Date();
    d.setMonth(num - 1);
    return d.toLocaleString('en-US', { month: 'long' });
  };

  // 1Ô∏è‚É£ Fetch project data when context changes
  useEffect(() => {
    const fetchProjectData = async (month, year, accId, projectType) => {
      setLoading(true);
      setError(null);

      try {
        const resp = await fetch(`${BACKEND_URL}/api/project`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ month, year, accId, projectType })
        });
        if (!resp.ok) throw new Error(await resp.text());

        const raw = await resp.json();
        const list = Array.isArray(raw) ? raw : [raw].filter(Boolean);
        setProjects(list);

        if (list.length && list[0].accountName) {
          setActiveAccountName(list[0].accountName);
        }
      } catch (e) {
        console.error(e);
        setError(`Failed to load project data: ${e.message}`);
      } finally {
        setLoading(false);
      }
    };

    // derive context from URL / state / sessionStorage
    let m = location.state?.month ?? sessionStorage.getItem('lastFetchedProjectMonth');
    let y = location.state?.year ?? sessionStorage.getItem('lastFetchedProjectYear');
    let accType = location.state?.projectType ?? sessionStorage.getItem('lastFetchedProjectType');
    let sbu = location.state?.sbu ?? sessionStorage.getItem('lastFetchedProjectSbu');
    let acctName = location.state?.accountName ?? sessionStorage.getItem('lastFetchedProjectAccountName');

    m = m ? parseInt(m, 10) : null;
    y = y ? parseInt(y, 10) : null;

    if (m && y && urlAccountId) {
      setActiveMonth(m);
      setActiveYear(y);
      setActiveAccountId(urlAccountId);
      if (acctName) setActiveAccountName(acctName);
      setActiveProjectType(accType);
      setActiveSbu(sbu);

      sessionStorage.setItem('lastFetchedProjectMonth', m);
      sessionStorage.setItem('lastFetchedProjectYear', y);
      sessionStorage.setItem('lastFetchedProjectAccountName', acctName || '');
      sessionStorage.setItem('lastFetchedProjectType', accType || '');
      sessionStorage.setItem('lastFetchedProjectSbu', sbu || '');

      fetchProjectData(m, y, urlAccountId, accType);
    } else {
      setError('Missing month, year, or account ID. Please navigate here with all parameters.');
      setLoading(false);
    }
  }, [location.state, urlAccountId]);

  // 2Ô∏è‚É£ Helper: get unique dropdown options for each column
  const getUniqueOptions = (key) => {
    if (!projects.length) return ['All'];
    let vals = projects.map((p) =>
      key === 'revenue' ? formatCurrency(p[key]) : p[key]
    );
    vals = vals.map((v) => String(v));
    return ['All', ...[...new Set(vals)].sort()];
  };

  // 3Ô∏è‚É£ applyFilters: filter raw projects using every active column filter
  const applyFilters = (data, filters) =>
    data.filter((row) =>
      Object.entries(filters).every(([col, val]) => {
        if (!val || val === 'All') return true;

        let cell = row[col];
        if (col === 'revenue') cell = formatCurrency(cell);
        return String(cell) === String(val);
      })
    );

  // 4Ô∏è‚É£ Regenerate filteredProjects when raw data or filters change
  useEffect(() => {
    setFilteredProjects(applyFilters(projects, columnFilters));
  }, [projects, columnFilters]);

  // 5Ô∏è‚É£ Initialize / destroy DataTable on filteredProjects change
  useEffect(() => {
    const $tbl = $(tableRef.current);
    if (!loading) {
      if ($.fn.DataTable.isDataTable($tbl)) {
        $tbl.DataTable().destroy();
      }
      $tbl.DataTable({
        paging: true,
        searching: true,
        ordering: true,
        info: true,
        autoWidth: false
      });
    }
    return () => {
      if ($.fn.DataTable.isDataTable($tbl)) {
        $tbl.DataTable().destroy();
      }
    };
  }, [loading, filteredProjects]);

  // Handler to update one column‚Äôs filter
  const handleFilterChange = (col, val) => {
    setColumnFilters((prev) => ({
      ...prev,
      [col]: val === 'All' ? null : val
    }));
  };

  // Download CSV of the **filtered** view
  const handleDownloadData = () => {
    if (!filteredProjects.length) return;

    const headers = [
      'Project ID',
      'Project Name',
      'Total Associates',
      'Company Hours',
      'Client Hours',
      'Variance Hours',
      'Revenue'
    ];
    const rows = filteredProjects.map((p) => [
      p.projectId,
      p.projectName,
      p.totalAssociatesCount,
      p.totalCompanyHours,
      p.totalClientHours,
      p.varianceHours,
      formatCurrency(p.revenue)
    ]);

    const csvContent =
      [headers.join(','), ...rows.map((r) => r.map((c) => `"${c}"`).join(','))].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    const fname = `Projects_Account_${activeAccountId}_${getMonthName(activeMonth)}_${activeYear}.csv`;
    a.href = url;
    a.download = fname;
    document.body.appendChild(a);
    a.click();
    a.remove();
    URL.revokeObjectURL(url);
  };

  // Build breadcrumb path
  const breadcrumbPath = [
    { name: 'PMO Dashboard', page: '' },
    { name: 'Revenue Forecast - Early View', page: 'upload' },
    ...(activeSbu
      ? [
          {
            name: `${activeSbu} SBU Level`,
            page: 'sbu',
            state: { month: activeMonth, year: activeYear, sbu: activeSbu }
          }
        ]
      : []),
    {
      name: 'Account Level',
      page: 'accounts',
      state: { month: activeMonth, year: activeYear, sbu: activeSbu }
    },
    ...(activeProjectType
      ? [
          {
            name: `${activeProjectType} Project Type`,
            page: `accounts/${activeAccountId}/project-types`,
            state: {
              month: activeMonth,
              year: activeYear,
              sbu: activeSbu,
              accId: activeAccountId
            }
          }
        ]
      : []),
    {
      name: `Projects (${activeAccountName})`,
      page: `accounts/${activeAccountId}/projects`
    }
  ].filter(Boolean);

  // 6Ô∏è‚É£ Render states
  if (loading) {
    return (
      <div className="min-vh-100 d-flex justify-content-center align-items-center">
        <div className="spinner-border text-primary" role="status" />
        <p className="ms-3 text-primary">Loading Project Data...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-vh-100 d-flex flex-column justify-content-center align-items-center text-danger">
        <p className="fs-4">Error: {error}</p>
        <button
          className="btn btn-primary mt-3"
          onClick={() =>
            navigate('/accounts', {
              state: { month: activeMonth, year: activeYear, sbu: activeSbu }
            })
          }
        >
          Go to Account Level
        </button>
      </div>
    );
  }

  // 7Ô∏è‚É£ Main render
  return (
    <div
      className="min-vh-100 d-flex flex-column align-items-center px-3 py-4"
      style={{
        background: 'linear-gradient(to bottom right, #f5f7fa, #e9ecef)',
        fontFamily: "'Poppins', sans-serif'"
      }}
    >
      <div
        className="card shadow p-4 p-md-5 w-100"
        style={{
          maxWidth: '1200px',
          backgroundColor: '#ffffffdd',
          borderRadius: '1rem'
        }}
      >
        <Breadcrumbs path={breadcrumbPath} />

        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2 className="mb-0 fw-semibold text-dark">
            üìÅ Project Level Overview{' '}
            {activeAccountName && `for ${activeAccountName}`}
          </h2>
          {filteredProjects.length > 0 && (
            <button
              className="btn btn-outline-success rounded-pill px-3"
              onClick={handleDownloadData}
            >
              Download Data
            </button>
          )}
        </div>

        <div className="table-responsive rounded">
          <table
            ref={tableRef}
            className="display table table-hover align-middle table-borderless mb-0"
            style={{ width: '100%' }}
          >
            <thead
              className="text-white"
              style={{ background: 'linear-gradient(to right, #3b82f6, #2563eb)' }}
            >
              <tr>
                <th>
                  Project ID
                  <select
                    className="form-select form-select-sm mt-1"
                    value={columnFilters.projectId || 'All'}
                    onChange={(e) => handleFilterChange('projectId', e.target.value)}
                  >
                    {getUniqueOptions('projectId').map((opt) => (
                      <option key={opt} value={opt}>
                        {opt}
                      </option>
                    ))}
                  </select>
                </th>
                <th>
                  Project Name
                  <select
                    className="form-select form-select-sm mt-1"
                    value={columnFilters.projectName || 'All'}
                    onChange={(e) => handleFilterChange('projectName', e.target.value)}
                  >
                    {getUniqueOptions('projectName').map((opt) => (
                      <option key={opt} value={opt}>
                        {opt}
                      </option>
                    ))}
                  </select>
                </th>
                <th>
                  Total Associates
                  <select
                    className="form-select form-select-sm mt-1"
                    value={columnFilters.totalAssociatesCount || 'All'}
                    onChange={(e) =>
                      handleFilterChange('totalAssociatesCount', e.target.value)
                    }
                  >
                    {getUniqueOptions('totalAssociatesCount').map((opt) => (
                      <option key={opt} value={opt}>
                        {opt}
                      </option>
                    ))}
                  </select>
                </th>
                <th>
                  Company Hours
                  <select
                    className="form-select form-select-sm mt-1"
                    value={columnFilters.totalCompanyHours || 'All'}
                    onChange={(e) =>
                      handleFilterChange('totalCompanyHours', e.target.value)
                    }
                  >
                    {getUniqueOptions('totalCompanyHours').map((opt) => (
                      <option key={opt} value={opt}>
                        {opt}
                      </option>
                    ))}
                  </select>
                </th>
                <th>
                  Client Hours
                  <select
                    className="form-select form-select-sm mt-1"
                    value={columnFilters.totalClientHours || 'All'}
                    onChange={(e) =>
                      handleFilterChange('totalClientHours', e.target.value)
                    }
                  >
                    {getUniqueOptions('totalClientHours').map((opt) => (
                      <option key={opt} value={opt}>
                        {opt}
                      </option>
                    ))}
                  </select>
                </th>
                <th>
                  Variance Hours
                  <select
                    className="form-select form-select-sm mt-1"
                    value={columnFilters.varianceHours || 'All'}
                    onChange={(e) =>
                      handleFilterChange('varianceHours', e.target.value)
                    }
                  >
                    {getUniqueOptions('varianceHours').map((opt) => (
                      <option key={opt} value={opt}>
                        {opt}
                      </option>
                    ))}
                  </select>
                </th>
                <th>
                  Revenue
                  <select
                    className="form-select form-select-sm mt-1"
                    value={columnFilters.revenue || 'All'}
                    onChange={(e) => handleFilterChange('revenue', e.target.value)}
                  >
                    {getUniqueOptions('revenue').map((opt) => (
                      <option key={opt} value={opt}>
                        {opt}
                      </option>
                    ))}
                  </select>
                </th>
                <th className="text-center">View</th>
              </tr>
            </thead>
            <tbody>
              {filteredProjects.map((p) => (
                <tr key={p.projectId}>
                  <td>{p.projectId}</td>
                  <td>{p.projectName}</td>
                  <td>{p.totalAssociatesCount}</td>
                  <td>{p.totalCompanyHours}</td>
                  <td>{p.totalClientHours}</td>
                  <td>{p.varianceHours}</td>
                  <td>{formatCurrency(p.revenue)}</td>
                  <td className="text-center">
                    <button
                      className="btn btn-sm btn-outline-primary rounded-circle"
                      onClick={() =>
                        navigate(`/projects/${p.projectId}/associates`, {
                          state: {
                            projectId: p.projectId,
                            projectName: p.projectName,
                            accountId: p.accountId,
                            accountName: p.accountName,
                            month: activeMonth,
                            year: activeYear,
                            sbu: activeSbu,
                            projectType: activeProjectType
                          }
                        })
                      }
                    >
                      üîç
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default ProjectLevel;
