import React, { useRef, useEffect, useState } from 'react';

import { useNavigate, useParams, useLocation } from 'react-router-dom';

import $ from 'jquery';

import 'datatables.net';

import 'datatables.net-dt/css/dataTables.dataTables.min.css';

import { Bell, Download, Search, Users, Filter } from 'lucide-react';

import { Button } from '@/components/ui/button';

import { Card, CardContent, CardHeader } from '@/components/ui/card';

import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';

import { Badge } from '@/components/ui/badge';

import Breadcrumbs from './Breadcrumbs';

import commonData from '../../data/commonData.json';



const AssociateLevel = () => {

 const navigate = useNavigate();

 const { projectId: urlProjectId } = useParams();

 const location = useLocation();

 const tableRef = useRef(null);

 const dataTableInstance = useRef(null);

  

 const [associates, setAssociates] = useState([]);

 const [loading, setLoading] = useState(true);

 const [error, setError] = useState(null);

 const [activeMonth, setActiveMonth] = useState(null);

 const [activeYear, setActiveYear] = useState(null);

 const [activeProjectId, setActiveProjectId] = useState(null);

 const [activeProjectName, setActiveProjectName] = useState('Loading...');

 const [activeAccountId, setActiveAccountId] = useState(null);

 const [activeAccountName, setActiveAccountName] = useState('Loading...');

 const [filterOption, setFilterOption] = useState('showAll');



 const BACKEND_URL = 'http://localhost:8081';



 // Currency formatter from commonData.json

 const {

  locale,

  currency,

  minimumFractionDigits,

  maximumFractionDigits

 } = commonData.currencySettings;



 const formatCurrency = (value = 0) =>

  value.toLocaleString(locale, {

   style: 'currency',

   currency,

   minimumFractionDigits,

   maximumFractionDigits

  });



 // Load Poppins font once

 useEffect(() => {

  const link = document.createElement('link');

  link.href = 'https://fonts.googleapis.com/css2?family=Poppins:wght@400;600;700&display=swap';

  link.rel = 'stylesheet';

  document.head.appendChild(link);

  document.body.style.fontFamily = "'Poppins', sans-serif";

 }, []);



 // Fetch associates based on context

 useEffect(() => {

  const fetchAssociateData = async (month, year, projId) => {

   setLoading(true);

   setError(null);

   try {

    const resp = await fetch(`${BACKEND_URL}/api/associate`, {

     method: 'POST',

     headers: { 'Content-Type': 'application/json' },

     body: JSON.stringify({ month, year, projectId: projId })

    });



    if (!resp.ok) {

     const msg = await resp.text();

     throw new Error(`status: ${resp.status}, message: ${msg || resp.statusText}`);

    }



    const data = await resp.json();

    const list = Array.isArray(data) ? data : [data].filter(Boolean);

    setAssociates(list);



    if (list.length > 0) {

     const first = list[0];

     setActiveProjectName(first.projectName || projId);

     setActiveAccountId(first.accountId || activeAccountId);

     setActiveAccountName(first.accountName || activeAccountName);

    }

   } catch (e) {

    console.error('Error fetching associate data:', e);

    setError(`Failed to load associate data: ${e.message}`);

   } finally {

    setLoading(false);

   }

  };



  let month = location.state?.month;

  let year = location.state?.year;

  let projId = urlProjectId;

  let acctId = location.state?.accountId;

  let acctName = location.state?.accountName;

  let projName = location.state?.projectName;



  if (!month || !year || !acctId || !projName) {

   const sm = sessionStorage.getItem('lastFetchedAssociateMonth');

   const sy = sessionStorage.getItem('lastFetchedAssociateYear');

   const sp = sessionStorage.getItem('lastFetchedAssociateProjectId');

   const sn = sessionStorage.getItem('lastFetchedAssociateProjectName');

   const sa = sessionStorage.getItem('lastFetchedAssociateAccountId');

   const san = sessionStorage.getItem('lastFetchedAssociateAccountName');



   if (sm && sy && sp) {

    month = parseInt(sm, 10);

    year = parseInt(sy, 10);

    projId = sp;

    projName = sn;

    acctId = sa;

    acctName = san;

   }

  }



  if (month && year && projId && acctId && projName) {

   if (

    month !== activeMonth ||

    year !== activeYear ||

    projId !== activeProjectId ||

    associates.length === 0

   ) {

    setActiveMonth(month);

    setActiveYear(year);

    setActiveProjectId(projId);

    setActiveProjectName(projName);

    setActiveAccountId(acctId);

    setActiveAccountName(acctName);



    sessionStorage.setItem('lastFetchedAssociateMonth', month.toString());

    sessionStorage.setItem('lastFetchedAssociateYear', year.toString());

    sessionStorage.setItem('lastFetchedAssociateProjectId', projId);

    sessionStorage.setItem('lastFetchedAssociateProjectName', projName);

    sessionStorage.setItem('lastFetchedAssociateAccountId', acctId);

    sessionStorage.setItem('lastFetchedAssociateAccountName', acctName);



    fetchAssociateData(month, year, projId);

   } else {

    setLoading(false);

   }

  } else {

   setError('Missing associate context (month, year, project, or account). Please go back.');

   setLoading(false);

  }

 }, [location.state, urlProjectId, activeMonth, activeYear, activeProjectId, associates.length]);



 // Initialize DataTables when associates load or filter changes

 useEffect(() => {

  if (!loading && tableRef.current) {

   const $tbl = $(tableRef.current);

   if ($.fn.DataTable.isDataTable($tbl)) {

    dataTableInstance.current.destroy(); // Destroy existing instance

   }



   // Remove any previously added custom search functions

   $.fn.dataTable.ext.search.pop();



   // Apply custom filter based on filterOption state

   if (filterOption === 'varianceAboveZero') {

    $.fn.dataTable.ext.search.push(

     (settings, data, dataIndex) => {

      // Assuming 'Variance Hours (Monthly)' is the 8th column (index 7)

      const varianceHoursMonthly = parseFloat(data[7]) || 0;

      return varianceHoursMonthly !== 0;

     }

    );

   }



   // Initialize DataTables with no column ordering

   dataTableInstance.current = $tbl.DataTable({

    paging: true,

    searching: true,

    ordering: false, // Removed individual column sorting

    info: true,

    autoWidth: false

   });

  }



  // Cleanup function for DataTables

  return () => {

   if (dataTableInstance.current) {

    dataTableInstance.current.destroy();

    dataTableInstance.current = null;

   }

   $.fn.dataTable.ext.search.pop(); // Clean up custom search on unmount

  };

 }, [loading, associates, filterOption]); // Re-run effect when filterOption changes



 const breadcrumbPath = [

  { name: 'PMO Dashboard', page: '' },

  { name: 'Revenue Forecast - Early View', page: 'upload' },

  { name: 'Account Level', page: 'accounts' },

  {

   name: `Projects (${activeAccountName})`,

   page: `accounts/${activeAccountId}/projects`

  },

  {

   name: `Associates (${activeProjectName})`,

   page: `projects/${activeProjectId}/associates`

  }

 ];



 const handleNotifyAll = () => {

  alert('Notifying all associates for this project!');

 };



 const handleDownloadData = () => {

  alert('Downloading associate data for this project!');

 };



 // Handler for the variance filter dropdown change

 const handleFilterChange = (value) => {

  setFilterOption(value);

 };



 if (loading) {

  return (

   <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100">

    <div className="flex flex-col items-center space-y-4">

     <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>

     <p className="text-blue-600 font-medium">Loading Associate Data...</p>

    </div>

   </div>

  );

 }



 if (error) {

  return (

   <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-red-50 to-pink-100">

    <div className="text-center space-y-4">

     <p className="text-red-600 text-lg font-medium">Error: {error}</p>

     <Button

      onClick={() => navigate(`/accounts/${activeAccountId}/projects`)}

      className="bg-blue-600 hover:bg-blue-700"

     >

      Go to Project Level

     </Button>

    </div>

   </div>

  );

 }



 return (

  <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50 p-4 font-sans">

   <div className="max-w-7xl mx-auto">

    <Card className="shadow-xl border-0 bg-white/95 backdrop-blur">

     <CardHeader className="border-b border-slate-200 bg-white/50">

      <Breadcrumbs path={breadcrumbPath} />

       

      {/* Header Section with improved alignment */}

      <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4 pt-4">

       <div className="flex items-center space-x-3">

        <div className="p-2 bg-blue-100 rounded-lg">

         <Users className="h-6 w-6 text-blue-600" />

        </div>

        <div>

         <h1 className="text-2xl font-bold text-slate-900">Associate Details</h1>

         <p className="text-slate-600 text-sm">Project: {activeProjectName}</p>

        </div>

       </div>



       {/* Action buttons with professional alignment */}

       <div className="flex flex-col sm:flex-row items-start sm:items-center gap-3">

        <div className="flex items-center space-x-2">

         <Filter className="h-4 w-4 text-slate-500" />

         <span className="text-sm font-medium text-slate-700">Filter Variance:</span>

         <Select value={filterOption} onValueChange={handleFilterChange}>

          <SelectTrigger className="w-48 h-9">

           <SelectValue />

          </SelectTrigger>

          <SelectContent>

           <SelectItem value="showAll">Show All Variance</SelectItem>

           <SelectItem value="varianceAboveZero">Variance Above 0</SelectItem>

          </SelectContent>

         </Select>

        </div>



        <div className="flex items-center space-x-2">

         <Button

          variant="outline"

          size="sm"

          onClick={handleNotifyAll}

          className="flex items-center space-x-2 border-blue-200 text-blue-700 hover:bg-blue-50"

         >

          <Bell className="h-4 w-4" />

          <span>Notify All</span>

         </Button>

         <Button

          variant="outline"

          size="sm"

          onClick={handleDownloadData}

          className="flex items-center space-x-2 border-green-200 text-green-700 hover:bg-green-50"

         >

          <Download className="h-4 w-4" />

          <span>Download Data</span>

         </Button>

        </div>

       </div>

      </div>

     </CardHeader>



     <CardContent className="p-0">

      <div className="overflow-x-auto">

       <table

        ref={tableRef}

        className="display table table-hover table-borderless align-middle mb-0 w-full"

       >

        <thead className="bg-gradient-to-r from-blue-600 to-blue-700 text-white">

         <tr>

          <th className="px-4 py-4 text-left text-sm font-semibold">Associate ID</th>

          <th className="px-4 py-4 text-left text-sm font-semibold">Associate Name</th>

          <th className="px-4 py-4 text-left text-sm font-semibold">Project ID</th>

          <th className="px-4 py-4 text-left text-sm font-semibold">Project Name</th>

          <th className="px-4 py-4 text-left text-sm font-semibold">PM ID</th>

          <th className="px-4 py-4 text-left text-sm font-semibold">Total Company Hours (Monthly)</th>

          <th className="px-4 py-4 text-left text-sm font-semibold">Total Client Hours (Monthly)</th>

          <th className="px-4 py-4 text-left text-sm font-semibold">Variance Hours (Monthly)</th>

          <th className="px-4 py-4 text-left text-sm font-semibold">Actual Revenue</th>

          <th className="px-4 py-4 text-left text-sm font-semibold">Associate Rate</th>

          <th className="px-4 py-4 text-center text-sm font-semibold">Notify</th>

          <th className="px-4 py-4 text-center text-sm font-semibold">View Daily</th>

         </tr>

        </thead>

        <tbody className="divide-y divide-slate-200 bg-white">

         {associates.map((associate) => (

          <tr key={associate.associateId} className="hover:bg-slate-50 transition-colors">

           <td className="px-4 py-4 text-sm font-medium text-slate-900">

            {associate.associateId}

           </td>

           <td className="px-4 py-4 text-sm text-slate-700 font-medium">

            {associate.associateName}

           </td>

           <td className="px-4 py-4 text-sm text-slate-600">

            {associate.projectId}

           </td>

           <td className="px-4 py-4 text-sm text-slate-700">

            {associate.projectName}

           </td>

           <td className="px-4 py-4 text-sm text-slate-600">

            {associate.esaID}

           </td>

           <td className="px-4 py-4 text-sm text-slate-700 font-mono">

            {associate.totalCompanyHoursMonthly}

           </td>

           <td className="px-4 py-4 text-sm text-slate-700 font-mono">

            {associate.totalClientHoursMonthly}

           </td>

           <td className="px-4 py-4 text-sm font-mono">

            <span className={

             (associate.varianceHoursMonthly || 0) > 0 

              ? 'text-red-600 font-semibold' 

              : (associate.varianceHoursMonthly || 0) < 0

              ? 'text-green-600 font-semibold'

              : 'text-slate-700'

            }>

             {associate.varianceHoursMonthly}

            </span>

           </td>

           <td className="px-4 py-4 text-sm text-slate-700 font-mono font-semibold">

            {formatCurrency(associate.actualRevenue)}

           </td>

           <td className="px-4 py-4 text-sm text-slate-700 font-mono">

            {formatCurrency(associate.associateRtRate)}

           </td>

           <td className="px-4 py-4 text-center">

            <Button

             variant="outline"

             size="sm"

             className="border-blue-200 text-blue-700 hover:bg-blue-50 h-8 px-3"

            >

             <Bell className="h-3 w-3 mr-1" />

             Notify

            </Button>

           </td>

           <td className="px-4 py-4 text-center">

            <Button

             variant="outline"

             size="sm"

             onClick={() => {

              navigate(

               `/projects/${associate.projectId}/associates/${associate.associateId}/daily`,

               {

                state: {

                 month: activeMonth,

                 year: activeYear,

                 projectId: associate.projectId,

                 projectName: associate.projectName,

                 accountId: activeAccountId,

                 accountName: activeAccountName,

                 associateId: associate.associateId,

                 associateName: associate.associateName

                }

               }

              );

             }}

             className="border-slate-200 text-slate-700 hover:bg-slate-50 h-8 w-8 p-0"

            >

             <Search className="h-3 w-3" />

            </Button>

           </td>

          </tr>

         ))}

        </tbody>

       </table>

      </div>

     </CardContent>

    </Card>

   </div>

  </div>

 );

};



export default AssociateLevel;
