import React, { useRef, useEffect, useState } from "react";
import { useNavigate, useParams, useLocation } from "react-router-dom";
import $ from "jquery";
import "datatables.net";
import "datatables.net-dt/css/dataTables.dataTables.min.css";
// Assuming these paths are correct relative to where ProjectLevel.jsx resides
import Breadcrumbs from "../Breadcrumbs";
import commonData from "../../data/commonData.json"; // Ensure commonData.json exists and is accessible

// Declare the BACKEND_URL constant at the top level
const BACKEND_URL = "http://localhost:8081"; // Ensure this matches your backend API endpoint

const ProjectLevel = () => {
  // Hooks for navigation and route parameters
  const navigate = useNavigate();
  const { accountId: urlAccountId } = useParams();
  const location = useLocation();

  // Ref for the DataTable HTML table element
  const tableRef = useRef(null);

  // State variables for data and UI management
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // State for parameters defining the current view
  const [activeMonth, setActiveMonth] = useState(null);
  const [activeYear, setActiveYear] = useState(null);
  const [activeAccountId, setActiveAccountId] = useState(null);
  const [activeAccountName, setActiveAccountName] = useState("Loading...");
  const [activeSbu, setActiveSbu] = useState(null);
  const [activeProjectType, setActiveProjectType] = useState(null);

  // State to manage column filters. Initialized from sessionStorage or as an empty object.
  const [columnFilters, setColumnFilters] = useState(() => {
    try {
      const savedFilters = sessionStorage.getItem("projectLevelFilters");
      return savedFilters ? JSON.parse(savedFilters) : {};
    } catch (e) {
      console.error("Failed to parse saved filters from sessionStorage:", e);
      return {}; // Fallback to empty if parsing fails
    }
  });

  /**
   * Formats a numeric value into a currency string based on commonData settings.
   * @param {number} value - The number to format. Defaults to 0 if not provided.
   * @returns {string} The formatted currency string.
   */
  const formatCurrency = (value = 0) =>
    value.toLocaleString(commonData.currencySettings.locale, {
      style: "currency",
      currency: commonData.currencySettings.currency,
      minimumFractionDigits: commonData.currencySettings.minimumFractionDigits,
      maximumFractionDigits: commonData.currencySettings.maximumFractionDigits,
    });

  /**
   * Converts a month number (1-12) to its full English name.
   * Used primarily for file naming in downloads.
   * @param {number} monthNumber - The month number (e.g., 1 for January).
   * @returns {string} The full month name.
   */
  const getMonthName = (monthNumber) => {
    if (!monthNumber) return "";
    const date = new Date();
    date.setMonth(monthNumber - 1); // Month is 0-indexed in Date object
    return date.toLocaleString("en-US", { month: "long" });
  };

  /**
   * Handles the "Download Data" button click.
   * Generates a CSV file from the 'projects' data (filtered by current columnFilters)
   * and triggers a download in the user's browser.
   */
  const handleDownloadData = () => {
    if (projects.length === 0) {
      console.log("No data to download.");
      return;
    }

    // Define CSV headers that match the table columns and expected data structure
    const headers = [
      "Project ID",
      "Project Name",
      "Total Associates",
      "Company Hours",
      "Client Hours",
      "Variance Hours",
      "Revenue",
    ];

    // Apply the current column filters to the 'projects' array before generating CSV.
    // This ensures only visible data in the table is downloaded.
    const csvRows = applyFilters(projects, columnFilters).map((project) => {
      return [
        `"${project.projectId}"`, // Enclose strings in quotes to handle commas
        `"${project.projectName}"`,
        project.totalAssociatesCount,
        project.totalCompanyHours,
        project.totalClientHours,
        project.varianceHours,
        `"${formatCurrency(project.revenue)}"`, // Format currency for CSV
      ].join(","); // Join each row's values with a comma
    });

    // Combine headers and data rows for the complete CSV content
    const csvContent = [headers.join(","), ...csvRows].join("\n");

    // Create a Blob containing the CSV data and trigger download
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
    const link = document.createElement("a");
    const url = URL.createObjectURL(blob);
    const monthName = getMonthName(activeMonth);
    const fileName = `Projects_Account_${activeAccountId}_${monthName}_${activeYear}.csv`; // Dynamic file name

    link.setAttribute("href", url);
    link.setAttribute("download", fileName);
    link.style.visibility = "hidden"; // Hide the link element
    document.body.appendChild(link); // Append to body to make it clickable
    link.click(); // Programmatically click the link to trigger download
    document.body.removeChild(link); // Clean up the link element
    URL.revokeObjectURL(url); // Release the object URL
  };

  /**
   * Generates a unique, sorted list of options for a given column filter dropdown.
   * Includes an "All" option for resetting the filter.
   * @param {string} columnKey - The key of the column (e.g., "projectId", "revenue").
   * @returns {string[]} An array of unique string values, starting with "All".
   */
  const getUniqueOptions = (columnKey) => {
    // Return default options if projects data is not yet available or empty
    if (!projects || !Array.isArray(projects) || projects.length === 0) {
      return ["All"];
    }

    // Map project data to obtain values for the specified column
    const options = projects.map((project) => {
      const value = project[columnKey];
      if (columnKey === "revenue") {
        // If the column is revenue, format it to match the displayed table value
        return formatCurrency(value);
      }
      return String(value); // Convert all values to string for consistent comparison
    });

    // Get unique options and sort them
    const uniqueOptions = [...new Set(options)].sort((a, b) => {
      // Determine if the column is numeric based on its key
      const isNumericColumn =
        columnKey === "totalAssociatesCount" ||
        columnKey === "totalCompanyHours" ||
        columnKey === "totalClientHours" ||
        columnKey === "varianceHours";

      if (isNumericColumn) {
        // Numeric sort for numeric columns
        return Number(a) - Number(b);
      }
      // Lexicographical sort for string columns
      return a.localeCompare(b);
    });

    return ["All", ...uniqueOptions]; // Prepend "All" option
  };

  /**
   * Handles changes from the column filter dropdowns.
   * Updates the `columnFilters` state, which in turn triggers DataTable filtering.
   * @param {string} columnKey - The key of the column being filtered.
   * @param {string} value - The selected filter value (e.g., "All", "Project A", "100").
   */
  const handleFilterChange = (columnKey, value) => {
    setColumnFilters((prevFilters) => ({
      ...prevFilters,
      [columnKey]: value === "All" ? null : value, // Set to null if "All" is selected
    }));
  };

  /**
   * Filters the provided `projectsArray` based on the given `filters` object.
   * This function is used internally for the download functionality to ensure the
   * downloaded CSV matches the currently visible, filtered data in the table.
   * @param {Array} projectsArray - The array of project objects to filter.
   * @param {Object} filters - An object where keys are column keys and values are filter criteria.
   * @returns {Array} The array of projects that match all active filters.
   */
  const applyFilters = (projectsArray, filters) => {
    return projectsArray.filter((project) => {
      // Check if every active filter matches the current project
      return Object.keys(filters).every((columnKey) => {
        const filterValue = filters[columnKey];
        // If no filter is applied for this column, or filterValue is null/undefined, it matches
        if (filterValue === null || filterValue === undefined) {
          return true;
        }

        const projectValue = project[columnKey];
        // If project's value for this column is null/undefined, it cannot match a filter
        if (projectValue === null || projectValue === undefined) {
          return false;
        }

        // Special handling for 'revenue' column: compare formatted currency strings
        if (columnKey === "revenue") {
          return formatCurrency(project.revenue) === String(filterValue);
        }

        // For numeric columns, perform strict number equality comparison
        if (
          typeof projectValue === "number" &&
          !Number.isNaN(Number(filterValue)) // Ensure filterValue can be converted to a number
        ) {
          return projectValue === Number(filterValue);
        }

        // For all other columns (strings), perform a case-insensitive string comparison
        return (
          String(projectValue).toLowerCase() ===
          String(filterValue).toLowerCase()
        );
      });
    });
  };

  // Effect to load Poppins font from Google Fonts for consistent styling
  useEffect(() => {
    const link = document.createElement("link");
    link.href =
      "https://fonts.googleapis.com/css2?family=Poppins:wght@400;600;700&display=swap";
    link.rel = "stylesheet";
    document.head.appendChild(link);
    document.body.style.fontFamily = "'Poppins', sans-serif";
  }, []); // Empty dependency array ensures this runs only once on mount

  // Effect to fetch project data based on URL parameters and session storage.
  // This hook also initializes state related to the current view (month, year, account, etc.).
  useEffect(() => {
    const fetchProjectData = async (month, year, accId, projectType) => {
      setLoading(true); // Indicate that data is being loaded
      setError(null); // Clear any previous errors

      try {
        const payload = { month, year, accId, projectType };
        // Make a POST request to the backend API
        const resp = await fetch(`${BACKEND_URL}/api/project`, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(payload),
        });

        if (!resp.ok) {
          // If response is not OK (e.g., 404, 500), throw an error
          const errText = await resp.text();
          throw new Error(
            `status: ${resp.status}, message: ${errText || resp.statusText}`
          );
        }

        const data = await resp.json();
        // Ensure data is an array; if a single object, wrap it in an array
        const list = Array.isArray(data) ? data : [data].filter(Boolean);
        setProjects(list); // Update the projects state

        // Attempt to set the account name from fetched data, otherwise use stored or URL ID
        if (list.length > 0 && list[0].accountName) {
          setActiveAccountName(list[0].accountName);
        } else {
          const storedName = sessionStorage.getItem(
            "lastFetchedProjectAccountName"
          );
          setActiveAccountName(storedName || accId);
        }
      } catch (e) {
        console.error("Error fetching project data:", e);
        setError(`Failed to load project data: ${e.message}`); // Set error message
      } finally {
        setLoading(false); // End loading state regardless of success or failure
      }
    };

    // Determine the parameters to use for fetching data:
    // Prioritize `location.state` (from navigation), then `sessionStorage`, then URL params.
    let monthToUse = location.state?.month || sessionStorage.getItem("lastFetchedProjectMonth");
    let yearToUse = location.state?.year || sessionStorage.getItem("lastFetchedProjectYear");
    let accountIdToUse = urlAccountId || sessionStorage.getItem("lastFetchedProjectAccountId");
    let accountNameToUse = location.state?.accountName || sessionStorage.getItem("lastFetchedProjectAccountName");
    let sbuToUse = location.state?.sbu || sessionStorage.getItem("lastFetchedProjectSbu");
    let projectTypeToUse = location.state?.projectType || sessionStorage.getItem("lastFetchedProjectType");

    // Parse month and year to integers
    monthToUse = monthToUse ? parseInt(monthToUse, 10) : null;
    yearToUse = yearToUse ? parseInt(yearToUse, 10) : null;

    // Proceed only if essential parameters are available
    if (monthToUse && yearToUse && accountIdToUse) {
      // Check if the current parameters differ from the active ones to avoid redundant fetches
      if (
        monthToUse !== activeMonth ||
        yearToUse !== activeYear ||
        accountIdToUse !== activeAccountId ||
        sbuToUse !== activeSbu ||
        projectTypeToUse !== activeProjectType
      ) {
        // Update active states
        setActiveMonth(monthToUse);
        setActiveYear(yearToUse);
        setActiveAccountId(accountIdToUse);
        if (accountNameToUse) {
          setActiveAccountName(accountNameToUse);
        }
        setActiveSbu(sbuToUse);
        setActiveProjectType(projectTypeToUse);

        // Save current parameters to sessionStorage for persistence
        sessionStorage.setItem("lastFetchedProjectMonth", monthToUse.toString());
        sessionStorage.setItem("lastFetchedProjectYear", yearToUse.toString());
        sessionStorage.setItem("lastFetchedProjectAccountId", accountIdToUse);
        if (accountNameToUse) {
          sessionStorage.setItem("lastFetchedProjectAccountName", accountNameToUse);
        }
        if (sbuToUse) {
          sessionStorage.setItem("lastFetchedProjectSbu", sbuToUse);
        }
        if (projectTypeToUse) {
          sessionStorage.setItem("lastFetchedProjectType", projectTypeToUse);
        }

        // Trigger the data fetch
        fetchProjectData(monthToUse, yearToUse, accountIdToUse, projectTypeToUse);
      } else {
        // If parameters haven't changed, but data is already loaded, just set loading to false
        setLoading(false);
      }
    } else {
      // If essential parameters are missing, set an error
      setError(
        "Missing month, year, or Account ID. Please navigate here with all parameters."
      );
      setLoading(false);
    }
  }, [
    location.state,
    urlAccountId,
    activeMonth,
    activeYear,
    activeAccountId,
    activeSbu,
    activeProjectType,
  ]); // Dependencies ensure this effect re-runs when relevant parameters change

  // Effect to handle DataTables initialization and destruction.
  // It runs when 'loading' or 'projects' state changes.
  useEffect(() => {
    // Only initialize if not loading, projects are available, and the table ref exists
    if (!loading && projects.length > 0 && tableRef.current) {
      const $tbl = $(tableRef.current);

      // Destroy existing DataTable instance if it already exists to prevent re-initialization issues
      if ($.fn.DataTable.isDataTable($tbl)) {
        $tbl.DataTable().destroy();
      }

      // Initialize DataTable with desired options
      const tableInstance = $tbl.DataTable({
        paging: true,      // Enable pagination
        searching: true,   // Enable global search bar
        ordering: false,   // Disable default column ordering (handled by custom filtering)
        info: true,        // Show table information (e.g., "Showing 1 to 10 of X entries")
        autoWidth: false,  // Disable auto column width calculation for better responsiveness
      });

      // Store the DataTables instance on the ref for later access (e.g., for applying filters)
      tableRef.current.dataTableInstance = tableInstance;
    }

    // Cleanup function: This runs when the component unmounts or when dependencies change,
    // ensuring that the DataTable instance is properly destroyed to prevent memory leaks.
    return () => {
      if (tableRef.current && tableRef.current.dataTableInstance) {
        tableRef.current.dataTableInstance.destroy(); // Destroy the DataTable instance
        tableRef.current.dataTableInstance = null; // Clear the stored instance
      }
    };
  }, [loading, projects]); // Dependencies: re-run when loading state or projects data changes

  // NEW EFFECT: Persist column filters to sessionStorage whenever they change.
  useEffect(() => {
    // Save filters only if there's at least one filter applied, otherwise remove the key.
    if (Object.keys(columnFilters).length > 0) {
      sessionStorage.setItem("projectLevelFilters", JSON.stringify(columnFilters));
    } else {
      sessionStorage.removeItem("projectLevelFilters");
    }
  }, [columnFilters]); // This effect runs every time `columnFilters` state is updated

  // Effect to apply filters using the DataTables API whenever columnFilters state changes.
  useEffect(() => {
    if (tableRef.current && tableRef.current.dataTableInstance) {
      const tableInstance = tableRef.current.dataTableInstance;

      // Clear all existing column-specific searches before applying new ones
      tableInstance.columns().search("");

      // Define a mapping from column keys (used in state) to DataTables column indices
      // This is crucial for DataTables' `.column(index).search()` method
      const columnMapping = {
        projectId: 0,
        projectName: 1,
        totalAssociatesCount: 2,
        totalCompanyHours: 3,
        totalClientHours: 4,
        varianceHours: 5,
        revenue: 6,
      };

      // Iterate over the current `columnFilters` and apply each filter to the corresponding DataTable column
      Object.keys(columnFilters).forEach((columnKey) => {
        const filterValue = columnFilters[columnKey];
        if (filterValue !== null) { // Only apply filter if a value is selected (not "All")
          const columnIndex = columnMapping[columnKey];
          if (columnIndex !== undefined) {
            // Escape special regex characters in the filter value to ensure exact match
            const escapedValue = String(filterValue).replace(
              /[.*+?^${}()|[\]\\]/g,
              "\\$&"
            );
            // Apply the column search: `^${escapedValue}$` ensures an exact match for the cell content
            // The `true` argument enables regex search, `false` disables smart search
            tableInstance
              .column(columnIndex)
              .search(`^${escapedValue}$`, true, false);
          }
        }
      });

      // Redraw the table to apply all the newly set filters
      tableInstance.draw();
    }
  }, [columnFilters]); // This effect runs whenever the `columnFilters` state changes

  // Define the breadcrumb navigation path dynamically
  const breadcrumbPath = [
    { name: "PMO Dashboard", page: "" },
    { name: "Revenue Forecast - Early View", page: "upload" },
    // Conditionally add SBU level if activeSbu is present
    ...(activeSbu
      ? [
          {
            name: `${activeSbu} SBU Level`,
            page: "sbu",
            state: { month: activeMonth, year: activeYear, sbu: activeSbu },
          },
        ]
      : []),
    {
      name: "Account Level",
      page: `accounts`,
      state: { month: activeMonth, year: activeYear, sbu: activeSbu },
    },
    // Conditionally add Project Type level if activeProjectType is present
    ...(activeProjectType
      ? [
          {
            name: `${activeProjectType} Project Type`,
            page: `accounts/${activeAccountId}/project-types`,
            state: {
              month: activeMonth,
              year: activeYear,
              sbu: activeSbu,
              accId: activeAccountId,
            },
          },
        ]
      : []),
    {
      name: `Projects (${activeAccountName})`,
      page: `accounts/${activeAccountId}/projects`,
    },
  ].filter(Boolean); // Filter out any null or undefined entries from the array

  // Render loading state while data is being fetched
  if (loading) {
    return (
      <div className="min-vh-100 d-flex justify-content-center align-items-center">
        <div className="spinner-border text-primary" role="status" />
        <p className="ms-3 text-primary">Loading Project Data...</p>
      </div>
    );
  }

  // Render error state if data fetching failed
  if (error) {
    return (
      <div className="min-vh-100 d-flex flex-column justify-content-center align-items-center text-danger">
        <p className="fs-4">Error: {error}</p>
        <button
          className="btn btn-primary mt-3"
          onClick={() =>
            // Provide a way to navigate back if an error occurs
            navigate("/accounts", {
              state: { month: activeMonth, year: activeYear, sbu: activeSbu },
            })
          }
        >
          Go to Account Level
        </button>
      </div>
    );
  }

  // Main component render: displays the project data table
  return (
    <div
      className="min-vh-100 d-flex flex-column align-items-center px-3 py-4"
      style={{
        background: "linear-gradient(to bottom right, #f5f7fa, #e9ecef)", // Gradient background
        fontFamily: "'Poppins', sans-serif", // Apply Poppins font
      }}
    >
      <div
        className="card shadow p-4 p-md-5 w-100"
        style={{
          maxWidth: "1200px",
          backgroundColor: "#ffffffdd", // Slightly transparent white card background
          borderRadius: "1rem", // Rounded corners for the card
        }}
      >
        {/* Breadcrumbs component for navigation hierarchy */}
        <Breadcrumbs path={breadcrumbPath} />

        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2 className="mb-0 fw-semibold text-dark">
             Project Level Overview{" "}
            {activeAccountId ? `for ${activeAccountName}` : ""}{" "}
            {/* Display account name if available */}
          </h2>
          {/* Download button, visible only if projects data is loaded */}
          {projects.length > 0 && (
            <button
              className="btn btn-outline-success rounded-pill px-3"
              onClick={handleDownloadData}
              title="Download Project Data"
            >
              <i className="bi bi-download me-2"></i> Download Data
            </button>
          )}
        </div>

        {/* Responsive table container for DataTables */}
        <div className="table-responsive rounded">
          <table
            ref={tableRef} // Attach ref to the table element
            className="display table table-hover align-middle table-borderless mb-0"
            style={{ width: "100%" }}
          >
            <thead
              className="text-white"
              style={{
                background: "linear-gradient(to right, #3b82f6, #2563eb)", // Blue gradient for table header
              }}
            >
              <tr>
                {/* Each table header now includes a filter dropdown for its column */}
                <th>
                  Project ID
                  <select
                    className="form-select form-select-sm mt-1"
                    onChange={(e) =>
                      handleFilterChange("projectId", e.target.value)
                    }
                    value={columnFilters.projectId || "All"} // Controlled component: value reflects state
                  >
                    {getUniqueOptions("projectId").map((option) => (
                      <option key={option} value={option}>
                        {option}
                      </option>
                    ))}
                  </select>
                </th>
                <th>
                  Project Name
                  <select
                    className="form-select form-select-sm mt-1"
                    onChange={(e) =>
                      handleFilterChange("projectName", e.target.value)
                    }
                    value={columnFilters.projectName || "All"}
                  >
                    {getUniqueOptions("projectName").map((option) => (
                      <option key={option} value={option}>
                        {option}
                      </option>
                    ))}
                  </select>
                </th>
                <th>
                  Total Associates
                  <select
                    className="form-select form-select-sm mt-1"
                    onChange={(e) =>
                      handleFilterChange("totalAssociatesCount", e.target.value)
                    }
                    value={columnFilters.totalAssociatesCount || "All"}
                  >
                    {getUniqueOptions("totalAssociatesCount").map((option) => (
                      <option key={option} value={option}>
                        {option}
                      </option>
                    ))}
                  </select>
                </th>
                <th>
                  Company Hours
                  <select
                    className="form-select form-select-sm mt-1"
                    onChange={(e) =>
                      handleFilterChange("totalCompanyHours", e.target.value)
                    }
                    value={columnFilters.totalCompanyHours || "All"}
                  >
                    {getUniqueOptions("totalCompanyHours").map((option) => (
                      <option key={option} value={option}>
                        {option}
                      </option>
                    ))}
                  </select>
                </th>
                <th>
                  Client Hours
                  <select
                    className="form-select form-select-sm mt-1"
                    onChange={(e) =>
                      handleFilterChange("totalClientHours", e.target.value)
                    }
                    value={columnFilters.totalClientHours || "All"}
                  >
                    {getUniqueOptions("totalClientHours").map((option) => (
                      <option key={option} value={option}>
                        {option}
                      </option>
                    ))}
                  </select>
                </th>
                <th>
                  Variance Hours
                  <select
                    className="form-select form-select-sm mt-1"
                    onChange={(e) =>
                      handleFilterChange("varianceHours", e.target.value)
                    }
                    value={columnFilters.varianceHours || "All"}
                  >
                    {getUniqueOptions("varianceHours").map((option) => (
                      <option key={option} value={option}>
                        {option}
                      </option>
                    ))}
                  </select>
                </th>
                <th>
                  Revenue
                  <select
                    className="form-select form-select-sm mt-1"
                    onChange={(e) =>
                      handleFilterChange("revenue", e.target.value)
                    }
                    value={columnFilters.revenue || "All"}
                  >
                    {getUniqueOptions("revenue").map((option) => (
                      <option key={option} value={option}>
                        {option}
                      </option>
                    ))}
                  </select>
                </th>
                <th className="text-center">View</th>
              </tr>
            </thead>
            <tbody>
              {/* Render project rows. DataTables will automatically handle filtering and pagination. */}
              {projects.map((project) => (
                <tr key={project.projectId}>
                  <td>{project.projectId}</td>
                  <td>{project.projectName}</td>
                  <td>{project.totalAssociatesCount}</td>
                  <td>{project.totalCompanyHours}</td>
                  <td>{project.totalClientHours}</td>
                  <td>{project.varianceHours}</td>
                  <td>{formatCurrency(project.revenue)}</td>
                  <td className="text-center">
                    <button
                      className="btn btn-sm btn-outline-primary rounded-circle"
                      onClick={() =>
                        // Navigate to the Associates level with relevant state
                        navigate(`/projects/${project.projectId}/associates`, {
                          state: {
                            projectId: project.projectId,
                            projectName: project.projectName,
                            accountId: project.accountId,
                            accountName: project.accountName,
                            month: activeMonth,
                            year: activeYear,
                            sbu: activeSbu,
                            projectType: activeProjectType,
                          },
                        })
                      }
                      title="View Associates"
                    >
                       {/* Magnifying glass icon */}
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default ProjectLevel;
