import React, { useRef, useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import $ from 'jquery';
import 'datatables.net';
import 'datatables.net-dt/css/dataTables.dataTables.min.css';
import Breadcrumbs from '../Breadcrumbs';
import commonData from '../../data/commonData.json';

// Utility to convert month number to month name
const getMonthName = (monthNumber) => {
Â  const date = new Date();
Â  date.setMonth(monthNumber - 1);
Â  return date.toLocaleString('en-US', { month: 'long' });
};

const AccountLevel = () => {
Â  const navigate = useNavigate();
Â  const location = useLocation();
Â  const tableRef = useRef(null);

Â  // UI state
Â  const [accounts, setAccounts] = useState([]);
Â  const [commentsMap, setCommentsMap] = useState({});
Â  const [modalVisible, setModalVisible] = useState(false);
Â  const [currentAccountId, setCurrentAccountId] = useState(null);
Â  const [commentInput, setCommentInput] = useState('');
Â  const [loading, setLoading] = useState(true);
Â  const [error, setError] = useState(null);

Â  // Active filters
Â  const [activeMonth, setActiveMonth] = useState(null);
Â  const [activeYear, setActiveYear] = useState(null);
Â  const [activeMonthName, setActiveMonthName] = useState(null);
Â  const [activeSbu, setActiveSbu] = useState(null);

Â  const BACKEND_URL = 'http://localhost:8081';

Â  // Currency formatter
Â  const {
Â  Â  locale,
Â  Â  currency,
Â  Â  minimumFractionDigits,
Â  Â  maximumFractionDigits,
Â  } = commonData.currencySettings;

Â  const formatCurrency = (value = 0) =>
Â  Â  value.toLocaleString(locale, {
Â  Â  Â  style: 'currency',
Â  Â  Â  currency,
Â  Â  Â  minimumFractionDigits,
Â  Â  Â  maximumFractionDigits,
Â  Â  });

Â  // Load Poppins font
Â  useEffect(() => {
Â  Â  const link = document.createElement('link');
Â  Â  link.href =
Â  Â  Â  'https://fonts.googleapis.com/css2?family=Poppins:wght@400;600;700&display=swap';
Â  Â  link.rel = 'stylesheet';
Â  Â  document.head.appendChild(link);
Â  Â  document.body.style.fontFamily = "'Poppins', sans-serif";
Â  }, []);

Â  // Fetch accounts when filters change
Â  useEffect(() => {
Â  Â  const fetchAccountData = async (month, year, monthNameParam, sbu) => {
Â  Â  Â  setLoading(true);
Â  Â  Â  setError(null);

Â  Â  Â  try {
Â  Â  Â  Â  const resp = await fetch(`${BACKEND_URL}/api/account`, {
Â  Â  Â  Â  Â  method: 'POST',
Â  Â  Â  Â  Â  headers: { 'Content-Type': 'application/json' },
Â  Â  Â  Â  Â  body: JSON.stringify({ month, year, monthNameParam, sbu }),
Â  Â  Â  Â  });
Â  Â  Â  Â  if (!resp.ok) {
Â  Â  Â  Â  Â  const txt = await resp.text();
Â  Â  Â  Â  Â  throw new Error(txt || resp.statusText);
Â  Â  Â  Â  }
Â  Â  Â  Â  const data = await resp.json();
Â  Â  Â  Â  const list = Array.isArray(data) ? data : [data].filter(Boolean);
Â  Â  Â  Â  setAccounts(list);

Â  Â  Â  Â  // seed comments map
Â  Â  Â  Â  const initMap = {};
Â  Â  Â  Â  list.forEach((acc) => {
Â  Â  Â  Â  Â  if (acc.accountComment) {
Â  Â  Â  Â  Â  Â  initMap[acc.accountId] = acc.accountComment;
Â  Â  Â  Â  Â  }
Â  Â  Â  Â  });
Â  Â  Â  Â  setCommentsMap(initMap);
Â  Â  Â  } catch (e) {
Â  Â  Â  Â  console.error(e);
Â  Â  Â  Â  setError(`Failed to load accounts: ${e.message}`);
Â  Â  Â  } finally {
Â  Â  Â  Â  setLoading(false);
Â  Â  Â  }
Â  Â  };

Â  Â  // Read from React state, sessionStorage, or URL query
Â  Â  const qs = new URLSearchParams(location.search);
Â  Â  let m = location.state?.month;
Â  Â  let y = location.state?.year;
Â  Â  let s = location.state?.sbu;

Â  Â  if (!m) {
Â  Â  Â  const sm = sessionStorage.getItem('lastFetchedAccountMonth');
Â  Â  Â  const qm = qs.get('month');
Â  Â  Â  m = sm ?? qm;
Â  Â  }
Â  Â  if (!y) {
Â  Â  Â  const sy = sessionStorage.getItem('lastFetchedAccountYear');
Â  Â  Â  const qy = qs.get('year');
Â  Â  Â  y = sy ?? qy;
Â  Â  }
Â  Â  if (!s) {
Â  Â  Â  const ss = sessionStorage.getItem('lastFetchedAccountSbu');
Â  Â  Â  const qsS = qs.get('sbu');
Â  Â  Â  s = ss ?? qsS;
Â  Â  }

Â  Â  // Normalize numeric values
Â  Â  m = m ? parseInt(m, 10) : null;
Â  Â  y = y ? parseInt(y, 10) : null;

Â  Â  if (m && y && s) {
Â  Â  Â  const mn = getMonthName(m);
Â  Â  Â  // only refetch if any filter changed or first load
Â  Â  Â  if (
Â  Â  Â  Â  m !== activeMonth ||
Â  Â  Â  Â  y !== activeYear ||
Â  Â  Â  Â  s !== activeSbu ||
Â  Â  Â  Â  accounts.length === 0
Â  Â  Â  ) {
Â  Â  Â  Â  setActiveMonth(m);
Â  Â  Â  Â  setActiveYear(y);
Â  Â  Â  Â  setActiveMonthName(mn);
Â  Â  Â  Â  setActiveSbu(s);

Â  Â  Â  Â  sessionStorage.setItem('lastFetchedAccountMonth', m.toString());
Â  Â  Â  Â  sessionStorage.setItem('lastFetchedAccountYear', y.toString());
Â  Â  Â  Â  sessionStorage.setItem('lastFetchedAccountSbu', s);

Â  Â  Â  Â  fetchAccountData(m, y, mn, s);
Â  Â  Â  }
Â  Â  } else {
Â  Â  Â  setError(
Â  Â  Â  Â  'Missing month, year or SBU. Please navigate here with those parameters.'
Â  Â  Â  );
Â  Â  Â  setLoading(false);
Â  Â  }
Â  }, [location.state, location.search, activeMonth, activeYear, activeSbu, accounts.length]);

Â  // Initialize & destroy DataTable
Â  useEffect(() => {
Â  Â  if (!loading && accounts.length > 0 && tableRef.current) {
Â  Â  Â  const $tbl = $(tableRef.current);
Â  Â  Â  if ($.fn.DataTable.isDataTable($tbl)) {
Â  Â  Â  Â  $tbl.DataTable().destroy();
Â  Â  Â  }
Â  Â  Â  const dataTable = $tbl.DataTable({
Â  Â  Â  Â  paging: true,
Â  Â  Â  Â  searching: true,
Â  Â  Â  Â  ordering: true,
Â  Â  Â  Â  info: true,
Â  Â  Â  Â  autoWidth: false,
Â  Â  Â  });
      
      // Add individual column filters
      dataTable.columns().every(function () {
          const column = this;
          const columnIndex = column.index();

          // Skip the last two columns (Comment and View)
          if (columnIndex >= 6) {
              return;
          }

          const headerCell = $(column.header());
          const input = $('<input type="text" class="form-control form-control-sm" placeholder="Filter..." />')
              .appendTo(headerCell)
              .on('keyup change clear', function () {
                  if (column.search() !== this.value) {
                      column.search(this.value).draw();
                  }
              });

          // Style the header to stack the title and filter input
          headerCell.css({
              display: 'flex',
              'flex-direction': 'column',
              'justify-content': 'center',
              'align-items': 'center',
              'gap': '5px' // Space between title and input
          });
      });
Â  Â  }
Â  Â  return () => {
Â  Â  Â  if (tableRef.current) {
Â  Â  Â  Â  const $tbl = $(tableRef.current);
Â  Â  Â  Â  if ($.fn.DataTable.isDataTable($tbl)) {
Â  Â  Â  Â  Â  $tbl.DataTable().destroy();
Â  Â  Â  Â  }
Â  Â  Â  }
Â  Â  };
Â  }, [loading, accounts]);

Â  // Download Excel (.xls) of current table view (respects DataTables filters/search)
Â  const handleDownloadExcel = () => {
Â  Â  const $tbl = $(tableRef.current);
Â  Â  let headers = [
Â  Â  Â  'Account ID',
Â  Â  Â  'Account Name',
Â  Â  Â  'Total Projects',
Â  Â  Â  'Total Revenue',
Â  Â  Â  'Forecast Revenue',
Â  Â  Â  'Revenue Difference',
Â  Â  ];
Â  Â  let rows = [];

Â  Â  if ($.fn.DataTable.isDataTable($tbl)) {
Â  Â  Â  const dt = $tbl.DataTable();

Â  Â  Â  // Read headers (first 6 columns)
Â  Â  Â  headers = $tbl
Â  Â  Â  Â  .find('thead th')
Â  Â  Â  Â  .slice(0, 6)
Â  Â  Â  Â  .map(function () {
Â  Â  Â  Â  Â  // Extract only the header text, not the filter input text
Â  Â  Â  Â  Â  return $(this).clone().children().remove().end().text().trim();
Â  Â  Â  Â  })
Â  Â  Â  Â  .get();

Â  Â  Â  // Read visible rows (filtered) and grab displayed text for first 6 cells
Â  Â  Â  const nodes = dt.rows({ search: 'applied' }).nodes().toArray();
Â  Â  Â  rows = nodes.map((tr) =>
Â  Â  Â  Â  Array.from(tr.cells)
Â  Â  Â  Â  Â  .slice(0, 6)
Â  Â  Â  Â  Â  .map((td) => td.textContent.trim())
Â  Â  Â  );
Â  Â  } else {
Â  Â  Â  // Fallback: build from state
Â  Â  Â  rows = accounts.map((acc) => [
Â  Â  Â  Â  String(acc.accountId ?? ''),
Â  Â  Â  Â  String(acc.accountName ?? ''),
Â  Â  Â  Â  String(acc.totalProjects ?? ''),
Â  Â  Â  Â  formatCurrency(acc.totalRevenue ?? 0),
Â  Â  Â  Â  formatCurrency(acc.forecastNetRevenue ?? 0),
Â  Â  Â  Â  formatCurrency(acc.difference ?? 0),
Â  Â  Â  ]);
Â  Â  }

Â  Â  // Build simple HTML table which Excel can open as .xls
Â  Â  const theadHtml =
Â  Â  Â  '<tr>' + headers.map((h) => `<th>${h.replace(/&/g, '&amp;').replace(/</g, '&lt;')}</th>`).join('') + '</tr>';
Â  Â  const tbodyHtml = rows
Â  Â  Â  .map(
Â  Â  Â  Â  (r) =>
Â  Â  Â  Â  Â  '<tr>' +
Â  Â  Â  Â  Â  r
Â  Â  Â  Â  Â  Â  .map((v) => `<td>${String(v).replace(/&/g, '&amp;').replace(/</g, '&lt;')}</td>`)
Â  Â  Â  Â  Â  Â  .join('') +
Â  Â  Â  Â  Â  '</tr>'
Â  Â  Â  )
Â  Â  Â  .join('');

Â  Â  const html =
Â  Â  Â  `<html xmlns:o="urn:schemas-microsoft-com:office:office" ` +
Â  Â  Â  `xmlns:x="urn:schemas-microsoft-com:office:excel" ` +
Â  Â  Â  `xmlns="http://www.w3.org/TR/REC-html40">` +
Â  Â  Â  `<head></head>` +
Â  Â  Â  `<body><table border="1"><thead>${theadHtml}</thead><tbody>${tbodyHtml}</tbody></table></body></html>`;

Â  Â  const blob = new Blob([html], { type: 'application/vnd.ms-excel;charset=utf-8;' });
Â  Â  const url = URL.createObjectURL(blob);
Â  Â  const a = document.createElement('a');
Â  Â  const fileName = `AccountLevel_${activeSbu || 'All'}_${activeMonthName || ''}${activeYear || ''}.xls`;
Â  Â  a.href = url;
Â  Â  a.download = fileName.replace(/\s+/g, '_');
Â  Â  document.body.appendChild(a);
Â  Â  a.click();
Â  Â  a.remove();
Â  Â  URL.revokeObjectURL(url);
Â  };

Â  // Open comment modal
Â  const openCommentModal = (accId) => {
Â  Â  setCurrentAccountId(accId);
Â  Â  setCommentInput(commentsMap[accId] || '');
Â  Â  setModalVisible(true);
Â  };

Â  // Save or update comment
Â  const saveComment = async () => {
Â  Â  try {
Â  Â  Â  const dto = {
Â  Â  Â  Â  accId: currentAccountId,
Â  Â  Â  Â  month: activeMonth,
Â  Â  Â  Â  year: activeYear,
Â  Â  Â  Â  comment: commentInput,
Â  Â  Â  };
Â  Â  Â  const resp = await fetch(`${BACKEND_URL}/api/account/comment`, {
Â  Â  Â  Â  method: 'POST',
Â  Â  Â  Â  headers: { 'Content-Type': 'application/json' },
Â  Â  Â  Â  body: JSON.stringify(dto),
Â  Â  Â  });
Â  Â  Â  if (!resp.ok) {
Â  Â  Â  Â  throw new Error(await resp.text());
Â  Â  Â  }
Â  Â  Â  // update local maps
Â  Â  Â  setCommentsMap((m) => ({ ...m, [currentAccountId]: commentInput }));
Â  Â  Â  setAccounts((list) =>
Â  Â  Â  Â  list.map((acc) =>
Â  Â  Â  Â  Â  acc.accountId === currentAccountId
Â  Â  Â  Â  Â  Â  ? { ...acc, accountComment: commentInput }
Â  Â  Â  Â  Â  Â  : acc
Â  Â  Â  Â  )
Â  Â  Â  );
Â  Â  Â  setModalVisible(false);
Â  Â  } catch (e) {
Â  Â  Â  alert('Error saving comment: ' + e.message);
Â  Â  }
Â  };

Â  // Dynamic breadcrumb path
Â  const breadcrumbPath = [
Â  Â  { name: 'PMO Dashboard', page: '' },
Â  Â  { name: 'Revenue Forecast - Early View', page: 'upload' },
Â  Â  ...(activeSbu ? [{ name: `${activeSbu} SBU Level`, page: 'sbu' }] : []),
Â  Â  { name: 'Account Level', page: 'accounts' },
Â  ].filter(Boolean);

Â  return (
Â  Â  <div
Â  Â  Â  className="min-vh-100 d-flex flex-column align-items-center px-3 py-4"
Â  Â  Â  style={{
Â  Â  Â  Â  background: 'linear-gradient(to bottom right, #f5f7fa, #e9ecef)',
Â  Â  Â  Â  fontFamily: "'Poppins', sans-serif",
Â  Â  Â  }}
Â  Â  >
Â  Â  Â  <div
Â  Â  Â  Â  className="card shadow p-4 p-md-5 w-100"
Â  Â  Â  Â  style={{
Â  Â  Â  Â  Â  maxWidth: '1100px',
Â  Â  Â  Â  Â  backgroundColor: '#ffffffdd',
Â  Â  Â  Â  Â  borderRadius: '1rem',
Â  Â  Â  Â  }}
Â  Â  Â  >
Â  Â  Â  Â  <Breadcrumbs path={breadcrumbPath} />

Â  Â  Â  Â  <div className="d-flex flex-column flex-md-row justify-content-between align-items-md-center mb-3">
Â  Â  Â  Â  Â  <h2 className="mb-3 mb-md-0 fw-semibold text-dark">
Â  Â  Â  Â  Â  Â  ğŸ¢ Account Level Overview â€“ {activeSbu}
Â  Â  Â  Â  Â  Â  {activeMonthName && activeYear && (
Â  Â  Â  Â  Â  Â  Â  <span className="ms-2 text-primary">
Â  Â  Â  Â  Â  Â  Â  Â  ({activeMonthName} {activeYear})
Â  Â  Â  Â  Â  Â  Â  </span>
Â  Â  Â  Â  Â  Â  )}
Â  Â  Â  Â  Â  </h2>
Â  Â  Â  Â  Â  <button
Â  Â  Â  Â  Â  Â  type="button"
Â  Â  Â  Â  Â  Â  onClick={handleDownloadExcel}
Â  Â  Â  Â  Â  Â  disabled={loading || !!error}
Â  Â  Â  Â  Â  Â  title="Download current table view as Excel"
Â  Â  Â  Â  Â  Â  className="btn"
Â  Â  Â  Â  Â  Â  style={{ backgroundColor: '#ffffff', border: '1px solid #198754', color: '#198754' }}
Â  Â  Â  Â  Â  >
Â  Â  Â  Â  Â  Â  Download
Â  Â  Â  Â  Â  </button>
Â  Â  Â  Â  </div>

Â  Â  Â  Â  {loading ? (
Â  Â  Â  Â  Â  <div className="d-flex justify-content-center align-items-center">
Â  Â  Â  Â  Â  Â  <div className="spinner-border text-primary" role="status" />
Â  Â  Â  Â  Â  Â  <p className="ms-3 text-primary">Loading Account Data...</p>
Â  Â  Â  Â  Â  </div>
Â  Â  Â  Â  ) : error ? (
Â  Â  Â  Â  Â  <div className="d-flex flex-column justify-content-center align-items-center text-danger">
Â  Â  Â  Â  Â  Â  <p className="fs-4">Error: {error}</p>
Â  Â  Â  Â  Â  Â  <button
Â  Â  Â  Â  Â  Â  Â  className="btn btn-primary mt-3"
Â  Â  Â  Â  Â  Â  Â  onClick={() => navigate('/upload')}
Â  Â  Â  Â  Â  Â  >
Â  Â  Â  Â  Â  Â  Â  Go to Upload Page
Â  Â  Â  Â  Â  Â  </button>
Â  Â  Â  Â  Â  </div>
Â  Â  Â  Â  ) : (
Â  Â  Â  Â  Â  <div className="table-responsive rounded">
Â  Â  Â  Â  Â  Â  <table
Â  Â  Â  Â  Â  Â  Â  ref={tableRef}
Â  Â  Â  Â  Â  Â  Â  className="display table table-hover align-middle table-bordered mb-0"
Â  Â  Â  Â  Â  Â  Â  style={{ width: '100%' }}
Â  Â  Â  Â  Â  Â  >
Â  Â  Â  Â  Â  Â  Â  <thead
Â  Â  Â  Â  Â  Â  Â  Â  className="text-white"
Â  Â  Â  Â  Â  Â  Â  Â  style={{
Â  Â  Â  Â  Â  Â  Â  Â  Â  background: 'linear-gradient(to right, #1d4ed8, #2563eb)',
Â  Â  Â  Â  Â  Â  Â  Â  }}
Â  Â  Â  Â  Â  Â  Â  >
Â  Â  Â  Â  Â  Â  Â  Â  <tr>
Â  Â  Â  Â  Â  Â  Â  Â  Â  <th>Account ID</th>
Â  Â  Â  Â  Â  Â  Â  Â  Â  <th>Account Name</th>
Â  Â  Â  Â  Â  Â  Â  Â  Â  <th>Total Projects</th>
Â  Â  Â  Â  Â  Â  Â  Â  Â  <th>Total Revenue</th>
Â  Â  Â  Â  Â  Â  Â  Â  Â  <th>Forecast Revenue</th>
Â  Â  Â  Â  Â  Â  Â  Â  Â  <th>Revenue Difference</th>
Â  Â  Â  Â  Â  Â  Â  Â  Â  <th className="text-center">Comment</th>
Â  Â  Â  Â  Â  Â  Â  Â  Â  <th className="text-center">View</th>
Â  Â  Â  Â  Â  Â  Â  Â  </tr>
Â  Â  Â  Â  Â  Â  Â  </thead>
Â  Â  Â  Â  Â  Â  Â  <tbody>
Â  Â  Â  Â  Â  Â  Â  Â  {accounts.map((acc) => (
Â  Â  Â  Â  Â  Â  Â  Â  Â  <tr key={acc.accountId}>
