import React, { useRef, useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import $ from 'jquery';
import 'datatables.net';
import 'datatables.net-dt/css/dataTables.dataTables.min.css';
import Breadcrumbs from '../Breadcrumbs';
import commonData from '../../data/commonData.json';
import * as XLSX from 'xlsx'; // Import XLSX for Excel export
import { saveAs } from 'file-saver'; // Import saveAs for triggering file download

// Utility to convert month number to month name
const getMonthName = (monthNumber) => {
  const date = new Date();
  date.setMonth(monthNumber - 1);
  return date.toLocaleString('en-US', { month: 'long' });
};

// Filterable columns (indices from 0 to 5)
const FILTERABLE_COL_INDICES = [0, 1, 2, 3, 4, 5];

const AccountLevel = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const tableRef = useRef(null);
  const tableInstanceRef = useRef(null); // Ref to hold the DataTable instance
  const dropdownRef = useRef(null); // Ref for the filter dropdown

  // UI state
  const [accounts, setAccounts] = useState([]);
  const [commentsMap, setCommentsMap] = useState({});
  const [modalVisible, setModalVisible] = useState(false);
  const [currentAccountId, setCurrentAccountId] = useState(null);
  const [commentInput, setCommentInput] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Active filters
  const [activeMonth, setActiveMonth] = useState(null);
  const [activeYear, setActiveYear] = useState(null);
  const [activeMonthName, setActiveMonthName] = useState(null);
  const [activeSbu, setActiveSbu] = useState(null);

  // Dropdown filter state
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [dropdownColIdx, setDropdownColIdx] = useState(null);
  const [dropdownPosition, setDropdownPosition] = useState({ top: 0, left: 0 });
  const [dropdownValues, setDropdownValues] = useState([]); // Unique values for the current dropdown
  const [tempSelections, setTempSelections] = useState(new Set()); // Selections in the open dropdown
  const [listQuery, setListQuery] = useState(''); // Search query for values within the dropdown
  const [columnSelections, setColumnSelections] = useState({}); // { [colIdx]: Set<string> } - Applied filters

  const BACKEND_URL = 'http://localhost:8081';

  // Currency formatter
  const {
    locale,
    currency,
    minimumFractionDigits,
    maximumFractionDigits,
  } = commonData.currencySettings;

  const formatCurrency = (value = 0) =>
    value.toLocaleString(locale, {
      style: 'currency',
      currency,
      minimumFractionDigits,
      maximumFractionDigits,
    });

  /**
   * Handles the "Download Data" button click.
   * Generates an Excel (XLSX) file from the filtered 'accounts' data and triggers a download.
   */
  const handleDownloadData = () => {
    // Get the currently filtered data from DataTables
    const dataToExport = tableInstanceRef.current
      ? tableInstanceRef.current.rows({ search: 'applied' }).data().toArray()
      : accounts; // Fallback to all accounts if DataTable isn't initialized or no filter applied

    if (dataToExport.length === 0) {
      console.log('No data to download after applying filters.');
      // Optionally, show a user-friendly message here
      return;
    }

    const headers = [
      'Account ID',
      'Account Name',
      'Total Projects',
      'Total Revenue',
      'Forecast Revenue',
      'Revenue Difference',
      'Comment',
    ];

    const dataForExcel = dataToExport.map((account) => ({
      'Account ID': account.accountId,
      'Account Name': account.accountName,
      'Total Projects': account.totalProjects,
      'Total Revenue': formatCurrency(account.totalRevenue),
      'Forecast Revenue': formatCurrency(account.forecastNetRevenue),
      'Revenue Difference': formatCurrency(account.difference),
      'Comment': account.accountComment || '',
    }));

    const wb = XLSX.utils.book_new();
    const ws = XLSX.utils.json_to_sheet(dataForExcel, { header: headers });

    XLSX.utils.book_append_sheet(wb, ws, 'Account Data');

    const excelBuffer = XLSX.write(wb, { bookType: 'xlsx', type: 'array' });
    const dataBlob = new Blob([excelBuffer], {
      type: 'application/octet-stream',
    });

    const monthName = getMonthName(activeMonth);
    const filename = `Account_Data_${activeSbu}_${monthName}_${activeYear}.xlsx`;
    saveAs(dataBlob, filename);

    console.log('Downloading filtered account data in Excel format!');
  };


  // Load Poppins font + inject custom CSS for filter icons/dropdown
  useEffect(() => {
    const link = document.createElement('link');
    link.href =
      'https://fonts.googleapis.com/css2?family=Poppins:wght@400;600;700&display=swap';
    link.rel = 'stylesheet';
    document.head.appendChild(link);
    document.body.style.fontFamily = "'Poppins', sans-serif";

    // Inject CSS to hide DataTables sorting arrows and style custom filters
    const style = document.createElement('style');
    style.innerHTML = `
      table.dataTable thead .sorting,
      table.dataTable thead .sorting_asc,
      table.dataTable thead .sorting_desc,
      table.dataTable thead .sorting_asc_disabled,
      table.dataTable thead .sorting_desc_disabled {
        background-image: none !important;
        cursor: default !important;
      }
      .filter-icon-btn {
        border: none;
        background: transparent;
        padding: 2px;
        margin-left: 8px;
        cursor: pointer;
        display: inline-flex;
        align-items: center;
        vertical-align: middle; /* Align icon vertically with text */
      }
      .filter-icon-btn svg { fill: #6b7280; } /* Default icon color */
      .filter-icon-btn.active svg { fill: #1d4ed8; } /* Active filter icon color */
      .excel-filter-dropdown {
        position: absolute;
        z-index: 2000;
        width: 260px;
        background: #fff;
        border: 1px solid #e5e7eb;
        border-radius: 8px;
        box-shadow: 0 10px 25px rgba(0,0,0,0.1);
        font-family: 'Poppins', sans-serif; /* Ensure dropdown uses Poppins */
      }
      .excel-filter-header {
        padding: 10px 12px;
        border-bottom: 1px solid #f1f5f9;
        background: #f8fafc;
        border-top-left-radius: 8px;
        border-top-right-radius: 8px;
      }
      .excel-filter-body {
        max-height: 240px;
        overflow-y: auto; /* Enable vertical scrolling */
        padding: 8px 12px;
      }
      .excel-filter-footer {
        display: flex;
        justify-content: flex-end;
        gap: 8px;
        padding: 10px 12px;
        border-top: 1px solid #f1f5f9;
      }
      .form-check-label {
        margin-bottom: 0; /* Remove default margin for checkboxes */
        cursor: pointer;
      }
    `;
    document.head.appendChild(style);
  }, []);

  // Fetch accounts when filters change
  useEffect(() => {
    const fetchAccountData = async (month, year, monthNameParam, sbu) => {
      setLoading(true);
      setError(null);

      try {
        const payload = { month, year, monthNameParam, sbu };
        const resp = await fetch(`${BACKEND_URL}/api/account`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(payload),
        });
        if (!resp.ok) {
          const txt = await resp.text();
          throw new Error(txt || resp.statusText);
        }
        const data = await resp.json();
        const list = Array.isArray(data) ? data : [data].filter(Boolean);
        setAccounts(list);

        // seed comments map
        const initMap = {};
        list.forEach((acc) => {
          if (acc.accountComment) {
            initMap[acc.accountId] = acc.accountComment;
          }
        });
        setCommentsMap(initMap);
      } catch (e) {
        console.error(e);
        setError(`Failed to load accounts: ${e.message}`);
      } finally {
        setLoading(false);
      }
    };

    // Read from React state, sessionStorage, or URL query
    const qs = new URLSearchParams(location.search);
    let m = location.state?.month;
    let y = location.state?.year;
    let s = location.state?.sbu;

    if (!m) {
      const sm = sessionStorage.getItem('lastFetchedAccountMonth');
      const qm = qs.get('month');
      m = sm ?? qm;
    }
    if (!y) {
      const sy = sessionStorage.getItem('lastFetchedAccountYear');
      const qy = qs.get('year');
      y = sy ?? qy;
    }
    if (!s) {
      const ss = sessionStorage.getItem('lastFetchedAccountSbu');
      const qsS = qs.get('sbu');
      s = ss ?? qsS;
    }

    // Normalize numeric values
    m = m ? parseInt(m, 10) : null;
    y = y ? parseInt(y, 10) : null;

    if (m && y && s) {
      const mn = getMonthName(m);
      if (
        m !== activeMonth ||
        y !== activeYear ||
        s !== activeSbu ||
        accounts.length === 0
      ) {
        setActiveMonth(m);
        setActiveYear(y);
        setActiveMonthName(mn);
        setActiveSbu(s);

        sessionStorage.setItem('lastFetchedAccountMonth', m.toString());
        sessionStorage.setItem('lastFetchedAccountYear', y.toString());
        sessionStorage.setItem('lastFetchedAccountSbu', s);

        fetchAccountData(m, y, mn, s);
      }
    } else {
      setError(
        'Missing month, year or SBU. Please navigate here with those parameters.'
      );
      setLoading(false);
    }
  }, [location.state, location.search, activeMonth, activeYear, activeSbu, accounts.length]);

  // Initialize & destroy DataTable (ordering disabled; arrows hidden). Re-apply filters after init.
  useEffect(() => {
    if (!loading && accounts.length > 0 && tableRef.current) {
      const $tbl = $(tableRef.current);

      // Destroy existing DataTable instance if it exists
      if ($.fn.DataTable.isDataTable($tbl)) {
        tableInstanceRef.current.destroy();
      }

      // Initialize DataTable
      const instance = $tbl.DataTable({
        paging: true,
        searching: true, // Global search
        ordering: false, // Disable all default sorting arrows
        info: true,
        autoWidth: false,
        data: accounts, // Pass the accounts data
        columns: [
          { data: 'accountId', orderable: false }, // Set to false to remove sorting arrows
          { data: 'accountName', orderable: false }, // Already false, kept as is
          { data: 'totalProjects', orderable: false },
          { data: 'totalRevenue', orderable: false, render: (data) => formatCurrency(data) },
          { data: 'forecastNetRevenue', orderable: false, render: (data) => formatCurrency(data) },
          { data: 'difference', orderable: false, render: (data) => formatCurrency(data) },
          {
            data: 'accountComment',
            orderable: false, // Comment column not sortable
            className: 'text-center',
            render: function (data, type, row) {
              return `
                <button class="btn btn-sm btn-outline-secondary comment-btn" data-account-id="${row.accountId}">
                  üí¨
                </button>
              `;
            }
          },
          {
            data: null,
            orderable: false, // View button column not sortable
            className: 'text-center',
            render: function (data, type, row) {
              return `
                <button class="btn btn-sm btn-outline-primary rounded-circle view-btn" data-account-id="${row.accountId}">
                  üîç
                </button>
              `;
            }
          },
        ],
        order: [], // Remove default sorting to prevent initial sorting on disabled columns
        // No initComplete for column inputs, as we're using custom dropdowns
      });

      tableInstanceRef.current = instance;
      // Re-apply any previously selected filters after DataTables is initialized
      applyDataTablesFilters(instance);

      // Re-attach event listeners for dynamically rendered buttons (comments and view)
      // DataTables re-renders rows, so we need delegated event listeners on the table body
      $tbl.off('click', '.comment-btn').on('click', '.comment-btn', function() {
        const accId = $(this).data('account-id');
        openCommentModal(accId);
      });

      $tbl.off('click', '.view-btn').on('click', '.view-btn', function() {
        const accId = $(this).data('account-id');
        navigate(`/accounts/${accId}/project-types`, {
          state: {
            accId: accId,
            month: activeMonth,
            year: activeYear,
            monthName: activeMonthName,
            sbu: activeSbu,
          },
        });
      });
    }

    // Cleanup function to destroy DataTable instance and event listeners
    return () => {
      if (tableInstanceRef.current) {
        tableInstanceRef.current.destroy();
        tableInstanceRef.current = null;
      }
      // Remove delegated event listeners to prevent memory leaks
      // Ensure we target the table element for event listener removal
      $(tableRef.current).off('click', '.comment-btn');
      $(tableRef.current).off('click', '.view-btn');
    };
    // Re-run effect if loading state changes or accounts data changes (for re-initialization)
  }, [loading, accounts, activeMonth, activeYear, activeMonthName, activeSbu]);

  // Close dropdown on outside click or Esc key press
  useEffect(() => {
    if (!dropdownOpen) return;
    const onDocClick = (e) => {
      // Check if click is inside the dropdown or the filter icon button
      if (dropdownRef.current && dropdownRef.current.contains(e.target)) return;
      if ($(e.target).closest('.filter-icon-btn').length) return;
      setDropdownOpen(false);
    };
    const onKeyDown = (e) => {
      if (e.key === 'Escape') setDropdownOpen(false);
    };
    document.addEventListener('mousedown', onDocClick);
    document.addEventListener('keydown', onKeyDown);
    return () => {
      document.removeEventListener('mousedown', onDocClick);
      document.removeEventListener('keydown', onKeyDown);
    };
  }, [dropdownOpen]);

  // Helpers: Get cell's displayed value and unique values for a column
  const getCellDisplayValue = (acc, colIdx) => {
    switch (colIdx) {
      case 0: return String(acc.accountId ?? '');
      case 1: return String(acc.accountName ?? '');
      case 2: return String(acc.totalProjects ?? '');
      case 3: return formatCurrency(acc.totalRevenue ?? 0);
      case 4: return formatCurrency(acc.forecastNetRevenue ?? 0);
      case 5: return formatCurrency(acc.difference ?? 0);
      default: return '';
    }
  };

  const getUniqueValuesForColumn = (colIdx) => {
    const set = new Set();
    accounts.forEach((acc) => {
      set.add(getCellDisplayValue(acc, colIdx));
    });
    return Array.from(set).sort((a, b) =>
      String(a).localeCompare(String(b), undefined, { numeric: true })
    );
  };

  // Check if a column has an active filter applied (some but not all values selected)
  const isColumnFiltered = (colIdx) => {
    if (!FILTERABLE_COL_INDICES.includes(colIdx)) return false;
    const appliedSet = columnSelections[colIdx];
    if (!appliedSet) return false; // No filter applied
    const uniqueVals = getUniqueValuesForColumn(colIdx);
    // Filter is active if some values are selected but not all
    return appliedSet.size > 0 && appliedSet.size < uniqueVals.length;
  };

  // Open dropdown for a column
  const openFilterDropdown = (event, colIdx) => {
    if (!FILTERABLE_COL_INDICES.includes(colIdx)) return;

    const rect = event.currentTarget.getBoundingClientRect(); // Get position of the clicked header button
    const uniqueValues = getUniqueValuesForColumn(colIdx);

    // Initialize tempSelections based on current applied filter or all unique values
    const appliedSet = columnSelections[colIdx];
    const initialSet =
      appliedSet && appliedSet.size > 0 ? new Set(appliedSet) : new Set(uniqueValues);

    setDropdownValues(uniqueValues);
    setTempSelections(initialSet);
    setListQuery(''); // Clear dropdown search query
    setDropdownColIdx(colIdx);
    setDropdownPosition({
      top: rect.bottom + window.scrollY + 4, // Position below the clicked header
      left: rect.left + window.scrollX,
    });
    setDropdownOpen(true);
  };

  // Dropdown interactions
  const toggleTempSelection = (value) => {
    const next = new Set(tempSelections);
    if (next.has(value)) next.delete(value);
    else next.add(value);
    setTempSelections(next);
  };
  const selectAllTemp = () => setTempSelections(new Set(dropdownValues));
  const clearAllTemp = () => setTempSelections(new Set());

  // Apply filter to DataTable
  const applyFilter = () => {
    if (dropdownColIdx == null || !tableInstanceRef.current) return;

    // Update the columnSelections state with the new temp selections
    setColumnSelections((prev) => ({
      ...prev,
      [dropdownColIdx]: new Set(tempSelections),
    }));

    // Re-apply all filters via DataTables
    applyDataTablesFilters(tableInstanceRef.current, {
      overrideColIdx: dropdownColIdx,
      overrideSet: new Set(tempSelections),
    });

    setDropdownOpen(false); // Close dropdown
  };

  // Reset filter for the current column
  const resetFilter = () => {
    if (dropdownColIdx == null || !tableInstanceRef.current) return;

    // Remove the filter for this column from state
    setColumnSelections((prev) => {
      const next = { ...prev };
      delete next[dropdownColIdx];
      return next;
    });

    // Clear the filter for this column in DataTables and redraw
    tableInstanceRef.current.column(dropdownColIdx).search('').draw();

    setDropdownOpen(false); // Close dropdown
  };

  // Apply all active column filters to DataTables instance
  const applyDataTablesFilters = (instance, options = {}) => {
    FILTERABLE_COL_INDICES.forEach((colIdx) => {
      const uniqueValues = getUniqueValuesForColumn(colIdx);
      const appliedSet =
        options.overrideColIdx === colIdx
          ? options.overrideSet // Use override for the currently changed column
          : columnSelections[colIdx]; // Otherwise, use the stored selection

      // If no selections, all selected, or the column is not filterable by this mechanism, clear its filter
      if (!appliedSet || appliedSet.size === 0 || appliedSet.size === uniqueValues.length) {
        instance.column(colIdx).search('', false, false, true); // Clear filter
      } else {
        // Build a regex to match any of the selected values
        const escaped = Array.from(appliedSet).map((v) =>
          $.fn.dataTable.util.escapeRegex(String(v))
        );
        const regex = `^(${escaped.join('|')})$`; // Match exactly one of the selected values
        instance.column(colIdx).search(regex, true, false, true); // Apply regex filter
      }
    });

    instance.draw(); // Redraw the table to apply filters
  };

  // Close dropdown on outside click or Esc key press
  useEffect(() => {
    if (!dropdownOpen) return;
    const onDocClick = (e) => {
      // Check if click is inside the dropdown or the filter icon button
      if (dropdownRef.current && dropdownRef.current.contains(e.target)) return;
      if ($(e.target).closest('.filter-icon-btn').length) return;
      setDropdownOpen(false);
    };
    const onKeyDown = (e) => {
      if (e.key === 'Escape') setDropdownOpen(false);
    };
    document.addEventListener('mousedown', onDocClick);
    document.addEventListener('keydown', onKeyDown);
    return () => {
      document.removeEventListener('mousedown', onDocClick);
      document.removeEventListener('keydown', onKeyDown);
    };
  }, [dropdownOpen]);

  // Define breadcrumb path for navigation
  const breadcrumbPath = [
    { name: 'PMO Dashboard', page: '' }, // Always starts with Dashboard
    { name: 'Revenue Forecast - Early View', page: 'upload' }, // Added this line
    ...(activeSbu ? [{ name: `${activeSbu} SBU Level`, page: 'sbu' }] : []), // Conditionally add SBU Level
    { name: 'Account Level', page: 'accounts' },
  ].filter(Boolean);

  // Hamburger icon component
  const HamburgerIcon = ({ active }) => (
    <svg width="14" height="14" viewBox="0 0 24 24" aria-hidden="true" style={{ verticalAlign: 'middle', fill: active ? '#1d4ed8' : '#6b7280' }}>
      <path d="M3 6h18v2H3zM3 11h18v2H3zM3 16h18v2H3z" />
    </svg>
  );

  // Filter dropdown filtered list (based on internal search query)
  const filteredDropdownValues = dropdownValues.filter((v) =>
    String(v).toLowerCase().includes(listQuery.toLowerCase())
  );

  return (
    <div
      className="min-vh-100 d-flex flex-column align-items-center px-3 py-4"
      style={{
        background: 'linear-gradient(to bottom right, #f5f7fa, #e9ecef)',
        fontFamily: "'Poppins', sans-serif",
      }}
    >
      <div
        className="card shadow p-4 p-md-5 w-100"
        style={{
          maxWidth: '1100px',
          backgroundColor: '#ffffffdd',
          borderRadius: '1rem',
        }}
      >
        <Breadcrumbs path={breadcrumbPath} />

        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2 className="mb-0 fw-semibold text-dark">
            üè¢ Account Level Overview ‚Äì {activeSbu}
            {activeMonthName && activeYear && (
              <span className="ms-2 text-primary">
                ({activeMonthName} {activeYear})
              </span>
            )}
          </h2>
          {/* Download button, visible only if accounts are loaded */}
          {accounts.length > 0 && (
            <button
              className="btn btn-outline-success rounded-pill px-3"
              onClick={handleDownloadData}
              title="Download Account Data"
            >
              <i className="bi bi-download me-2"></i> Download Data
            </button>
          )}
        </div>

        {loading ? (
          <div className="d-flex justify-content-center align-items-center">
            <div className="spinner-border text-primary" role="status" />
            <p className="ms-3 text-primary">Loading Account Data...</p>
          </div>
        ) : error ? (
          <div className="d-flex flex-column justify-content-center align-items-center text-danger">
            <p className="fs-4">Error: {error}</p>
            <button
              className="btn btn-primary mt-3"
              onClick={() => navigate('/upload')}
            >
              Go to Upload Page
            </button>
          </div>
        ) : (
          <div className="table-responsive rounded">
            <table
              ref={tableRef}
              className="display table table-hover align-middle table-bordered mb-0"
              style={{ width: '100%' }}
            >
              <thead
                className="text-white"
                style={{
                  background: 'linear-gradient(to right, #1d4ed8, #2563eb)',
                }}
              >
                <tr>
                  {[
                    'Account ID',
                    'Account Name',
                    'Total Projects',
                    'Total Revenue',
                    'Forecast Revenue',
                    'Revenue Difference',
                    'Comment',
                    'View',
                  ].map((title, idx) => (
                    <th key={idx}>
                      <div className="d-flex justify-content-between align-items-center">
                        <span>{title}</span>
                        {FILTERABLE_COL_INDICES.includes(idx) && (
                          <button
                            type="button"
                            className={
                              'filter-icon-btn ' +
                              (isColumnFiltered(idx) ? 'active' : '')
                            }
                            onClick={(e) => openFilterDropdown(e, idx)}
                            title="Filter"
                          >
                            <HamburgerIcon active={isColumnFiltered(idx)} />
                          </button>
                        )}
                      </div>
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {accounts.map((acc) => (
                  <tr key={acc.accountId}>
                    <td>{getCellDisplayValue(acc, 0)}</td>
                    <td>{getCellDisplayValue(acc, 1)}</td>
                    <td>{getCellDisplayValue(acc, 2)}</td>
                    <td>{getCellDisplayValue(acc, 3)}</td>
                    <td>{getCellDisplayValue(acc, 4)}</td>
                    <td>{getCellDisplayValue(acc, 5)}</td>
                    <td className="text-center">
                      <button
                        className="btn btn-sm btn-outline-secondary comment-btn"
                        data-account-id={acc.accountId}
                      >
                        üí¨
                      </button>
                    </td>
                    <td className="text-center">
                      <button
                        className="btn btn-sm btn-outline-primary rounded-circle view-btn"
                        data-account-id={acc.accountId}
                      >
                        üîç
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Hamburger-style Filter Dropdown */}
      {dropdownOpen && (
        <div
          ref={dropdownRef}
          className="excel-filter-dropdown"
          style={{ top: dropdownPosition.top, left: dropdownPosition.left }}
        >
          <div className="excel-filter-header">
            <input
              type="text"
              className="form-control form-control-sm"
              placeholder="Search values..."
              value={listQuery}
              onChange={(e) => setListQuery(e.target.value)}
            />
          </div>
          <div className="excel-filter-body">
            <div className="d-flex justify-content-between mb-2">
              <button
                type="button"
                className="btn btn-sm btn-outline-secondary"
                onClick={selectAllTemp}
              >
                Select All
              </button>
              <button
                type="button"
                className="btn btn-sm btn-outline-secondary"
                onClick={clearAllTemp}
              >
                Clear
              </button>
            </div>

            {filteredDropdownValues.length === 0 ? (
              <div className="text-muted small px-3 py-1">No values</div>
            ) : (
              filteredDropdownValues.map((val) => (
                <div key={val} className="form-check">
                  <input
                    className="form-check-input"
                    type="checkbox"
                    id={`chk-${dropdownColIdx}-${val}`}
                    checked={tempSelections.has(val)}
                    onChange={() => toggleTempSelection(val)}
                  />
                  <label
                    className="form-check-label"
                    htmlFor={`chk-${dropdownColIdx}-${val}`}
                    style={{ userSelect: 'none' }}
                  >
                    {val}
                  </label>
                </div>
              ))
            )}
          </div>
          <div className="excel-filter-footer">
            <button
              type="button"
              className="btn btn-sm btn-light"
              onClick={resetFilter}
            >
              Reset
            </button>
            <button
              type="button"
              className="btn btn-sm btn-primary"
              onClick={applyFilter}
            >
              Apply
            </button>
          </div>
        </div>
      )}

      {modalVisible && (
        <div
          className="modal fade show d-block"
          style={{ backgroundColor: '#00000066' }}
        >
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Comment for {currentAccountId}</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setModalVisible(false)}
                />
              </div>
              <div className="modal-body">
                <textarea
                  className="form-control"
                  rows="4"
                  value={commentInput}
                  onChange={(e) => setCommentInput(e.target.value)}
                />
              </div>
              <div className="modal-footer">
                <button className="btn btn-secondary" onClick={() => setModalVisible(false)}>
                  Cancel
                </button>
                <button className="btn btn-primary" onClick={saveComment} disabled={!commentInput.trim()}>
                  Save
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AccountLevel;
