import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

// The Breadcrumbs component provided by the user, with "Home" changed to "Dashboard"
const Breadcrumbs = ({ path }) => {
  const navigate = useNavigate();

  return (
    <nav aria-label="breadcrumb">
      <ol className="breadcrumb bg-white rounded shadow-sm p-3 mb-4">
        {/* Changed 'Home' to 'Dashboard' as requested */}
        <li className="breadcrumb-item">
          <button onClick={() => navigate('/')} className="btn btn-link text-decoration-none p-0">
            Dashboard
          </button>
        </li>
        {path.map((step, index) => (
          <li
            key={index}
            className={`breadcrumb-item ${index === path.length - 1 ? 'active' : ''}`}
            aria-current={index === path.length - 1 ? 'page' : undefined}
          >
            {index === path.length - 1 ? (
              step.name
            ) : (
              <button
                className="btn btn-link text-decoration-none p-0"
                onClick={() => navigate(`/${step.page}`)}
              >
                {step.name}
              </button>
            )}
          </li>
        ))}
      </ol>
    </nav>
  );
};

// This is a placeholder component for the "Account Level" page
const AccountLevelPage = ({ sbuTitle, onBack }) => (
  <div className="container d-flex justify-content-center align-items-center vh-100">
    <div className="card shadow-lg p-5 w-100" style={{ maxWidth: '40rem' }}>
      <button onClick={onBack} className="btn btn-link text-start text-dark mb-4">
        <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-chevron-left me-2">
            <polyline points="15 18 9 12 15 6"></polyline>
        </svg>
        Back to SBU List
      </button>
      <h1 className="display-5 fw-bold text-dark mb-4">
        Account Level for {sbuTitle}
      </h1>
      <p className="text-muted">
        This is a placeholder page. You would add the account-level details here, filtered by the selected SBU.
      </p>
    </div>
  </div>
);

// This is the main SBU selection component
const App = () => {
  const navigate = useNavigate();
  const location = useLocation();
  
  // State to manage the UI view
  const [view, setView] = useState('sbu-level');
  const [selectedSbuTitle, setSelectedSbuTitle] = useState('');

  // New states for fetching data
  const [sbus, setSbus] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Extract month and year from the location state passed from the dashboard
  const { month, year } = location.state || {};

  // Function to convert month number to month name
  const getMonthName = (monthNumber) => {
    const date = new Date();
    date.setMonth(monthNumber - 1);
    return date.toLocaleString('en-US', { month: 'long' }).toLowerCase();
  };

  useEffect(() => {
    // Only fetch data if month and year are available
    if (month && year) {
      const fetchSbuData = async () => {
        try {
          const payload = {
            month: month,
            year: year,
            monthNameParam: getMonthName(month)
          };
          
          const response = await fetch('/sbu', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(payload)
          });
          
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }

          const data = await response.json();
          setSbus(data);
        } catch (err) {
          setError(err.message);
          console.error("Error fetching SBU data:", err);
        } finally {
          setLoading(false);
        }
      };

      fetchSbuData();
    } else {
      // If month and year are missing, show an error and stop loading
      setError("Month and year data are missing. Please go back to the dashboard and select a month.");
      setLoading(false);
    }
  }, [month, year]); // Re-run effect if month or year changes

  const handleViewClick = (sbuTitle) => {
    setSelectedSbuTitle(sbuTitle);
    setView('account-level');
  };

  const handleBackClick = () => {
    setView('sbu-level');
    setSelectedSbuTitle('');
  };

  if (view === 'account-level') {
    return <AccountLevelPage sbuTitle={selectedSbuTitle} onBack={handleBackClick} />;
  }

  const breadcrumbPath = [
    { name: 'SBU Level', page: 'sbus' }
  ];

  return (
    <div className="container-fluid bg-light d-flex justify-content-center align-items-center min-vh-100 p-4">
      <div className="w-100" style={{ maxWidth: '1100px' }}>

        <Breadcrumbs path={breadcrumbPath} />
        
        <div className="card shadow-lg p-4 p-md-5 rounded-4">
          
          <button onClick={() => navigate('/')} className="btn btn-link text-start text-dark mb-4 p-0">
            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-chevron-left me-2">
                <polyline points="15 18 9 12 15 6"></polyline>
            </svg>
            Back to Dashboard
          </button>
          
          <h1 className="h2 fw-bold text-dark mb-4 text-center text-md-start">
            Select Strategic Business Unit (SBU) for {getMonthName(month)} {year}
          </h1>

          {/* Conditional rendering based on loading, error, and data state */}
          {loading && (
            <div className="text-center py-5">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Loading...</span>
              </div>
              <p className="mt-2 text-muted">Loading SBU data...</p>
            </div>
          )}

          {error && (
            <div className="alert alert-danger" role="alert">
              Error: {error}
            </div>
          )}

          {!loading && !error && (
            <div className="table-responsive">
              <table className="table table-hover table-borderless">
                <thead className="bg-light">
                  <tr>
                    <th scope="col" className="text-start text-uppercase text-muted fw-bold">
                      Strategic Business Unit (SBU)
                    </th>
                    <th scope="col" className="text-start text-uppercase text-muted fw-bold">
                      Revenue
                    </th>
                    <th scope="col" className="text-end text-uppercase text-muted fw-bold">
                      View
                    </th>
                  </tr>
                </thead>
                <tbody>
                  {sbus.map((sbu) => (
                    <tr key={sbu.id}>
                      <td className="align-middle text-primary fw-bold">
                        {sbu.title}
                      </td>
                      <td className="align-middle text-muted">
                        {sbu.revenue}
                      </td>
                      <td className="align-middle text-end">
                        <button
                          onClick={() => handleViewClick(sbu.title)}
                          className="btn btn-outline-primary rounded-pill p-2"
                          title="View Details"
                        >
                          {/* Eye icon SVG */}
                          <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-eye">
                              <path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z"></path>
                              <circle cx="12" cy="12" r="3"></circle>
                          </svg>
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
