import React, { useRef, useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import $ from 'jquery';
import 'datatables.net';
import 'datatables.net-dt/css/dataTables.dataTables.min.css';
import Breadcrumbs from '../Breadcrumbs';
import commonData from '../../data/commonData.json';
import * as XLSX from 'xlsx'; // Import XLSX for Excel export
import { saveAs } from 'file-saver'; // Import saveAs for triggering file download

// Utility to convert month number to month name
const getMonthName = (monthNumber) => {
  const date = new Date();
  date.setMonth(monthNumber - 1);
  return date.toLocaleString('en-US', { month: 'long' });
};

const AccountLevel = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const tableRef = useRef(null);
  const tableInstanceRef = useRef(null); // Ref to hold the DataTable instance

  // UI state
  const [accounts, setAccounts] = useState([]);
  const [commentsMap, setCommentsMap] = useState({});
  const [modalVisible, setModalVisible] = useState(false);
  const [currentAccountId, setCurrentAccountId] = useState(null);
  const [commentInput, setCommentInput] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Active filters
  const [activeMonth, setActiveMonth] = useState(null);
  const [activeYear, setActiveYear] = useState(null);
  const [activeMonthName, setActiveMonthName] = useState(null);
  const [activeSbu, setActiveSbu] = useState(null);

  const BACKEND_URL = 'http://localhost:8081';

  // Currency formatter
  const {
    locale,
    currency,
    minimumFractionDigits,
    maximumFractionDigits,
  } = commonData.currencySettings;

  const formatCurrency = (value = 0) =>
    value.toLocaleString(locale, {
      style: 'currency',
      currency,
      minimumFractionDigits,
      maximumFractionDigits,
    });

  /**
   * Handles the "Download Data" button click.
   * Generates an Excel (XLSX) file from the filtered 'accounts' data and triggers a download.
   */
  const handleDownloadData = () => {
    // Get the currently filtered data from DataTables
    const dataToExport = tableInstanceRef.current
      ? tableInstanceRef.current.rows({ search: 'applied' }).data().toArray()
      : accounts; // Fallback to all accounts if DataTable isn't initialized or no filter applied

    if (dataToExport.length === 0) {
      console.log('No data to download after applying filters.');
      // Optionally, show a user-friendly message here (e.g., a toast notification)
      return;
    }

    const headers = [
      'Account ID',
      'Account Name',
      'Total Projects',
      'Total Revenue',
      'Forecast Revenue',
      'Revenue Difference',
      'Comment',
    ];

    const dataForExcel = dataToExport.map((account) => ({
      'Account ID': account.accountId,
      'Account Name': account.accountName,
      'Total Projects': account.totalProjects,
      'Total Revenue': formatCurrency(account.totalRevenue),
      'Forecast Revenue': formatCurrency(account.forecastNetRevenue),
      'Revenue Difference': formatCurrency(account.difference),
      'Comment': account.accountComment || '',
    }));

    const wb = XLSX.utils.book_new();
    const ws = XLSX.utils.json_to_sheet(dataForExcel, { header: headers });

    XLSX.utils.book_append_sheet(wb, ws, 'Account Data');

    const excelBuffer = XLSX.write(wb, { bookType: 'xlsx', type: 'array' });
    const dataBlob = new Blob([excelBuffer], {
      type: 'application/octet-stream',
    });

    const monthName = getMonthName(activeMonth);
    const filename = `Account_Data_${activeSbu || 'All'}_${monthName || ''}_${activeYear || ''}.xlsx`;
    saveAs(dataBlob, filename);

    console.log('Downloading filtered account data in Excel format!');
  };


  // Load Poppins font and inject custom CSS for column filters
  useEffect(() => {
    const link = document.createElement('link');
    link.href =
      'https://fonts.googleapis.com/css2?family=Poppins:wght@400;600;700&display=swap';
    link.rel = 'stylesheet';
    document.head.appendChild(link);
    document.body.style.fontFamily = "'Poppins', sans-serif";

    // Inject CSS to style the column filter inputs
    const style = document.createElement('style');
    style.innerHTML = `
      .dataTables_filter { /* Hide default global search filter label */
        display: none;
      }
      .dataTables_filter input { /* Style for global search input */
        margin-left: 0.5em;
        padding: 0.5rem;
        border-radius: 0.375rem; /* rounded-md */
        border: 1px solid #d1d5db; /* border border-slate-300 */
        box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05); /* shadow-sm */
      }
      .dataTables_filter input:focus { /* Focus styles */
        border-color: #3b82f6; /* focus:border-blue-500 */
        outline: none;
        box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.25); /* focus:ring focus:ring-blue-200 focus:ring-opacity-50 */
      }
      .column-filter-input {
        width: 100%;
        padding: 0.25rem 0.5rem; /* py-1 px-2 */
        border-radius: 0.375rem; /* rounded-md */
        border: 1px solid #d1d5db; /* border border-slate-300 */
        box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05); /* shadow-sm */
        font-size: 0.875rem; /* text-sm */
        box-sizing: border-box; /* Include padding and border in the element's total width and height */
      }
      .column-filter-input:focus {
        border-color: #3b82f6;
        outline: none;
        box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.25);
      }
      /* Ensure the table header (thead) has enough height for the search inputs in tfoot if it were needed there.
         Since we're putting inputs in tfoot, this might not be strictly necessary for thead. */
      table.dataTable thead th {
        vertical-align: bottom; /* Align content to bottom if it's not already */
      }
      table.dataTable tfoot th {
        padding: 0.5rem 0.75rem; /* Adjust padding for filter cells */
      }
    `;
    document.head.appendChild(style);
  }, []);

  // Fetch accounts when filters change
  useEffect(() => {
    const fetchAccountData = async (month, year, monthNameParam, sbu) => {
      setLoading(true);
      setError(null);

      try {
        const payload = { month, year, monthNameParam, sbu };
        const resp = await fetch(`${BACKEND_URL}/api/account`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(payload),
        });
        if (!resp.ok) {
          const txt = await resp.text();
          throw new Error(txt || resp.statusText);
        }
        const data = await resp.json();
        const list = Array.isArray(data) ? data : [data].filter(Boolean);
        setAccounts(list);

        // seed comments map
        const initMap = {};
        list.forEach((acc) => {
          if (acc.accountComment) {
            initMap[acc.accountId] = acc.accountComment;
          }
        });
        setCommentsMap(initMap);
      } catch (e) {
        console.error(e);
        setError(`Failed to load accounts: ${e.message}`);
      } finally {
        setLoading(false);
      }
    };

    // Read from React state, sessionStorage, or URL query
    const qs = new URLSearchParams(location.search);
    let m = location.state?.month;
    let y = location.state?.year;
    let s = location.state?.sbu;

    if (!m) {
      const sm = sessionStorage.getItem('lastFetchedAccountMonth');
      const qm = qs.get('month');
      m = sm ?? qm;
    }
    if (!y) {
      const sy = sessionStorage.getItem('lastFetchedAccountYear');
      const qy = qs.get('year');
      y = sy ?? qy;
    }
    if (!s) {
      const ss = sessionStorage.getItem('lastFetchedAccountSbu');
      const qsS = qs.get('sbu');
      s = ss ?? qsS;
    }

    // Normalize numeric values
    m = m ? parseInt(m, 10) : null;
    y = y ? parseInt(y, 10) : null;

    if (m && y && s) {
      const mn = getMonthName(m);
      if (
        m !== activeMonth ||
        y !== activeYear ||
        s !== activeSbu ||
        accounts.length === 0
      ) {
        setActiveMonth(m);
        setActiveYear(y);
        setActiveMonthName(mn);
        setActiveSbu(s);

        sessionStorage.setItem('lastFetchedAccountMonth', m.toString());
        sessionStorage.setItem('lastFetchedAccountYear', y.toString());
        sessionStorage.setItem('lastFetchedAccountSbu', s);

        fetchAccountData(m, y, mn, s);
      }
    } else {
      setError(
        'Missing month, year or SBU. Please navigate here with those parameters.'
      );
      setLoading(false);
    }
  }, [location.state, location.search, activeMonth, activeYear, activeSbu, accounts.length]);

  // Initialize & destroy DataTable with standard sorting and searching
  useEffect(() => {
    if (!loading && accounts.length > 0 && tableRef.current) {
      const $tbl = $(tableRef.current);

      // Destroy existing DataTable instance if it exists
      if ($.fn.DataTable.isDataTable($tbl)) {
        tableInstanceRef.current.destroy();
      }

      // Initialize DataTable with standard options
      const instance = $tbl.DataTable({
        paging: true,
        searching: true, // Enable global search box
        ordering: true, // Enable default column sorting
        info: true,
        autoWidth: false,
        data: accounts, // Pass the accounts data
        columns: [
          { data: 'accountId', orderable: true },
          { data: 'accountName', orderable: true },
          { data: 'totalProjects', orderable: true },
          { data: 'totalRevenue', orderable: true, render: (data) => formatCurrency(data) },
          { data: 'forecastNetRevenue', orderable: true, render: (data) => formatCurrency(data) },
          { data: 'difference', orderable: true, render: (data) => formatCurrency(data) },
          {
            data: 'accountComment',
            orderable: false, // Comment column not sortable
            className: 'text-center',
            render: function (data, type, row) {
              return `
                <button class="btn btn-sm btn-outline-secondary comment-btn" data-account-id="${row.accountId}">
                  üí¨
                </button>
              `;
            }
          },
          {
            data: null,
            orderable: false, // View button column not sortable
            className: 'text-center',
            render: function (data, type, row) {
              return `
                <button class="btn btn-sm btn-outline-primary rounded-circle view-btn" data-account-id="${row.accountId}">
                  üîç
                </button>
              `;
            }
          },
        ],
        order: [[0, 'asc']], // Default sort by Account ID (first column) ascending
        initComplete: function () {
            // Setup individual column filters in the footer
            this.api().columns().every(function () {
                const column = this;
                // Get the header text to decide where to add the filter input
                const headerText = $(column.header()).text().trim();

                // Only add search input to specific data columns, exclude "Comment" and "View"
                if (['Account ID', 'Account Name', 'Total Projects', 'Total Revenue', 'Forecast Revenue', 'Revenue Difference'].includes(headerText)) {
                    const input = $('<input type="text" placeholder="Search '+headerText+'" class="column-filter-input" />')
                        .appendTo($(column.footer()).empty()) // Append to the footer cell for this column
                        .on('keyup change clear', function () {
                            if (column.search() !== this.value) {
                                column.search(this.value).draw();
                            }
                        });
                } else {
                    // For "Comment" and "View" columns, just keep the footer cell empty
                    $(column.footer()).empty();
                }
            });

            // IMPORTANT: Move the global search input from its default position to above the table
            // This is DataTables specific. By default, it creates a div with class 'dataTables_filter'.
            // We are moving it to a custom div which is expected to be placed by React in the main render.
            // First, find the default DataTables search input and its label
            const defaultFilterDiv = $('.dataTables_filter');
            const defaultSearchInput = defaultFilterDiv.find('input');

            // Find your custom wrapper for the global search input
            const globalSearchWrapper = $('#global-search-wrapper');

            // Move the input and its label (if it exists) to your custom wrapper
            if (globalSearchWrapper.length && defaultSearchInput.length) {
                // Create a label for the moved input
                const label = $('<label>Search:</label>').css('margin-right', '0.5em');
                globalSearchWrapper.empty().append(label, defaultSearchInput);
                defaultFilterDiv.remove(); // Remove the original DataTables filter div
            }
        }
      });

      tableInstanceRef.current = instance;

      // Re-attach event listeners for dynamically rendered buttons (comments and view)
      $tbl.off('click', '.comment-btn').on('click', '.comment-btn', function() {
        const accId = $(this).data('account-id');
        openCommentModal(accId);
      });

      $tbl.off('click', '.view-btn').on('click', '.view-btn', function() {
        const accId = $(this).data('account-id');
        navigate(`/accounts/${accId}/project-types`, {
          state: {
            accId: accId,
            month: activeMonth,
            year: activeYear,
            monthName: activeMonthName,
            sbu: activeSbu,
          },
        });
      });
    }

    // Cleanup function to destroy DataTable instance and event listeners
    return () => {
      if (tableInstanceRef.current) {
        tableInstanceRef.current.destroy();
        tableInstanceRef.current = null;
      }
      $(tableRef.current).off('click', '.comment-btn');
      $(tableRef.current).off('click', '.view-btn');
      // Re-append the default global filter div back to body if it was moved, for full cleanup.
      // This helps prevent issues if the component mounts/unmounts multiple times quickly.
      if ($('#global-search-wrapper').find('input').length && $('.dataTables_filter').length === 0) {
        $('body').append($('<div class="dataTables_filter">').append($('#global-search-wrapper').contents()));
      }
    };
  }, [loading, accounts, activeMonth, activeYear, activeMonthName, activeSbu, navigate]);

  // Open comment modal
  const openCommentModal = (accId) => {
    setCurrentAccountId(accId);
    setCommentInput(commentsMap[accId] || '');
    setModalVisible(true);
  };

  // Save or update comment
  const saveComment = async () => {
    try {
      const dto = {
        accId: currentAccountId,
        month: activeMonth,
        year: activeYear,
        comment: commentInput,
      };
      const resp = await fetch(`${BACKEND_URL}/api/account/comment`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(dto),
      });
      if (!resp.ok) {
        throw new Error(await resp.text());
      }
      // update local maps
      setCommentsMap((m) => ({ ...m, [currentAccountId]: commentInput }));
      setAccounts((list) =>
        list.map((acc) =>
          acc.accountId === currentAccountId
            ? { ...acc, accountComment: commentInput }
            : acc
        )
      );
      setModalVisible(false);
    } catch (e) {
      window.alert('Error saving comment: ' + e.message); // Using window.alert for simplicity
    }
  };

  // Dynamic breadcrumb path
  const breadcrumbPath = [
    { name: 'PMO Dashboard', page: '' }, // Always starts with Dashboard
    { name: 'Revenue Forecast - Early View', page: 'upload' }, // Added this line
    ...(activeSbu ? [{ name: `${activeSbu} SBU Level`, page: 'sbu' }] : []), // Conditionally add SBU Level
    { name: 'Account Level', page: 'accounts' },
  ].filter(Boolean);

  return (
    <div
      className="min-vh-100 d-flex flex-column align-items-center px-3 py-4"
      style={{
        background: 'linear-gradient(to bottom right, #f5f7fa, #e9ecef)',
        fontFamily: "'Poppins', sans-serif",
      }}
    >
      <div
        className="card shadow p-4 p-md-5 w-100"
        style={{
          maxWidth: '1100px',
          backgroundColor: '#ffffffdd',
          borderRadius: '1rem',
        }}
      >
        <Breadcrumbs path={breadcrumbPath} />

        <div className="d-flex flex-column flex-md-row justify-content-between align-items-md-center mb-3">
          <h2 className="mb-3 mb-md-0 fw-semibold text-dark">
            üè¢ Account Level Overview ‚Äì {activeSbu}
            {activeMonthName && activeYear && (
              <span className="ms-2 text-primary">
                ({activeMonthName} {activeYear})
              </span>
            )}
          </h2>
          {/* Download button, visible only if accounts are loaded */}
          {accounts.length > 0 && (
            <button
              className="btn btn-outline-success rounded-pill px-3"
              onClick={handleDownloadData}
              title="Download Account Data"
            >
              <i className="bi bi-download me-2"></i> Download Data
            </button>
          )}
        </div>

        {/* Global search input moved here for better layout */}
        {/* It will be populated by DataTables' initComplete */}
        <div id="global-search-wrapper" className="mb-3 d-flex align-items-center justify-content-end">
            {/* DataTables will inject its search input here */}
        </div>


        {loading ? (
          <div className="d-flex justify-content-center align-items-center">
            <div className="spinner-border text-primary" role="status" />
            <p className="ms-3 text-primary">Loading Account Data...</p>
          </div>
        ) : error ? (
          <div className="d-flex flex-column justify-content-center align-items-center text-danger">
            <p className="fs-4">Error: {error}</p>
            <button
              className="btn btn-primary mt-3"
              onClick={() => navigate('/upload')}
            >
              Go to Upload Page
            </button>
          </div>
        ) : (
          <div className="table-responsive rounded">
            <table
              ref={tableRef}
              className="display table table-hover align-middle table-bordered mb-0"
              style={{ width: '100%' }}
            >
              <thead
                className="text-white"
                style={{
                  background: 'linear-gradient(to right, #1d4ed8, #2563eb)',
                }}
              >
                <tr>
                  <th>Account ID</th>
                  <th>Account Name</th>
                  <th>Total Projects</th>
                  <th>Total Revenue</th>
                  <th>Forecast Revenue</th>
                  <th>Revenue Difference</th>
                  <th className="text-center">Comment</th>
                  <th className="text-center">View</th>
                </tr>
              </thead>
              {/* TFOOT for column filters */}
              <tfoot>
                <tr>
                  <th></th> {/* Account ID Filter */}
                  <th></th> {/* Account Name Filter */}
                  <th></th> {/* Total Projects Filter */}
                  <th></th> {/* Total Revenue Filter */}
                  <th></th> {/* Forecast Revenue Filter */}
                  <th></th> {/* Revenue Difference Filter */}
                  <th></th> {/* Comment (no filter) */}
                  <th></th> {/* View (no filter) */}
                </tr>
              </tfoot>
              <tbody>
                {/* DataTables will handle rendering tbody. */}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {modalVisible && (
        <div
          className="modal fade show d-block"
          style={{ backgroundColor: '#00000066' }}
        >
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Comment for {currentAccountId}</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setModalVisible(false)}
                />
              </div>
              <div className="modal-body">
                <textarea
                  className="form-control"
                  rows="4"
                  value={commentInput}
                  onChange={(e) => setCommentInput(e.target.value)}
                />
              </div>
              <div className="modal-footer">
                <button className="btn btn-secondary" onClick={() => setModalVisible(false)}>
                  Cancel
                </button>
                <button className="btn btn-primary" onClick={saveComment} disabled={!commentInput.trim()}>
                  Save
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AccountLevel;
