import React, { useState } from "react";
import axios from "axios";
import {
  Container,
  Row,
  Col,
  Form,
  Button,
  Table,
  Alert,
  Spinner,
} from "react-bootstrap";

const SbuSummary = () => {
  // Form state
  const [month, setMonth] = useState("");
  const [year, setYear] = useState("");
  const [monthName, setMonthName] = useState("");

  // Data state
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Handle form submission
  const fetchSbu = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setData([]);

    try {
      const payload = {
        month: parseInt(month, 10),
        year: parseInt(year, 10),
        monthNameParam: monthName.toLowerCase(),
      };

      const resp = await axios.post(
        "http://localhost:8081/api/sbu",
        payload
      );

      setData(resp.data);
    } catch (err) {
      setError(
        err.response?.data || err.message || "Unexpected error occurred"
      );
    } finally {
      setLoading(false);
    }
  };

  // Dynamically render table headers from first object’s keys
  const renderTableHeader = () => {
    if (!data.length) return null;
    return (
      <tr>
        {Object.keys(data[0]).map((key) => (
          <th key={key}>{key}</th>
        ))}
      </tr>
    );
  };

  // Render table rows
  const renderTableBody = () =>
    data.map((row, idx) => (
      <tr key={idx}>
        {Object.values(row).map((val, cidx) => (
          <td key={cidx}>{val}</td>
        ))}
      </tr>
    ));

  return (
    <Container className="my-4">
      <h2>SBU Revenue Summary</h2>
      <Form onSubmit={fetchSbu} className="mb-4">
        <Row>
          <Col md={3}>
            <Form.Group controlId="formMonth">
              <Form.Label>Month (1–12)</Form.Label>
              <Form.Control
                type="number"
                min="1"
                max="12"
                value={month}
                onChange={(e) => setMonth(e.target.value)}
                required
              />
            </Form.Group>
          </Col>
          <Col md={3}>
            <Form.Group controlId="formYear">
              <Form.Label>Year</Form.Label>
              <Form.Control
                type="number"
                min="2000"
                max="2100"
                value={year}
                onChange={(e) => setYear(e.target.value)}
                required
              />
            </Form.Group>
          </Col>
          <Col md={4}>
            <Form.Group controlId="formMonthName">
              <Form.Label>Month Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="e.g. May"
                value={monthName}
                onChange={(e) => setMonthName(e.target.value)}
                required
              />
            </Form.Group>
          </Col>
          <Col md={2} className="d-flex align-items-end">
            <Button type="submit" disabled={loading} block>
              {loading ? (
                <>
                  <Spinner
                    as="span"
                    animation="border"
                    size="sm"
                    role="status"
                    aria-hidden="true"
                  />{" "}
                  Loading…
                </>
              ) : (
                "Fetch"
              )}
            </Button>
          </Col>
        </Row>
      </Form>

      {error && <Alert variant="danger">{error}</Alert>}

      {data.length > 0 && (
        <Table striped bordered hover responsive>
          <thead>{renderTableHeader()}</thead>
          <tbody>{renderTableBody()}</tbody>
        </Table>
      )}
    </Container>
  );
};

export default SbuSummary;
