import React, { useRef, useEffect, useState } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import $ from 'jquery';
import 'datatables.net';
import 'datatables.net-dt/css/dataTables.dataTables.min.css';
import Breadcrumbs from '../Breadcrumbs'; // Assuming this path is correct
import commonData from '../../data/commonData.json'; // Assuming this path is correct

// Utility to convert month number to month name
const getMonthName = (monthNumber) => {
  const date = new Date();
  date.setMonth(monthNumber - 1);
  return date.toLocaleString('en-US', { month: 'long' });
};

// Filterable columns for Project Level (indices: Project ID, Project Name)
const FILTERABLE_COL_INDICES = [0, 1]; // Project ID (0), Project Name (1)

const ProjectLevel = () => {
  const navigate = useNavigate();
  const { accountId: urlAccountId } = useParams(); // accountId from URL path
  const location = useLocation();
  const tableRef = useRef(null);
  const tableInstanceRef = useRef(null); // Ref to hold the DataTable instance
  const dropdownRef = useRef(null); // Ref for the filter dropdown

  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeMonth, setActiveMonth] = useState(null);
  const [activeYear, setActiveYear] = useState(null);
  const [activeAccountId, setActiveAccountId] = useState(null);
  const [activeAccountName, setActiveAccountName] = useState('Loading...');
  const [activeSbu, setActiveSbu] = useState(null);
  const [activeProjectType, setActiveProjectType] = useState(null);

  // Dropdown filter state
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [dropdownColIdx, setDropdownColIdx] = useState(null);
  const [dropdownPosition, setDropdownPosition] = useState({ top: 0, left: 0 });
  const [dropdownValues, setDropdownValues] = useState([]); // Unique values for the current dropdown
  const [tempSelections, setTempSelections] = useState(new Set()); // Selections in the open dropdown
  const [listQuery, setListQuery] = useState(''); // Search query for values within the dropdown
  const [columnSelections, setColumnSelections] = useState({}); // { [colIdx]: Set<string> } - Applied filters

  // Backend URL for API calls
  const BACKEND_URL = 'http://localhost:8081';

  // Currency formatting function
  const formatCurrency = (value = 0) =>
    value.toLocaleString(commonData.currencySettings.locale, {
      style: 'currency',
      currency: commonData.currencySettings.currency,
      minimumFractionDigits: commonData.currencySettings.minimumFractionDigits,
      maximumFractionDigits: commonData.currencySettings.maximumFractionDigits
    });

  // Utility to get month name (for file naming)
  const getMonthName = (monthNumber) => {
    if (!monthNumber) return '';
    const date = new Date();
    date.setMonth(monthNumber - 1);
    return date.toLocaleString('en-US', { month: 'long' });
  };

  /**
   * Handles the "Download Data" button click.
   * Generates a CSV file from the 'projects' data and triggers a download.
   */
  const handleDownloadData = () => {
    if (projects.length === 0) {
      console.log('No data to download.');
      return;
    }

    // Define CSV headers matching the table columns
    const headers = [
      "Project ID", "Project Name", "Total Associates", "Company Hours",
      "Client Hours", "Variance Hours", "Revenue"
    ];

    // Map project data to CSV rows
    const csvRows = projects.map(project => {
      return [
        `"${project.projectId}"`, // Enclose in quotes to handle commas if any
        `"${project.projectName}"`,
        project.totalAssociatesCount,
        project.totalCompanyHours,
        project.totalClientHours,
        project.varianceHours,
        `"${formatCurrency(project.revenue)}"` // Format currency and enclose in quotes
      ].join(','); // Join values with a comma
    });

    // Combine headers and rows
    const csvContent = [
      headers.join(','), // Join headers with a comma
      ...csvRows
    ].join('\n'); // Join rows with a newline character

    // Create a Blob from the CSV content
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });

    // Create a download link and trigger the download
    const link = document.createElement('a');
    const url = URL.createObjectURL(blob);

    // Generate a dynamic filename
    const monthName = getMonthName(activeMonth);
    const fileName = `Projects_Account_${activeAccountId}_${monthName}_${activeYear}.csv`;

    link.setAttribute('href', url);
    link.setAttribute('download', fileName);
    link.style.visibility = 'hidden'; // Hide the link
    document.body.appendChild(link); // Append to body
    link.click(); // Programmatically click the link to trigger download
    document.body.removeChild(link); // Clean up
    URL.revokeObjectURL(url); // Release the object URL
  };

  // Load Poppins font from Google Fonts + inject custom CSS for filters
  useEffect(() => {
    const link = document.createElement('link');
    link.href = 'https://fonts.googleapis.com/css2?family=Poppins:wght@400;600;700&display=swap';
    link.rel = 'stylesheet';
    document.head.appendChild(link);
    document.body.style.fontFamily = "'Poppins', sans-serif";

    // Inject CSS to hide DataTables sorting arrows and style custom filters
    const style = document.createElement('style');
    style.innerHTML = `
      table.dataTable thead .sorting,
      table.dataTable thead .sorting_asc,
      table.dataTable thead .sorting_desc,
      table.dataTable thead .sorting_asc_disabled,
      table.dataTable thead .sorting_desc_disabled {
        background-image: none !important;
        cursor: default !important;
      }
      .filter-icon-btn {
        border: none;
        background: transparent;
        padding: 2px;
        margin-left: 8px;
        cursor: pointer;
        display: inline-flex;
        align-items: center;
        vertical-align: middle; /* Align icon vertically with text */
      }
      .filter-icon-btn svg { fill: #6b7280; } /* Default icon color */
      .filter-icon-btn.active svg { fill: #1d4ed8; } /* Active filter icon color */
      .excel-filter-dropdown {
        position: absolute;
        z-index: 2000;
        width: 260px;
        background: #fff;
        border: 1px solid #e5e7eb;
        border-radius: 8px;
        box-shadow: 0 10px 25px rgba(0,0,0,0.1);
        font-family: 'Poppins', sans-serif; /* Ensure dropdown uses Poppins */
      }
      .excel-filter-header {
        padding: 10px 12px;
        border-bottom: 1px solid #f1f5f9;
        background: #f8fafc;
        border-top-left-radius: 8px;
        border-top-right-radius: 8px;
      }
      .excel-filter-body {
        max-height: 240px;
        overflow-y: auto; /* Enable vertical scrolling */
        padding: 8px 12px;
      }
      .excel-filter-footer {
        display: flex;
        justify-content: flex-end;
        gap: 8px;
        padding: 10px 12px;
        border-top: 1px solid #f1f5f9;
      }
      .form-check-label {
        margin-bottom: 0; /* Remove default margin for checkboxes */
        cursor: pointer;
      }
    `;
    document.head.appendChild(style);
  }, []);

  // Fetch project data when context (month, year, accountId, projectType) changes
  useEffect(() => {
    const fetchProjectData = async (month, year, accId, projectType) => {
      setLoading(true);
      setError(null);

      try {
        const payload = { month, year, accId, projectType };
        const resp = await fetch(`${BACKEND_URL}/api/project`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(payload)
        });

        if (!resp.ok) {
          const errText = await resp.text();
          throw new Error(`status: ${resp.status}, message: ${errText || resp.statusText}`);
        }

        const data = await resp.json();
        const list = Array.isArray(data) ? data : [data].filter(Boolean);
        setProjects(list);

        // Derive accountName from response or sessionStorage
        if (list.length > 0 && list[0].accountName) {
          setActiveAccountName(list[0].accountName);
        } else {
          const storedName = sessionStorage.getItem('lastFetchedProjectAccountName');
          setActiveAccountName(storedName || accId); // Fallback to accountId if name not found
        }
      } catch (e) {
        console.error('Error fetching project data:', e);
        setError(`Failed to load project data: ${e.message}`);
      } finally {
        setLoading(false);
      }
    };

    // Determine context from location.state, URL params, or sessionStorage
    let monthToUse = location.state?.month;
    let yearToUse = location.state?.year;
    let accountIdToUse = urlAccountId; // Always from URL params for this component
    let accountNameToUse = location.state?.accountName;
    let sbuToUse = location.state?.sbu;
    let projectTypeToUse = location.state?.projectType;

    // Prioritize sessionStorage for all parameters if not in location.state
    // This makes the context retrieval more robust
    if (!monthToUse) monthToUse = sessionStorage.getItem('lastFetchedProjectMonth');
    if (!yearToUse) yearToUse = sessionStorage.getItem('lastFetchedProjectYear');
    // accountIdToUse is primarily from useParams (urlAccountId)
    if (!accountNameToUse) accountNameToUse = sessionStorage.getItem('lastFetchedProjectAccountName');
    if (!sbuToUse) sbuToUse = sessionStorage.getItem('lastFetchedProjectSbu');
    if (!projectTypeToUse) projectTypeToUse = sessionStorage.getItem('lastFetchedProjectType');

    // Convert to number
    monthToUse = monthToUse ? parseInt(monthToUse, 10) : null;
    yearToUse = yearToUse ? parseInt(yearToUse, 10) : null;

    // Only fetch data if all necessary context is available and it's a new context
    if (monthToUse && yearToUse && accountIdToUse) {
      if (
        monthToUse !== activeMonth ||
        yearToUse !== activeYear ||
        accountIdToUse !== activeAccountId ||
        sbuToUse !== activeSbu ||
        projectTypeToUse !== activeProjectType ||
        projects.length === 0 // Re-fetch if projects array is empty (e.g., first load)
      ) {
        setActiveMonth(monthToUse);
        setActiveYear(yearToUse);
        setActiveAccountId(accountIdToUse);
        if (accountNameToUse) {
          setActiveAccountName(accountNameToUse);
        }
        setActiveSbu(sbuToUse);
        setActiveProjectType(projectTypeToUse);

        // Persist context to sessionStorage for future loads
        sessionStorage.setItem('lastFetchedProjectMonth', monthToUse.toString());
        sessionStorage.setItem('lastFetchedProjectYear', yearToUse.toString());
        sessionStorage.setItem('lastFetchedProjectAccountId', accountIdToUse);
        if (accountNameToUse) {
          sessionStorage.setItem('lastFetchedProjectAccountName', accountNameToUse);
        }
        if (sbuToUse) {
          sessionStorage.setItem('lastFetchedProjectSbu', sbuToUse);
        }
        if (projectTypeToUse) {
          sessionStorage.setItem('lastFetchedProjectType', projectTypeToUse);
        }

        fetchProjectData(monthToUse, yearToUse, accountIdToUse, projectTypeToUse);
      } else {
        setLoading(false);
      }
    } else {
      setError('Missing month, year, or Account ID. Please navigate here with all parameters.');
      setLoading(false);
    }
  }, [
    location.state,
    urlAccountId,
    activeMonth,
    activeYear,
    activeAccountId,
    activeSbu,
    activeProjectType,
    projects.length
  ]);

  // Initialize and destroy DataTable when projects data changes or component unmounts
  useEffect(() => {
    if (!loading && projects.length > 0 && tableRef.current) {
      const $tbl = $(tableRef.current);
      if ($.fn.DataTable.isDataTable($tbl)) {
        tableInstanceRef.current.destroy();
      }

      const instance = $tbl.DataTable({
        paging: true,
        searching: true,
        ordering: false, // Disable all default sorting arrows
        info: true,
        autoWidth: false,
        data: projects, // Pass the projects data to DataTable
        columns: [
          { data: 'projectId', orderable: false }, // Disable sorting for Project ID
          { data: 'projectName', orderable: false }, // Disable sorting for Project Name
          { data: 'totalAssociatesCount', orderable: false },
          { data: 'totalCompanyHours', orderable: false },
          { data: 'totalClientHours', orderable: false },
          { data: 'varianceHours', orderable: false },
          { data: 'revenue', orderable: false, render: (data) => formatCurrency(data) },
          {
            data: null,
            orderable: false,
            className: 'text-center',
            render: function (data, type, row) {
              return `
                <button
                  class="btn btn-sm btn-outline-primary rounded-circle view-associates-btn"
                  data-project-id="${row.projectId}"
                  data-project-name="${row.projectName}"
                  data-account-id="${row.accountId}"
                  data-account-name="${row.accountName}"
                >
                  üîç
                </button>
              `;
            }
          }
        ],
        order: [], // Ensure no default ordering is set
      });

      tableInstanceRef.current = instance;
      // Apply any previously selected filters after DataTables is initialized
      applyDataTablesFilters(instance);

      // Re-attach event listeners for dynamically rendered buttons
      $tbl.off('click', '.view-associates-btn').on('click', '.view-associates-btn', function() {
        const projectId = $(this).data('project-id');
        const projectName = $(this).data('project-name');
        const accountId = $(this).data('account-id');
        const accountName = $(this).data('account-name');

        navigate(
          `/projects/${projectId}/associates`,
          {
            state: {
              projectId: projectId,
              projectName: projectName,
              accountId: accountId,
              accountName: accountName,
              month: activeMonth,
              year: activeYear,
              sbu: activeSbu,
              projectType: activeProjectType
            }
          }
        );
      });
    }

    return () => {
      if (tableInstanceRef.current) {
        tableInstanceRef.current.destroy();
        tableInstanceRef.current = null;
      }
      $(tableRef.current).off('click', '.view-associates-btn');
    };
  }, [loading, projects, activeMonth, activeYear, activeAccountId, activeSbu, activeProjectType, navigate]);


  // Helper: Get cell's displayed value based on column index for filtering
  const getCellDisplayValue = (project, colIdx) => {
    switch (colIdx) {
      case 0: return String(project.projectId ?? '');
      case 1: return String(project.projectName ?? '');
      case 2: return String(project.totalAssociatesCount ?? '');
      case 3: return String(project.totalCompanyHours ?? '');
      case 4: return String(project.totalClientHours ?? '');
      case 5: return String(project.varianceHours ?? '');
      case 6: return formatCurrency(project.revenue ?? 0);
      default: return '';
    }
  };

  // Get unique values for a specific column for the dropdown filter
  const getUniqueValuesForColumn = (colIdx) => {
    const set = new Set();
    projects.forEach((project) => {
      const displayValue = getCellDisplayValue(project, colIdx);
      set.add(displayValue);
      // console.log(`[DEBUG] getUniqueValuesForColumn: Project ID: ${project.projectId}, ColIdx: ${colIdx}, DisplayValue: ${displayValue}`);
    });
    const uniqueVals = Array.from(set).sort((a, b) =>
      String(a).localeCompare(String(b), undefined, { numeric: true })
    );
    // console.log(`[DEBUG] Unique values for column ${colIdx}:`, uniqueVals);
    return uniqueVals;
  };

  // Check if a column has an active filter applied (some but not all values selected)
  const isColumnFiltered = (colIdx) => {
    if (!FILTERABLE_COL_INDICES.includes(colIdx)) return false;
    const appliedSet = columnSelections[colIdx];
    if (!appliedSet) return false; // No filter applied
    const uniqueVals = getUniqueValuesForColumn(colIdx);
    // Filter is active if some values are selected but not all
    return appliedSet.size > 0 && appliedSet.size < uniqueVals.length;
  };

  // Open dropdown for a column
  const openFilterDropdown = (event, colIdx) => {
    if (!FILTERABLE_COL_INDICES.includes(colIdx)) return;

    const rect = event.currentTarget.getBoundingClientRect(); // Get position of the clicked header button
    const uniqueValues = getUniqueValuesForColumn(colIdx);
    // console.log(`[DEBUG] openFilterDropdown: Unique values for col ${colIdx} (pre-filter):`, uniqueValues);

    // Initialize tempSelections based on current applied filter or all unique values
    const appliedSet = columnSelections[colIdx];
    const initialSet =
      appliedSet && appliedSet.size > 0 ? new Set(appliedSet) : new Set(uniqueValues);

    setDropdownValues(uniqueValues);
    setTempSelections(initialSet);
    setListQuery(''); // Clear dropdown search query
    setDropdownColIdx(colIdx);
    setDropdownPosition({
      top: rect.bottom + window.scrollY + 4, // Position below the clicked header
      left: rect.left + window.scrollX,
    });
    setDropdownOpen(true);
  };

  // Dropdown interactions
  const toggleTempSelection = (value) => {
    const next = new Set(tempSelections);
    if (next.has(value)) next.delete(value);
    else next.add(value);
    setTempSelections(next);
  };
  const selectAllTemp = () => setTempSelections(new Set(dropdownValues));
  const clearAllTemp = () => setTempSelections(new Set());

  // Apply filter to DataTable
  const applyFilter = () => {
    if (dropdownColIdx == null || !tableInstanceRef.current) return;

    // Update the columnSelections state with the new temp selections
    setColumnSelections((prev) => ({
      ...prev,
      [dropdownColIdx]: new Set(tempSelections),
    }));

    // Re-apply all filters via DataTables
    applyDataTablesFilters(tableInstanceRef.current, {
      overrideColIdx: dropdownColIdx,
      overrideSet: new Set(tempSelections),
    });

    setDropdownOpen(false); // Close dropdown
  };

  // Reset filter for the current column
  const resetFilter = () => {
    if (dropdownColIdx == null || !tableInstanceRef.current) return;

    // Remove the filter for this column from state
    setColumnSelections((prev) => {
      const next = { ...prev };
      delete next[dropdownColIdx];
      return next;
    });

    // Clear the filter for this column in DataTables and redraw
    tableInstanceRef.current.column(dropdownColIdx).search('').draw();

    setDropdownOpen(false); // Close dropdown
  };

  // Apply all active column filters to DataTables instance
  const applyDataTablesFilters = (instance, options = {}) => {
    FILTERABLE_COL_INDICES.forEach((colIdx) => {
      const uniqueValues = getUniqueValuesForColumn(colIdx);
      const appliedSet =
        options.overrideColIdx === colIdx
          ? options.overrideSet // Use override for the currently changed column
          : columnSelections[colIdx]; // Otherwise, use the stored selection

      // If no selections, all selected, or the column is not filterable by this mechanism, clear its filter
      if (!appliedSet || appliedSet.size === 0 || appliedSet.size === uniqueValues.length) {
        instance.column(colIdx).search('', false, false, true); // Clear filter
      } else {
        // Build a regex to match any of the selected values
        const escaped = Array.from(appliedSet).map((v) =>
          $.fn.dataTable.util.escapeRegex(String(v))
        );
        const regex = `^(${escaped.join('|')})$`; // Match exactly one of the selected values
        instance.column(colIdx).search(regex, true, false, true); // Apply regex filter
      }
    });

    instance.draw(); // Redraw the table to apply filters
  };

  // Close dropdown on outside click or Esc key press
  useEffect(() => {
    if (!dropdownOpen) return;
    const onDocClick = (e) => {
      // Check if click is inside the dropdown or the filter icon button
      if (dropdownRef.current && dropdownRef.current.contains(e.target)) return;
      if ($(e.target).closest('.filter-icon-btn').length) return;
      setDropdownOpen(false);
    };
    const onKeyDown = (e) => {
      if (e.key === 'Escape') setDropdownOpen(false);
    };
    document.addEventListener('mousedown', onDocClick);
    document.addEventListener('keydown', onKeyDown);
    return () => {
      document.removeEventListener('mousedown', onDocClick);
      document.removeEventListener('keydown', onKeyDown);
    };
  }, [dropdownOpen]);

  // Define breadcrumb path for navigation
  const breadcrumbPath = [
    { name: 'PMO Dashboard', page: '' },
    { name: 'Revenue Forecast - Early View', page: 'upload' },
    // Conditionally add SBU Level
    ...(activeSbu ? [{ name: `${activeSbu} SBU Level`, page: 'sbu', state: { month: activeMonth, year: activeYear, sbu: activeSbu } }] : []),
    // Account Level breadcrumb
    { name: 'Account Level', page: `accounts`, state: { month: activeMonth, year: activeYear, sbu: activeSbu } },
    // Conditionally add Project Type breadcrumb (name changed to "Project Type")
    ...(activeProjectType ? [{ name: `${activeProjectType} Project Type`, page: `accounts/${activeAccountId}/project-types`, state: { month: activeMonth, year: activeYear, sbu: activeSbu, accId: activeAccountId } }] : []),
    {
      name: `Projects (${activeAccountName})`,
      page: `accounts/${activeAccountId}/projects`
    }
  ].filter(Boolean);

  // Hamburger icon component
  const HamburgerIcon = ({ active }) => (
    <svg width="14" height="14" viewBox="0 0 24 24" aria-hidden="true" style={{ verticalAlign: 'middle', fill: active ? '#1d4ed8' : '#6b7280' }}>
      <path d="M3 6h18v2H3zM3 11h18v2H3zM3 16h18v2H3z" />
    </svg>
  );

  // Filter dropdown filtered list (based on internal search query)
  const filteredDropdownValues = dropdownValues.filter((v) =>
    String(v).toLowerCase().includes(listQuery.toLowerCase())
  );

  // Render loading state
  if (loading) {
    return (
      <div className="min-vh-100 d-flex justify-content-center align-items-center">
        <div className="spinner-border text-primary" role="status" />
        <p className="ms-3 text-primary">Loading Project Data...</p>
      </div>
    );
  }

  // Render error state
  if (error) {
    return (
      <div className="min-vh-100 d-flex flex-column justify-content-center align-items-center text-danger">
        <p className="fs-4">Error: {error}</p>
        <button
          className="btn btn-primary mt-3"
          onClick={() => navigate('/accounts', { state: { month: activeMonth, year: activeYear, sbu: activeSbu } })}
        >
          Go to Account Level
        </button>
      </div>
    );
  }

  // Main component render
  return (
    <div
      className="min-vh-100 d-flex flex-column align-items-center px-3 py-4"
      style={{
        background: 'linear-gradient(to bottom right, #f5f7fa, #e9ecef)',
        fontFamily: "'Poppins', sans-serif"
      }}
    >
      <div
        className="card shadow p-4 p-md-5 w-100"
        style={{
          maxWidth: '1200px',
          backgroundColor: '#ffffffdd',
          borderRadius: '1rem'
        }}
      >
        {/* Breadcrumbs component */}
        <Breadcrumbs path={breadcrumbPath} />

        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2 className="mb-0 fw-semibold text-dark">
            üìÅ Project Level Overview{' '}
            {activeAccountId ? `for ${activeAccountName}` : ''}
          </h2>
          {/* Download button, visible only if projects are loaded */}
          {projects.length > 0 && (
            <button
              className="btn btn-outline-success rounded-pill px-3"
              onClick={handleDownloadData}
              title="Download Project Data"
            >
              <i className="bi bi-download me-2"></i> Download Data
            </button>
          )}
        </div>

        {/* Responsive table container */}
        <div className="table-responsive rounded">
          <table
            ref={tableRef}
            className="display table table-hover align-middle table-borderless mb-0"
            style={{ width: '100%' }}
          >
            <thead
              className="text-white"
              style={{ background: 'linear-gradient(to right, #3b82f6, #2563eb)' }}
            >
              <tr>
                {[
                  'Project ID',
                  'Project Name',
                  'Total Associates',
                  'Company Hours',
                  'Client Hours',
                  'Variance Hours',
                  'Revenue',
                  'View'
                ].map((title, idx) => (
                  <th key={idx}>
                    <div className="d-flex justify-content-between align-items-center">
                      <span>{title}</span>
                      {FILTERABLE_COL_INDICES.includes(idx) && (
                        <button
                          type="button"
                          className={
                            'filter-icon-btn ' +
                            (isColumnFiltered(idx) ? 'active' : '')
                          }
                          onClick={(e) => openFilterDropdown(e, idx)}
                          title="Filter"
                        >
                          <HamburgerIcon active={isColumnFiltered(idx)} />
                        </button>
                      )}
                    </div>
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              {/* DataTables will handle rendering tbody. No need for projects.map here. */}
              {/* projects.map is removed here as DataTables directly uses the `data` prop for rendering. */}
            </tbody>
          </table>
        </div>
      </div>

      {/* Hamburger-style Filter Dropdown */}
      {dropdownOpen && (
        <div
          ref={dropdownRef}
          className="excel-filter-dropdown"
          style={{ top: dropdownPosition.top, left: dropdownPosition.left }}
        >
          <div className="excel-filter-header">
            <input
              type="text"
              className="form-control form-control-sm"
              placeholder="Search values..."
              value={listQuery}
              onChange={(e) => setListQuery(e.target.value)}
            />
          </div>
          <div className="excel-filter-body">
            <div className="d-flex justify-content-between mb-2">
              <button
                type="button"
                className="btn btn-sm btn-outline-secondary"
                onClick={selectAllTemp}
              >
                Select All
              </button>
              <button
                type="button"
                className="btn btn-sm btn-outline-secondary"
                onClick={clearAllTemp}
              >
                Clear
              </button>
            </div>

            {filteredDropdownValues.length === 0 ? (
              <div className="text-muted small px-3 py-1">No values</div>
            ) : (
              filteredDropdownValues.map((val) => (
                <div key={val} className="form-check">
                  <input
                    className="form-check-input"
                    type="checkbox"
                    id={`chk-${dropdownColIdx}-${val}`}
                    checked={tempSelections.has(val)}
                    onChange={() => toggleTempSelection(val)}
                  />
                  <label
                    className="form-check-label"
                    htmlFor={`chk-${dropdownColIdx}-${val}`}
                    style={{ userSelect: 'none' }}
                  >
                    {val}
                  </label>
                </div>
              ))
            )}
          </div>
          <div className="excel-filter-footer">
            <button
              type="button"
              className="btn btn-sm btn-light"
              onClick={resetFilter}
            >
              Reset
            </button>
            <button
              type="button"
              className="btn btn-sm btn-primary"
              onClick={applyFilter}
            >
              Apply
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProjectLevel;
