import React, { useRef, useEffect, useState } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import $ from 'jquery';
import 'datatables.net';
import 'datatables.net-dt/css/dataTables.dataTables.min.css';
import { Bell, Download, Search, Filter } from 'lucide-react';
import Breadcrumbs from '../Breadcrumbs';
import commonData from '../../data/commonData.json';

const AssociateLevel = () => {
  const navigate = useNavigate();
  const { projectId: urlProjectId } = useParams();
  const location = useLocation();
  const tableRef = useRef(null);
  const dataTableInstance = useRef(null);

  // component state
  const [associates, setAssociates]         = useState([]);
  const [loading, setLoading]               = useState(true);
  const [error, setError]                   = useState(null);
  const [activeMonth, setActiveMonth]       = useState(null);
  const [activeYear, setActiveYear]         = useState(null);
  const [activeProjectId, setActiveProjectId]     = useState(null);
  const [activeProjectName, setActiveProjectName] = useState('Loading...');
  const [activeAccountId, setActiveAccountId]     = useState(null);
  const [activeAccountName, setActiveAccountName] = useState('Loading...');
  const [filterOption, setFilterOption]     = useState('showAll'); // or 'varianceAboveZero'

  const BACKEND_URL = 'http://localhost:8081';

  // currency formatter
  const {
    locale,
    currency,
    minimumFractionDigits,
    maximumFractionDigits
  } = commonData.currencySettings;
  const formatCurrency = (v = 0) =>
    v.toLocaleString(locale, {
      style: 'currency',
      currency,
      minimumFractionDigits,
      maximumFractionDigits
    });

  // load Poppins font
  useEffect(() => {
    const link = document.createElement('link');
    link.href =
      'https://fonts.googleapis.com/css2?family=Poppins:wght@400;600;700&display=swap';
    link.rel = 'stylesheet';
    document.head.appendChild(link);
    document.body.style.fontFamily = "'Poppins', sans-serif";
  }, []);

  // fetch data
  useEffect(() => {
    const fetchAssociateData = async (m, y, p) => {
      setLoading(true);
      setError(null);
      try {
        const res = await fetch(`${BACKEND_URL}/api/associate`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ month: m, year: y, projectId: p })
        });
        if (!res.ok) {
          const msg = await res.text();
          throw new Error(msg || res.statusText);
        }
        const data = await res.json();
        const list = Array.isArray(data) ? data : [data].filter(Boolean);
        setAssociates(list);

        if (list.length) {
          const first = list[0];
          setActiveProjectName(first.projectName || p);
          setActiveAccountId(first.accountId || activeAccountId);
          setActiveAccountName(first.accountName || activeAccountName);
        }
      } catch (e) {
        setError(`Failed to load associate data: ${e.message}`);
      } finally {
        setLoading(false);
      }
    };

    // derive month/year/project/account from location or sessionStorage
    let m = location.state?.month;
    let y = location.state?.year;
    let p = urlProjectId;
    let aId   = location.state?.accountId;
    let aName = location.state?.accountName;
    let pName = location.state?.projectName;

    if (!m || !y || !aId || !pName) {
      const sm  = sessionStorage.getItem('lastFetchedAssociateMonth');
      const sy  = sessionStorage.getItem('lastFetchedAssociateYear');
      const sp  = sessionStorage.getItem('lastFetchedAssociateProjectId');
      const sn  = sessionStorage.getItem('lastFetchedAssociateProjectName');
      const sa  = sessionStorage.getItem('lastFetchedAssociateAccountId');
      const san = sessionStorage.getItem('lastFetchedAssociateAccountName');
      if (sm && sy && sp) {
        m     = parseInt(sm, 10);
        y     = parseInt(sy, 10);
        p     = sp;
        pName = sn;
        aId   = sa;
        aName = san;
      }
    }

    if (m && y && p && aId && pName) {
      if (
        m !== activeMonth ||
        y !== activeYear ||
        p !== activeProjectId ||
        associates.length === 0
      ) {
        setActiveMonth(m);
        setActiveYear(y);
        setActiveProjectId(p);
        setActiveProjectName(pName);
        setActiveAccountId(aId);
        setActiveAccountName(aName);

        sessionStorage.setItem('lastFetchedAssociateMonth', m.toString());
        sessionStorage.setItem('lastFetchedAssociateYear', y.toString());
        sessionStorage.setItem('lastFetchedAssociateProjectId', p);
        sessionStorage.setItem('lastFetchedAssociateProjectName', pName);
        sessionStorage.setItem('lastFetchedAssociateAccountId', aId);
        sessionStorage.setItem('lastFetchedAssociateAccountName', aName);

        fetchAssociateData(m, y, p);
      } else {
        setLoading(false);
      }
    } else {
      setError('Missing associate context. Please go back.');
      setLoading(false);
    }
  }, [
    location.state,
    urlProjectId,
    activeMonth,
    activeYear,
    activeProjectId,
    associates.length
  ]);

  // initialize DataTable & make the built-in ‚ÄúSearch:‚Äù sticky
  useEffect(() => {
    if (!loading && tableRef.current) {
      const $tbl = $(tableRef.current);

      if ($.fn.DataTable.isDataTable($tbl)) {
        dataTableInstance.current.destroy();
      }

      // clear old custom filters
      $.fn.dataTable.ext.search.pop();
      if (filterOption === 'varianceAboveZero') {
        $.fn.dataTable.ext.search.push((settings, data) => {
          return parseFloat(data[7]) !== 0;
        });
      }

      dataTableInstance.current = $tbl.DataTable({
        paging: true,
        searching: true,
        ordering: false,
        info: true,
        autoWidth: false,
        dom:
          '<"d-flex flex-column flex-md-row justify-content-between align-items-center mb-3"<"mb-2 mb-md-0"l><"ms-md-auto"f>>' +
          't' +
          '<"d-flex flex-column flex-md-row justify-content-between align-items-center mt-3"<"mb-2 mb-md-0"i>p>',
        language: {
          search: 'Search:',
          lengthMenu: 'Show _MENU_ entries'
        }
      });

      // ‚Äî‚Äî‚Äî Here‚Äôs the magic: pin the Search box to the right ‚Äî‚Äî‚Äî
      const wrapper = $tbl.closest('.dataTables_wrapper');
      wrapper.css('position', 'relative');
      wrapper
        .find('.dataTables_filter')
        .css({
          position: 'sticky',
          top: 0,
          right: 0,
          background: '#fff',
          'z-index': 10,
          margin: 0
        })
        .find('label')
        .css({
          margin: 0,
          padding: '0.5rem'
        });
    }

    return () => {
      if (dataTableInstance.current) {
        dataTableInstance.current.destroy();
        dataTableInstance.current = null;
      }
      $.fn.dataTable.ext.search.pop();
    };
  }, [loading, associates, filterOption]);

  // other handlers
  const handleFilterChange = (e) => setFilterOption(e.target.value);
  const handleNotifyAll   = () => console.log('Notify all');
  const handleDownload    = () => console.log('Download data');

  const breadcrumbPath = [
    { name: 'PMO Dashboard', page: '' },
    { name: 'Revenue Forecast - Early View', page: 'upload' },
    { name: 'Account Level', page: 'accounts' },
    {
      name: `Projects (${activeAccountName})`,
      page: `accounts/${activeAccountId}/projects`
    },
    {
      name: `Associates (${activeProjectName})`,
      page: `projects/${activeProjectId}/associates`
    }
  ];

  if (loading) {
    return (
      <div className="min-vh-100 d-flex justify-content-center align-items-center">
        <div className="spinner-border text-primary" role="status" />
        <p className="ms-3 text-primary">Loading Associate Data...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-vh-100 d-flex flex-column justify-content-center align-items-center text-danger">
        <p className="fs-4">Error: {error}</p>
        <button
          className="btn btn-primary mt-3"
          onClick={() => navigate(`/accounts/${activeAccountId}/projects`)}
        >
          Go to Project Level
        </button>
      </div>
    );
  }

  return (
    <div className="min-vh-100 d-flex flex-column align-items-center px-3 py-4">
      <div className="card shadow p-4 w-100" style={{ maxWidth: '1100px' }}>
        <Breadcrumbs path={breadcrumbPath} />

        {/* Header & Controls */}
        <div className="d-flex flex-column gap-3 mb-4">
          <h2 className="mb-0 text-center text-md-start fw-semibold text-dark">
            üë§ Associate Details ‚Äì Project: {activeProjectName}
          </h2>
          <div className="d-flex flex-wrap justify-content-between align-items-center gap-3">
            <div className="d-flex align-items-center">
              <label
                htmlFor="varianceFilter"
                className="me-2 mb-0 fw-semibold text-slate-700"
              >
                <Filter size={18} className="me-1 text-blue-500" />
                Filter
              </label>
              <select
                id="varianceFilter"
                className="form-select rounded-lg px-3 py-2 shadow-sm"
                value={filterOption}
                onChange={handleFilterChange}
                style={{ minWidth: '180px' }}
              >
                <option value="showAll">Show All Variance</option>
                <option value="varianceAboveZero">Variance ‚â† 0</option>
              </select>
            </div>
            <div className="d-flex gap-2 ms-auto">
              <button
                className="btn btn-outline-info d-flex align-items-center px-4 py-2"
                onClick={handleNotifyAll}
              >
                <Bell size={18} className="me-2" />
                Notify All
              </button>
              <button
                className="btn btn-outline-success d-flex align-items-center px-4 py-2"
                onClick={handleDownload}
              >
                <Download size={18} className="me-2" />
                Download Data
              </button>
            </div>
          </div>
        </div>

        {/* Table */}
        <div
          className="table-responsive rounded shadow-sm"
          style={{ overflowX: 'auto' }}
        >
          <table
            ref={tableRef}
            className="display table table-hover table-borderless align-middle mb-0 w-100"
            style={{ minWidth: '1200px' }}
          >
            <thead
              style={{
                background: 'linear-gradient(to right, #0ea5e9, #2563eb)',
                color: 'white'
              }}
            >
              <tr>
                <th className="px-4 py-3 text-left">Associate ID</th>
                <th className="px-4 py-3 text-left">Associate Name</th>
                <th className="px-4 py-3 text-left">Project ID</th>
                <th className="px-4 py-3 text-left">Project Name</th>
                <th className="px-4 py-3 text-left">PM ID</th>
                <th className="px-4 py-3 text-left">Company Hours</th>
                <th className="px-4 py-3 text-left">Client Hours</th>
                <th className="px-4 py-3 text-left">Variance Hours</th>
                <th className="px-4 py-3 text-left">Actual Revenue</th>
                <th className="px-4 py-3 text-left">Associate Rate</th>
                <th className="px-4 py-3 text-center">Notify</th>
                <th className="px-4 py-3 text-center">View Daily</th>
              </tr>
            </thead>
            <tbody>
              {(filterOption === 'varianceAboveZero'
                ? associates.filter((a) => a.varianceHoursMonthly !== 0)
                : associates
              ).map((associate) => (
                <tr key={associate.associateId}>
                  <td className="px-4 py-3">{associate.associateId}</td>
                  <td className="px-4 py-3 font-medium">
                    {associate.associateName}
                  </td>
                  <td className="px-4 py-3">{associate.projectId}</td>
                  <td className="px-4 py-3">{associate.projectName}</td>
                  <td className="px-4 py-3">{associate.esaID}</td>
                  <td className="px-4 py-3">
                    {associate.totalCompanyHoursMonthly}
                  </td>
                  <td className="px-4 py-3">
                    {associate.totalClientHoursMonthly}
                  </td>
                  <td className="px-4 py-3">
                    <span
                      className={`inline-flex px-2.5 py-0.5 rounded-full text-xs font-medium ${
                        associate.varianceHoursMonthly > 0
                          ? 'bg-red-100 text-red-800'
                          : associate.varianceHoursMonthly < 0
                          ? 'bg-green-100 text-green-800'
                          : 'bg-slate-100 text-slate-700'
                      }`}
                    >
                      {associate.varianceHoursMonthly}
                    </span>
                  </td>
                  <td className="px-4 py-3">
                    {formatCurrency(associate.actualRevenue)}
                  </td>
                  <td className="px-4 py-3">
                    {formatCurrency(associate.associateRtRate)}
                  </td>
                  <td className="px-4 py-3 text-center">
                    <button className="btn btn-sm btn-outline-info px-3 py-1">
                      <Bell size={16} className="me-1" />
                      Notify
                    </button>
                  </td>
                  <td className="px-4 py-3 text-center">
                    <button
                      className="btn btn-sm btn-outline-primary p-2"
                      onClick={() =>
                        navigate(
                          `/projects/${associate.projectId}/associates/${associate.associateId}/daily`,
                          {
                            state: {
                              month: activeMonth,
                              year: activeYear,
                              projectId: associate.projectId,
                              projectName: associate.projectName,
                              accountId: activeAccountId,
                              accountName: activeAccountName,
                              associateId: associate.associateId,
                              associateName: associate.associateName
                            }
                          }
                        )
                      }
                    >
                      <Search size={16} />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AssociateLevel;
