// src/components/Details/SbuLevel.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Table, Alert, Spinner, Button } from 'react-bootstrap';
import { useLocation, useNavigate } from 'react-router-dom';
import Breadcrumbs from '../Breadcrumbs';
import formatCurrency from '../../utils/formatCurrency'; // your helper

const SbuLevel = () => {
  const navigate = useNavigate();
  const { month, year } = useLocation().state || {};

  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const getMonthName = (m) =>
    new Date(year, m - 1).toLocaleString('en-US', { month: 'long' });

  useEffect(() => {
    if (!month || !year) {
      setError('Month and year are missing. Go back and select a period.');
      setLoading(false);
      return;
    }

    const fetchSbu = async () => {
      setLoading(true);
      setError('');
      try {
        const payload = {
          month: Number(month),
          year: Number(year),
          monthNameParam: getMonthName(month).toLowerCase(),
        };
        const resp = await axios.post(
          'http://localhost:8081/api/sbu',
          payload
        );
        setData(resp.data);
      } catch (err) {
        setError(err.response?.data || err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchSbu();
  }, [month, year]);

  // filter out any revenue columns
  const columns = data.length
    ? Object.keys(data[0]).filter((col) => {
        const lc = col.toLowerCase();
        return (
          lc !== 'revenue' &&
          lc !== 'sburevenue' &&
          lc !== 'totalsburevenue'
        );
      })
    : [];

  // add a View column at the end
  const headerCols = [...columns, 'View'];

  return (
    <Container className="my-4">
      <Breadcrumbs path={[{ name: 'SBU Level', page: 'sbu' }]} />

      <Button
        variant="link"
        className="ps-0 mb-4"
        onClick={() => navigate(-1)}
      >
        ← Back
      </Button>

      <h2 className="mb-4">
        SBU Summary for {getMonthName(month)} {year}
      </h2>

      {loading && (
        <div className="d-flex justify-content-center my-5">
          <Spinner animation="border" />
          <span className="ms-2">Loading data...</span>
        </div>
      )}

      {!loading && error && <Alert variant="danger">{error}</Alert>}

      {!loading && !error && data.length === 0 && (
        <Alert variant="info">No data found for this period.</Alert>
      )}

      {!loading && !error && data.length > 0 && (
        <Table striped bordered hover responsive>
          <thead>
            <tr>
              {headerCols.map((col) => (
                <th key={col}>{col}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {data.map((row, i) => (
              <tr key={i}>
                {columns.map((col) => (
                  <td key={col}>
                    {typeof row[col] === 'number' ||
                    /^e\d+$/i.test(row[col])
                      ? formatCurrency(row[col])
                      : row[col]}
                  </td>
                ))}

                {/* View button navigates to Account Level */}
                <td className="text-center">
                  <Button
                    size="sm"
                    variant="primary"
                    onClick={() =>
                      navigate('/accounts', {
                        state: { month, year, sbuId: row.sbuId },
                      })
                    }
                  >
                    View
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </Container>
  );
};

export default SbuLevel;
