import React, { useRef, useEffect, useState } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import $ from 'jquery';
import 'datatables.net';
import 'datatables.net-dt/css/dataTables.dataTables.min.css';
import { Bell, Download, Search, Users, Filter } from 'lucide-react'; // Re-introducing Lucide icons
import Breadcrumbs from '../Breadcrumbs';
import commonData from '../../data/commonData.json';

const AssociateLevel = () => {
  const navigate = useNavigate();
  const { projectId: urlProjectId } = useParams();
  const location = useLocation();
  const tableRef = useRef(null);
  const dataTableInstance = useRef(null); // Ref to store the DataTables instance

  const [associates, setAssociates] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeMonth, setActiveMonth] = useState(null);
  const [activeYear, setActiveYear] = useState(null);
  const [activeProjectId, setActiveProjectId] = useState(null);
  const [activeProjectName, setActiveProjectName] = useState('Loading...');
  const [activeAccountId, setActiveAccountId] = useState(null);
  const [activeAccountName, setActiveAccountName] = useState('Loading...');
  // New state for the selected filter option: 'showAll' or 'varianceAboveZero'
  const [filterOption, setFilterOption] = useState('showAll');
  const [globalSearchTerm, setGlobalSearchTerm] = useState(''); // State for the custom search input

  const BACKEND_URL = 'http://localhost:8081';

  // Currency formatter from commonData.json
  const {
    locale,
    currency,
    minimumFractionDigits,
    maximumFractionDigits
  } = commonData.currencySettings;

  const formatCurrency = (value = 0) =>
    value.toLocaleString(locale, {
      style: 'currency',
      currency,
      minimumFractionDigits,
      maximumFractionDigits
    });

  // Load Poppins font once
  useEffect(() => {
    const link = document.createElement('link');
    link.href =
      'https://fonts.googleapis.com/css2?family=Poppins:wght@400;600;700&display=swap';
    link.rel = 'stylesheet';
    document.head.appendChild(link);
    document.body.style.fontFamily = "'Poppins', sans-serif";
  }, []);

  // Fetch associates based on context
  useEffect(() => {
    const fetchAssociateData = async (month, year, projId) => {
      setLoading(true);
      setError(null);
      try {
        const resp = await fetch(`${BACKEND_URL}/api/associate`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ month, year, projectId: projId })
        });
        if (!resp.ok) {
          const msg = await resp.text();
          throw new Error(`status: ${resp.status}, message: ${msg || resp.statusText}`);
        }
        const data = await resp.json();
        const list = Array.isArray(data) ? data : [data].filter(Boolean);
        setAssociates(list);

        if (list.length > 0) {
          const first = list[0];
          setActiveProjectName(first.projectName || projId);
          setActiveAccountId(first.accountId || activeAccountId);
          setActiveAccountName(first.accountName || activeAccountName);
        }
      } catch (e) {
        console.error('Error fetching associate data:', e);
        setError(`Failed to load associate data: ${e.message}`);
      } finally {
        setLoading(false);
      }
    };

    let month = location.state?.month;
    let year = location.state?.year;
    let projId = urlProjectId;
    let acctId = location.state?.accountId;
    let acctName = location.state?.accountName;
    let projName = location.state?.projectName;

    if (!month || !year || !acctId || !projName) {
      const sm = sessionStorage.getItem('lastFetchedAssociateMonth');
      const sy = sessionStorage.getItem('lastFetchedAssociateYear');
      const sp = sessionStorage.getItem('lastFetchedAssociateProjectId');
      const sn = sessionStorage.getItem('lastFetchedAssociateProjectName');
      const sa = sessionStorage.getItem('lastFetchedAssociateAccountId');
      const san = sessionStorage.getItem('lastFetchedAssociateAccountName');
      if (sm && sy && sp) {
        month = parseInt(sm, 10);
        year = parseInt(sy, 10);
        projId = sp;
        projName = sn;
        acctId = sa;
        acctName = san;
      }
    }

    if (month && year && projId && acctId && projName) {
      if (
        month !== activeMonth ||
        year !== activeYear ||
        projId !== activeProjectId ||
        associates.length === 0
      ) {
        setActiveMonth(month);
        setActiveYear(year);
        setActiveProjectId(projId);
        setActiveProjectName(projName);
        setActiveAccountId(acctId);
        setActiveAccountName(acctName);

        sessionStorage.setItem('lastFetchedAssociateMonth', month.toString());
        sessionStorage.setItem('lastFetchedAssociateYear', year.toString());
        sessionStorage.setItem('lastFetchedAssociateProjectId', projId);
        sessionStorage.setItem('lastFetchedAssociateProjectName', projName);
        sessionStorage.setItem('lastFetchedAssociateAccountId', acctId);
        sessionStorage.setItem('lastFetchedAssociateAccountName', acctName);

        fetchAssociateData(month, year, projId);
      } else {
        setLoading(false);
      }
    } else {
      setError(
        'Missing associate context (month, year, project, or account). Please go back.'
      );
      setLoading(false);
    }
  }, [
    location.state,
    urlProjectId,
    activeMonth,
    activeYear,
    activeProjectId,
    associates.length
  ]);

  // Initialize DataTables when associates load or filter changes
  useEffect(() => {
    if (!loading && tableRef.current) {
      const $tbl = $(tableRef.current);
      if ($.fn.DataTable.isDataTable($tbl)) {
        dataTableInstance.current.destroy(); // Destroy existing instance
      }

      // Remove any previously added custom search functions
      $.fn.dataTable.ext.search.pop();

      // Apply custom filter based on filterOption state
      if (filterOption === 'varianceAboveZero') {
        $.fn.dataTable.ext.search.push(
          (settings, data, dataIndex) => {
            // Assuming 'Variance Hours (Monthly)' is the 8th column (index 7)
            const varianceHoursMonthly = parseFloat(data[7]) || 0;
            return varianceHoursMonthly !== 0;
          }
        );
      }

      // Initialize DataTables with no column ordering and custom DOM for cleaner layout
      dataTableInstance.current = $tbl.DataTable({
        paging: true,
        searching: true, // Keep searching enabled for API control
        ordering: false, // Removed individual column sorting
        info: true,
        autoWidth: false,
        // Remove 'f' from DOM as we're handling search manually
        dom: '<"d-flex flex-column flex-md-row justify-content-between align-items-center mb-3"<"mb-2 mb-md-0"l>>t<"d-flex flex-column flex-md-row justify-content-between align-items-center mt-3"<"mb-2 mb-md-0"i>p>',
        language: {
          // No need for search label here, as we're using a custom input
          lengthMenu: "Show _MENU_ entries" // Customize length menu label
        }
      });

      // Apply initial global search if there's a term
      if (globalSearchTerm) {
        dataTableInstance.current.search(globalSearchTerm).draw();
      }
    }

    // Cleanup function for DataTables
    return () => {
      if (dataTableInstance.current) {
        dataTableInstance.current.destroy();
        dataTableInstance.current = null;
      }
      $.fn.dataTable.ext.search.pop(); // Clean up custom search on unmount
    };
  }, [loading, associates, filterOption]); // Re-run effect when filterOption changes

  // Effect to apply global search whenever globalSearchTerm changes
  useEffect(() => {
    if (dataTableInstance.current) {
      dataTableInstance.current.search(globalSearchTerm).draw();
    }
  }, [globalSearchTerm]);


  const breadcrumbPath = [
    { name: 'PMO Dashboard', page: '' },
    { name: 'Revenue Forecast - Early View', page: 'upload' },
    { name: 'Account Level', page: 'accounts' },
    {
      name: `Projects (${activeAccountName})`,
      page: `accounts/${activeAccountId}/projects`
    },
    {
      name: `Associates (${activeProjectName})`,
      page: `projects/${activeProjectId}/associates`
    }
  ];

  const handleNotifyAll = () => {
    // Using console.log instead of alert for better UX in a web app
    console.log('Notifying all associates for this project!');
  };

  const handleDownloadData = () => {
    // Using console.log instead of alert for better UX in a web app
    console.log('Downloading associate data for this project!');
  };

  // Handler for the variance filter dropdown change
  const handleFilterChange = (event) => {
    setFilterOption(event.target.value);
  };

  // Handler for the custom global search input
  const handleGlobalSearchChange = (event) => {
    setGlobalSearchTerm(event.target.value);
  };

  if (loading) {
    return (
      <div className="min-vh-100 d-flex justify-content-center align-items-center bg-gradient-to-br from-blue-50 to-indigo-100">
        <div className="spinner-border text-primary" role="status" />
        <p className="ms-3 text-primary font-medium">Loading Associate Data...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-vh-100 d-flex flex-column justify-content-center align-items-center text-danger bg-gradient-to-br from-red-50 to-pink-100">
        <p className="fs-4 text-red-600 font-semibold">Error: {error}</p>
        <button
          className="btn btn-primary mt-3 px-4 py-2 rounded-lg shadow-md hover:shadow-lg transition-all"
          onClick={() =>
            navigate(`/accounts/${activeAccountId}/projects`)
          }
        >
          Go to Project Level
        </button>
      </div>
    );
  }

  return (
    <div
      className="min-vh-100 d-flex flex-column align-items-center px-3 py-4 bg-gradient-to-br from-slate-50 to-blue-50"
      style={{ fontFamily: "'Poppins', sans-serif" }}
    >
      {/* Inline styles for sticky column - IMPORTANT: Ensure these are placed where they can be applied */}
      <style>
        {`
        .sticky-column {
          position: sticky;
          right: 0;
          background-color: #ffffff; /* Explicit white */
          z-index: 1; /* Lower, more common z-index */
          border-left: 1px solid #e2e8f0; /* Subtle border for visibility */
          white-space: nowrap; /* Prevent content wrapping */
        }
        /* Higher z-index for sticky header cell */
        .sticky-column.header {
          z-index: 2; /* Higher z-index for sticky header */
          background: linear-gradient(to right, #0ea5e9, #2563eb); /* Match header background */
          border-left: 1px solid #e2e8f0; /* Match header border */
        }
        /* Ensure table has separate borders for sticky to work well */
        table {
          border-collapse: separate;
          border-spacing: 0;
        }
        /* Add consistent bottom border to all cells for visual continuity */
        table th, table td {
          border-bottom: 1px solid #e2e8f0;
        }
        /* Remove bottom border from the last row's cells if desired for cleaner look */
        table tbody tr:last-child td {
          border-bottom: none;
        }
        `}
      </style>

      <div
        className="card shadow-lg p-4 w-100" // Increased shadow
        style={{
          maxWidth: '1100px',
          backgroundColor: '#ffffffee', // Slightly more opaque white
          borderRadius: '1rem'
        }}
      >
        <Breadcrumbs path={breadcrumbPath} />

        {/* Enhanced Header Section */}
        <div className="d-flex flex-column gap-3 mb-4">
          {/* Title - Line 1 */}
          <div className="w-100 text-center text-md-start">
            <h2 className="mb-0 fw-semibold text-dark"> {/* Reverted font weight and color */}
              ðŸ‘¤ Associate Details â€“ Project: {activeProjectName}
            </h2>
          </div>

          {/* Filter, Search, and Action Buttons - Line 2 */}
          <div className="d-flex flex-wrap justify-content-between align-items-center gap-3 w-100">
            {/* Variance Filter Dropdown (Left side) */}
            <div className="d-flex align-items-center">
              <label htmlFor="varianceFilter" className="form-label mb-0 me-2 fw-semibold text-slate-700 text-nowrap">
                <Filter className="inline-block me-1 text-blue-500" size={16} /> Filter:
              </label>
              <select
                id="varianceFilter"
                className="form-select rounded-lg px-2.5 py-1.5 shadow-sm border border-slate-300 focus:border-blue-500 focus:ring focus:ring-blue-200 focus:ring-opacity-50 transition-all"
                value={filterOption}
                onChange={handleFilterChange}
                style={{ minWidth: '160px' }}
              >
                <option value="showAll">Show All Variance</option>
                <option value="varianceAboveZero">Variance â‰  0</option>
              </select>
            </div>

            {/* Search Input and Action Buttons (Right side) */}
            <div className="d-flex gap-2 ms-auto align-items-center"> {/* ms-auto pushes this div to the right */}
              {/* Custom Search Input */}
              <div className="input-group" style={{ maxWidth: '250px' }}>
                <span className="input-group-text rounded-start-lg bg-white border-slate-300 text-slate-600">
                  <Search size={16} />
                </span>
                <input
                  type="text"
                  className="form-control rounded-end-lg px-3 py-1.5 shadow-sm border border-slate-300 focus:border-blue-500 focus:ring focus:ring-blue-200 focus:ring-opacity-50 transition-all"
                  placeholder="Search..."
                  value={globalSearchTerm}
                  onChange={handleGlobalSearchChange}
                />
              </div>

              <button
                className="btn btn-outline-info rounded-lg px-3 py-1.5 d-flex align-items-center shadow-sm hover:shadow-md hover:scale-105 transition-all text-blue-700 border-blue-300 hover:bg-blue-50"
                onClick={handleNotifyAll}
              >
                <Bell className="me-2" size={16} /> Notify All
              </button>
              <button
                className="btn btn-outline-success rounded-lg px-3 py-1.5 d-flex align-items-center shadow-sm hover:shadow-md hover:scale-105 transition-all text-green-700 border-green-300 hover:bg-green-50"
                onClick={handleDownloadData}
              >
                <Download className="me-2" size={16} /> Download Data
              </button>
            </div>
          </div>
        </div>

        <div className="table-responsive rounded shadow-sm"> {/* Added shadow to table container */}
          <table
            ref={tableRef}
            className="display table table-hover table-borderless align-middle mb-0 w-100"
            style={{ width: '100%' }}
          >
            <thead
              style={{
                background: 'linear-gradient(to right, #0ea5e9, #2563eb)',
                color: 'white'
              }}
            >
              <tr>
                <th className="px-4 py-3 text-left text-sm font-semibold">Associate ID</th>
                <th className="px-4 py-3 text-left text-sm font-semibold">Associate Name</th>
                <th className="px-4 py-3 text-left text-sm font-semibold">Project ID</th>
                <th className="px-4 py-3 text-left text-sm font-semibold">Project Name</th>
                <th className="px-4 py-3 text-left text-sm font-semibold">PM ID</th>
                <th className="px-4 py-3 text-left text-sm font-semibold">Total Company Hours (Monthly)</th>
                <th className="px-4 py-3 text-left text-sm font-semibold">Total Client Hours (Monthly)</th>
                <th className="px-4 py-3 text-left text-sm font-semibold">Variance Hours (Monthly)</th>
                <th className="px-4 py-3 text-left text-sm font-semibold">Actual Revenue</th>
                <th className="px-4 py-3 text-left text-sm font-semibold">Associate Rate</th>
                <th className="px-4 py-3 text-center text-sm font-semibold">Notify</th>
                <th className="px-4 py-3 text-center text-sm font-semibold sticky-column header">View Daily</th> {/* Added sticky-column and header class */}
              </tr>
            </thead>
            <tbody>
              {associates.map((associate) => (
                <tr key={associate.associateId}>
                  <td className="px-4 py-3 text-sm text-slate-800">{associate.associateId}</td>
                  <td className="px-4 py-3 text-sm text-slate-800 font-medium">{associate.associateName}</td>
                  <td className="px-4 py-3 text-sm text-slate-700">{associate.projectId}</td>
                  <td className="px-4 py-3 text-sm text-slate-700">{associate.projectName}</td>
                  <td className="px-4 py-3 text-sm text-slate-700">{associate.esaID}</td>
                  <td className="px-4 py-3 text-sm text-slate-700 font-mono">{associate.totalCompanyHoursMonthly}</td>
                  <td className="px-4 py-3 text-sm text-slate-700 font-mono">{associate.totalClientHoursMonthly}</td>
                  <td className="px-4 py-3 text-sm font-mono">
                    {/* Visual styling for variance */}
                    <span className={`
                      inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium 
                      ${(associate.varianceHoursMonthly || 0) > 0 
                        ? 'bg-red-100 text-red-800' 
                        : (associate.varianceHoursMonthly || 0) < 0
                        ? 'bg-green-100 text-green-800'
                        : 'bg-slate-100 text-slate-700'
                      }
                    `}>
                      {associate.varianceHoursMonthly}
                    </span>
                  </td>
                  <td className="px-4 py-3 text-sm text-slate-800 font-semibold">{formatCurrency(associate.actualRevenue)}</td>
                  <td className="px-4 py-3 text-sm text-slate-700">{formatCurrency(associate.associateRtRate)}</td>
                  <td className="px-4 py-3 text-center">
                    <button className="btn btn-sm btn-outline-info rounded-lg px-3 py-1 d-flex align-items-center text-blue-600 border-blue-200 hover:bg-blue-50 transition-colors">
                      <Bell size={16} className="me-1" /> Notify
                    </button>
                  </td>
                  <td className="px-4 py-3 text-center sticky-column"> {/* Added sticky-column class */}
                    <button
                      className="btn btn-sm btn-outline-primary rounded-full p-2 d-flex align-items-center justify-content-center text-blue-600 border-blue-200 hover:bg-blue-50 transition-colors"
                      onClick={() => {
                        navigate(
                          `/projects/${associate.projectId}/associates/${associate.associateId}/daily`,
                          {
                            state: {
                              month: activeMonth,
                              year: activeYear,
                              projectId: associate.projectId,
                              projectName: associate.projectName,
                              accountId: activeAccountId,
                              accountName: activeAccountName,
                              associateId: associate.associateId,
                              associateName: associate.associateName
                            }
                          }
                        );
                      }}
                    >
                      <Search size={16} />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AssociateLevel;
