import React, { useEffect, useState } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import Breadcrumbs from '../Breadcrumbs'; // Corrected path as per your project structure

// 1) Import your commonData.json
import commonData from '../../data/commonData.json';

const AssociateLevel = () => {
  const navigate = useNavigate();
  const { projectId: urlProjectId } = useParams(); // Get projectId from URL parameters
  const location = useLocation(); // Hook to access location state

  // State for fetched associate data
  const [associates, setAssociates] = useState([]);
  // State for loading indicator
  const [loading, setLoading] = useState(true);
  // State for error messages
  const [error, setError] = useState(null);

  // States to keep track of the context (month, year, projectId, accountId) for which data was last fetched
  const [activeMonth, setActiveMonth] = useState(null);
  const [activeYear, setActiveYear] = useState(null);
  const [activeProjectId, setActiveProjectId] = useState(null);
  const [activeProjectName, setActiveProjectName] = useState('Loading...');
  const [activeAccountId, setActiveAccountId] = useState(null);
  const [activeAccountName, setActiveAccountName] = useState('Loading...');

  const BACKEND_URL = 'http://localhost:8081';

  //Destructure your currency settings
  const {
    locale,
    currency,
    minimumFractionDigits,
    maximumFractionDigits
  } = commonData.currencySettings;

  // 3) Utility to format any number to your centralized currency format
  const formatCurrency = (value = 0) =>
    value.toLocaleString(locale, {
      style: 'currency',
      currency,
      minimumFractionDigits,
      maximumFractionDigits
    });

  // Set font family on component mount
  useEffect(() => {
    const link = document.createElement('link');
    link.href = 'https://fonts.googleapis.com/css2?family=Poppins:wght@400;600;700&display=swap';
    link.rel = 'stylesheet';
    document.head.appendChild(link);
    document.body.style.fontFamily = "'Poppins', sans-serif";
  }, []);

  // Effect to handle context and fetch data
  useEffect(() => {
    const fetchAssociateData = async (month, year, projId) => {
      setLoading(true);
      setError(null);

      // Construct the request body as expected by your backend
      const requestBody = {
        month: month,
        year: year,
        projectId: projId // Pass the project ID
      };

      console.log("Fetching associate data with request body (POST):", requestBody); // For debugging

      try {
        const response = await fetch(`${BACKEND_URL}/api/associate`, {
          method: 'POST', // As per your backend spec
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(requestBody),
        });

        if (!response.ok) {
          const errorText = await response.text();
          throw new Error(`HTTP error! status: ${response.status}, message: ${errorText || response.statusText}`);
        }

        const data = await response.json();
        setAssociates(Array.isArray(data) ? data : [data].filter(Boolean));

        // Try to derive project and account names from fetched data if available
        if (data && data.length > 0) {
          if (data[0].projectName) setActiveProjectName(data[0].projectName);
          if (data[0].accountId) setActiveAccountId(data[0].accountId);
          if (data[0].accountName) setActiveAccountName(data[0].accountName);
        } else {
          // Fallback to stored names or IDs if data is empty or names are missing
          const storedProjectName = sessionStorage.getItem('lastFetchedAssociateProjectName');
          const storedAccountName = sessionStorage.getItem('lastFetchedAssociateAccountName');
          const storedAccountId = sessionStorage.getItem('lastFetchedAssociateAccountId');

          if (storedProjectName) setActiveProjectName(storedProjectName); else setActiveProjectName(projId);
          if (storedAccountName) setActiveAccountName(storedAccountName); else setActiveAccountName('Unknown Account');
          if (storedAccountId) setActiveAccountId(storedAccountId); else setActiveAccountId('Unknown Account ID');
        }

      } catch (err) {
        console.error("Error fetching associate data:", err);
        setError(`Failed to load associate data: ${err.message || "Network error"}`);
      } finally {
        setLoading(false);
      }
    };

    // Logic to determine context for fetching
    let monthToUse = location.state?.month;
    let yearToUse = location.state?.year;
    let projectIdToUse = urlProjectId; // Always get projectId from URL params
    let accountIdToUse = location.state?.accountId; // Get accountId from state for breadcrumbs
    let accountNameToUse = location.state?.accountName; // Get accountName from state for breadcrumbs
    let projectNameToUse = location.state?.projectName; // Get project name from state for breadcrumbs

    // If context is not in location.state (e.g., on back navigation), check sessionStorage
    if (!monthToUse || !yearToUse || !projectIdToUse || !accountIdToUse || !projectNameToUse) {
      const storedMonth = sessionStorage.getItem('lastFetchedAssociateMonth');
      const storedYear = sessionStorage.getItem('lastFetchedAssociateYear');
      const storedProjectId = sessionStorage.getItem('lastFetchedAssociateProjectId');
      const storedProjectName = sessionStorage.getItem('lastFetchedAssociateProjectName');
      const storedAccountId = sessionStorage.getItem('lastFetchedAssociateAccountId');
      const storedAccountName = sessionStorage.getItem('lastFetchedAssociateAccountName');

      if (storedMonth && storedYear && storedProjectId) {
        monthToUse = parseInt(storedMonth, 10);
        yearToUse = parseInt(storedYear, 10);
        projectIdToUse = storedProjectId;
        if (storedProjectName) projectNameToUse = storedProjectName;
        if (storedAccountId) accountIdToUse = storedAccountId;
        if (storedAccountName) accountNameToUse = storedAccountName;
      }
    }

    // If we have valid context, set active states and fetch data if needed
    if (monthToUse && yearToUse && projectIdToUse && accountIdToUse && projectNameToUse) {
      // Only fetch if the context has changed or if associates are empty
      if (
        monthToUse !== activeMonth ||
        yearToUse !== activeYear ||
        projectIdToUse !== activeProjectId ||
        associates.length === 0
      ) {
        setActiveMonth(monthToUse);
        setActiveYear(yearToUse);
        setActiveProjectId(projectIdToUse);
        setActiveProjectName(projectNameToUse); // Set project name from state
        setActiveAccountId(accountIdToUse);     // Set account ID from state
        setActiveAccountName(accountNameToUse); // Set account name from state

        // Store current context in sessionStorage
        sessionStorage.setItem('lastFetchedAssociateMonth', monthToUse.toString());
        sessionStorage.setItem('lastFetchedAssociateYear', yearToUse.toString());
        sessionStorage.setItem('lastFetchedAssociateProjectId', projectIdToUse);
        sessionStorage.setItem('lastFetchedAssociateProjectName', projectNameToUse);
        sessionStorage.setItem('lastFetchedAssociateAccountId', accountIdToUse);
        sessionStorage.setItem('lastFetchedAssociateAccountName', accountNameToUse);

        fetchAssociateData(monthToUse, yearToUse, projectIdToUse);
      } else {
        // Data is already present for the active context, no need to fetch
        setLoading(false);
      }
    } else {
      // No valid context found, show error
      setError("Missing associate context (month, year, project ID, or account ID). Please go back and select a project.");
      setLoading(false);
    }

  }, [location.state, urlProjectId, BACKEND_URL, activeMonth, activeYear, activeProjectId, activeAccountId, activeAccountName, activeProjectName, associates.length]); // Dependencies for useEffect

  // Adjust breadcrumb path based on active states
  const breadcrumbPath = [
    { name: 'PMO Dashboard', page: '' },
    { name: 'Revenue Forecast - Early View', page: 'upload' },
    { name: 'Account Level', page: 'accounts' },
    { name: `Projects (${activeAccountName})`, page: `accounts/${activeAccountId}/projects` },
    { name: `Associates (${activeProjectName})`, page: `projects/${activeProjectId}/associates` }
  ];

  // Placeholder function for Notify All
  const handleNotifyAll = () => {
    alert('Notifying all associates for this project!');
    // Implement your actual notification logic here
  };

  // Placeholder function for Download Data
  const handleDownloadData = () => {
    alert('Downloading associate data for this project!');
    // Implement your actual data download logic here
    // You might want to format 'associates' data into CSV or Excel and trigger a download
  };

  // Render loading state
  if (loading) {
    return (
      <div className="min-vh-100 d-flex justify-content-center align-items-center">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading Associate Data...</span>
        </div>
        <p className="ms-3 text-primary">Loading Associate Data...</p>
      </div>
    );
  }

  // Render error state
  if (error) {
    return (
      <div className="min-vh-100 d-flex flex-column justify-content-center align-items-center text-danger">
        <p className="fs-4">Error: {error}</p>
        <button className="btn btn-primary mt-3" onClick={() => navigate(`/accounts/${activeAccountId}/projects`)}>
          Go to Project Level
        </button>
      </div>
    );
  }

  return (
    <div
      className="min-vh-100 d-flex flex-column align-items-center px-3 py-4"
      style={{
        background: 'linear-gradient(to bottom right, #f5f7fa, #e9ecef)',
        fontFamily: "'Poppins', sans-serif"
      }}
    >
      <div
        className="card shadow p-4 w-100"
        style={{
          maxWidth: '1100px',
          backgroundColor: '#ffffffee',
          borderRadius: '1rem'
        }}
      >
        <Breadcrumbs path={breadcrumbPath} />

        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2 className="mb-0 fw-semibold text-dark">
            üë§ Associate Details ‚Äì Project: {activeProjectName}
          </h2>
          <div className="d-flex gap-2">
            <button
              className="btn btn-outline-info rounded-pill px-3"
              onClick={handleNotifyAll}
            >
              Notify All
            </button>
            <button
              className="btn btn-outline-success rounded-pill px-3"
              onClick={handleDownloadData}
            >
              Download Data
            </button>
          </div>
        </div>

        <div className="table-responsive rounded">
          <table className="table table-hover table-borderless align-middle mb-0">
            <thead
              style={{
                background: 'linear-gradient(to right, #0ea5e9, #2563eb)',
                color: 'white'
              }}
            >
              <tr>
                <th>Associate ID</th>
                <th>Associate Name</th>
                <th>Project ID</th>
                <th>Project Name</th>
                <th>PM ID</th>
                <th>Total Company Hours (Monthly)</th>
                <th>Total Client Hours (Monthly)</th>
                <th>Variance Hours (Monthly)</th>
                <th>Actual Revenue</th>
                <th>Associate Rate</th>
                <th className="text-center">Notify</th>
                <th className="text-center">View Daily</th>
              </tr>
            </thead>
            <tbody>
              {associates.length > 0 ? (
                associates.map((associate) => {
                  return (
                    <tr key={associate.associateId}>
                      <td>{associate.associateId}</td>
                      <td>{associate.associateName}</td>
                      <td>{associate.projectId}</td>
                      <td>{associate.projectName}</td>
                      <td>{associate.esaID}</td>
                      <td>{associate.totalCompanyHoursMonthly}</td>
                      <td>{associate.totalClientHoursMonthly}</td>
                      <td>{associate.varianceHoursMonthly}</td>
                      {/* Apply the formatCurrency function here */}
                      <td>{formatCurrency(associate.actualRevenue)}</td>
                      <td>{formatCurrency(associate.associateRtRate)}</td>
                      <td className="text-center">
                        <button className="btn btn-sm btn-outline-info rounded-pill px-3">
                          Notify
                        </button>
                      </td>
                      <td className="text-center">
                        <button
                          // Navigate to DateLevel, passing full context
                          onClick={() => {
                            const targetPath = `/projects/${associate.projectId}/associates/${associate.associateId}/daily`;
                            const stateToPass = {
                              month: activeMonth,
                              year: activeYear,
                              projectId: associate.projectId, // Use from current associate object
                              projectName: associate.projectName, // Use from current associate object
                              accountId: activeAccountId, // Use from AssociateLevel's state
                              accountName: activeAccountName, // Use from AssociateLevel's state
                              associateId: associate.associateId,
                              associateName: associate.associateName
                            };
                            // ADD THIS LOG: Check the path and state before navigation
                            console.log("Navigating to DateLevel with path:", targetPath, "and state:", stateToPass);
                            navigate(targetPath, { state: stateToPass });
                          }}
                          className="btn btn-sm btn-outline-primary rounded-circle"
                          title="View Daily Hours"
                        >
                          üîç
                        </button>
                      </td>
                    </tr>
                  );
                })
              ) : (
                <tr>
                  <td colSpan="11" className="text-center text-muted py-4">
                    No associates found for this project and period.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AssociateLevel;
