import React, { useRef, useEffect, useState } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import $ from 'jquery';
import 'datatables.net';
import 'datatables.net-dt/css/dataTables.dataTables.min.css';
import { Bell, Download, Search, Filter } from 'lucide-react';
import Breadcrumbs from '../Breadcrumbs';
import commonData from '../../data/commonData.json';

const AssociateLevel = () => {
  const navigate = useNavigate();
  const { projectId: urlProjectId } = useParams();
  const location = useLocation();
  const tableRef = useRef(null);
  const dataTableInstance = useRef(null);

  // State
  const [associates, setAssociates] = useState([]);
  const [loading, setLoading]       = useState(true);
  const [error, setError]           = useState(null);
  const [activeMonth, setActiveMonth]       = useState(null);
  const [activeYear, setActiveYear]         = useState(null);
  const [activeProjectId, setActiveProjectId]     = useState(null);
  const [activeProjectName, setActiveProjectName] = useState('Loading...');
  const [activeAccountId, setActiveAccountId]     = useState(null);
  const [activeAccountName, setActiveAccountName] = useState('Loading...');
  const [filterOption, setFilterOption] = useState('showAll');

  const BACKEND_URL = 'http://localhost:8081';

  // Currency formatter
  const { locale, currency, minimumFractionDigits, maximumFractionDigits } =
    commonData.currencySettings;
  const formatCurrency = (value = 0) =>
    value.toLocaleString(locale, {
      style: 'currency',
      currency,
      minimumFractionDigits,
      maximumFractionDigits,
    });

  // Load Poppins font
  useEffect(() => {
    const link = document.createElement('link');
    link.href =
      'https://fonts.googleapis.com/css2?family=Poppins:wght@400;600;700&display=swap';
    link.rel = 'stylesheet';
    document.head.appendChild(link);
    document.body.style.fontFamily = "'Poppins', sans-serif'";
  }, []);

  // Inject stickyâ€style for the DataTables search box
  useEffect(() => {
    const style = document.createElement('style');
    style.innerHTML = `
      /* ensure wrapper is positioned */
      .dataTables_wrapper {
        position: relative;
      }
      /* make the built-in "Search:" sticky top-right */
      .dataTables_filter {
        position: sticky;
        top: 0;
        right: 0;
        background: #fff;
        z-index: 10;
        margin: 0.5rem 0;
        padding: 0.25rem 0.5rem;
      }
      /* keep the input at its default height/width */
      .dataTables_filter input {
        width: auto;
        display: inline-block;
      }
    `;
    document.head.appendChild(style);
    return () => {
      document.head.removeChild(style);
    };
  }, []);

  // Fetch associates
  useEffect(() => {
    const fetchAssociateData = async (m, y, p) => {
      setLoading(true);
      setError(null);
      try {
        const resp = await fetch(`${BACKEND_URL}/api/associate`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ month: m, year: y, projectId: p }),
        });
        if (!resp.ok) {
          const msg = await resp.text();
          throw new Error(msg || resp.statusText);
        }
        const data = await resp.json();
        const list = Array.isArray(data) ? data : [data].filter(Boolean);
        setAssociates(list);

        if (list.length > 0) {
          const first = list[0];
          setActiveProjectName(first.projectName || p);
          setActiveAccountId(first.accountId || activeAccountId);
          setActiveAccountName(first.accountName || activeAccountName);
        }
      } catch (e) {
        setError(`Failed to load associate data: ${e.message}`);
      } finally {
        setLoading(false);
      }
    };

    // Derive context from location or sessionStorage
    let m = location.state?.month;
    let y = location.state?.year;
    let p = urlProjectId;
    let aId   = location.state?.accountId;
    let aName = location.state?.accountName;
    let pName = location.state?.projectName;

    if (!m || !y || !aId || !pName) {
      m     = parseInt(sessionStorage.getItem('lastFetchedAssociateMonth'), 10);
      y     = parseInt(sessionStorage.getItem('lastFetchedAssociateYear'), 10);
      p     = sessionStorage.getItem('lastFetchedAssociateProjectId');
      pName = sessionStorage.getItem('lastFetchedAssociateProjectName');
      aId   = sessionStorage.getItem('lastFetchedAssociateAccountId');
      aName = sessionStorage.getItem('lastFetchedAssociateAccountName');
    }

    if (m && y && p && aId && pName) {
      if (
        m !== activeMonth ||
        y !== activeYear ||
        p !== activeProjectId ||
        associates.length === 0
      ) {
        setActiveMonth(m);
        setActiveYear(y);
        setActiveProjectId(p);
        setActiveProjectName(pName);
        setActiveAccountId(aId);
        setActiveAccountName(aName);

        sessionStorage.setItem('lastFetchedAssociateMonth', m.toString());
        sessionStorage.setItem('lastFetchedAssociateYear', y.toString());
        sessionStorage.setItem('lastFetchedAssociateProjectId', p);
        sessionStorage.setItem('lastFetchedAssociateProjectName', pName);
        sessionStorage.setItem('lastFetchedAssociateAccountId', aId);
        sessionStorage.setItem('lastFetchedAssociateAccountName', aName);

        fetchAssociateData(m, y, p);
      } else {
        setLoading(false);
      }
    } else {
      setError('Missing associate context. Please go back.');
      setLoading(false);
    }
  }, [
    location.state,
    urlProjectId,
    activeMonth,
    activeYear,
    activeProjectId,
    associates.length,
  ]);

  // Initialize DataTable + variance filter
  useEffect(() => {
    if (!loading && tableRef.current) {
      const $tbl = $(tableRef.current);
      if ($.fn.DataTable.isDataTable($tbl)) {
        dataTableInstance.current.destroy();
      }

      // clear old filters
      $.fn.dataTable.ext.search.pop();
      if (filterOption === 'varianceAboveZero') {
        $.fn.dataTable.ext.search.push((settings, data) => parseFloat(data[7]) !== 0);
      }

      dataTableInstance.current = $tbl.DataTable({
        paging: true,
        searching: true,     // restore built-in search
        ordering: false,
        info: true,
        autoWidth: false,
        dom:
          '<"d-flex justify-content-between align-items-center mb-3"<"mb-2"l><"mb-2"f>>' +
          't' +
          '<"d-flex justify-content-between align-items-center mt-3"<"mb-2"i><"mb-2"p>>',
        language: { search: "Search:", lengthMenu: "Show _MENU_ entries" },
      });
    }
    return () => {
      if (dataTableInstance.current) {
        dataTableInstance.current.destroy();
        dataTableInstance.current = null;
      }
      $.fn.dataTable.ext.search.pop();
    };
  }, [loading, associates, filterOption]);

  // Handlers
  const handleFilterChange = (e) => setFilterOption(e.target.value);
  const handleNotifyAll   = () => console.log('Notify all');
  const handleDownload    = () => console.log('Download data');
  
  // breadcrumbs...
  const breadcrumbPath = [
    { name: 'PMO Dashboard', page: '' },
    { name: 'Revenue Forecast - Early View', page: 'upload' },
    { name: 'Account Level', page: 'accounts' },
    {
      name: `Projects (${activeAccountName})`,
      page: `accounts/${activeAccountId}/projects`
    },
    {
      name: `Associates (${activeProjectName})`,
      page: `projects/${activeProjectId}/associates`
    }
  ];

  if (loading) {
    return (
      <div className="min-vh-100 d-flex justify-content-center align-items-center">
        <div className="spinner-border text-primary" role="status" />
        <span className="ms-3 text-primary">Loading Associate Data...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-vh-100 d-flex flex-column justify-content-center align-items-center text-danger">
        <p className="fs-4">Error: {error}</p>
        <button
          className="btn btn-primary mt-3"
          onClick={() => navigate(`/accounts/${activeAccountId}/projects`)}
        >
          Go to Project Level
        </button>
      </div>
    );
  }

  return (
    <div className="px-3 py-4">
      <div className="card shadow p-4 mx-auto" style={{ maxWidth: 1100 }}>
        <Breadcrumbs path={breadcrumbPath} />

        {/* Header & Controls */}
        <div className="d-flex flex-column gap-3 mb-4">
          <h2 className="text-center text-md-start fw-semibold">
            ðŸ‘¤ Associate Details â€“ Project: {activeProjectName}
          </h2>
          <div className="d-flex flex-wrap justify-content-between align-items-center gap-3">
            {/* Variance Filter */}
            <div className="d-flex align-items-center">
              <Filter size={18} className="me-1 text-blue-500" />
              <select
                className="form-select"
                value={filterOption}
                onChange={handleFilterChange}
              >
                <option value="showAll">Show All Variance</option>
                <option value="varianceAboveZero">Variance â‰  0</option>
              </select>
            </div>

            {/* Actions */}
            <div className="d-flex gap-2 ms-auto">
              <button
                className="btn btn-outline-info d-flex align-items-center"
                onClick={handleNotifyAll}
              >
                <Bell size={18} className="me-2" /> Notify All
              </button>
              <button
                className="btn btn-outline-success d-flex align-items-center"
                onClick={handleDownload}
              >
                <Download size={18} className="me-2" /> Download Data
              </button>
            </div>
          </div>
        </div>

        {/* Scrollable table with sticky built-in search */}
        <div
          className="table-responsive rounded shadow-sm"
          style={{ overflowX: 'auto' }}
        >
          <table
            ref={tableRef}
            className="display table table-hover table-borderless align-middle mb-0 w-100"
            style={{ minWidth: 1200 }}
          >
            <thead style={{ background: 'linear-gradient(to right, #0ea5e9, #2563eb)', color: '#fff' }}>
              <tr>
                <th>Associate ID</th>
                <th>Associate Name</th>
                <th>Project ID</th>
                <th>Project Name</th>
                <th>PM ID</th>
                <th>Company Hours</th>
                <th>Client Hours</th>
                <th>Variance Hours</th>
                <th>Actual Revenue</th>
                <th>Associate Rate</th>
                <th>Notify</th>
                <th>View Daily</th>
              </tr>
            </thead>
            <tbody>
              {(filterOption === 'varianceAboveZero'
                ? associates.filter(a => a.varianceHoursMonthly !== 0)
                : associates
              ).map(associate => (
                <tr key={associate.associateId}>
                  <td>{associate.associateId}</td>
                  <td>{associate.associateName}</td>
                  <td>{associate.projectId}</td>
                  <td>{associate.projectName}</td>
                  <td>{associate.esaID}</td>
                  <td>{associate.totalCompanyHoursMonthly}</td>
                  <td>{associate.totalClientHoursMonthly}</td>
                  <td>
                    <span className={`px-2 py-0.5 rounded-full text-xs ${
                      associate.varianceHoursMonthly > 0
                        ? 'bg-red-100 text-red-800'
                        : associate.varianceHoursMonthly < 0
                        ? 'bg-green-100 text-green-800'
                        : 'bg-slate-100 text-slate-700'
                    }`}>
                      {associate.varianceHoursMonthly}
                    </span>
                  </td>
                  <td>{formatCurrency(associate.actualRevenue)}</td>
                  <td>{formatCurrency(associate.associateRtRate)}</td>
                  <td>
                    <button className="btn btn-sm btn-outline-info">
                      <Bell size={16} />
                    </button>
                  </td>
                  <td>
                    <button
                      className="btn btn-sm btn-outline-primary"
                      onClick={() =>
                        navigate(
                          `/projects/${associate.projectId}/associates/${associate.associateId}/daily`,
                          {
                            state: {
                              month: activeMonth,
                              year: activeYear,
                              projectId: associate.projectId,
                              projectName: associate.projectName,
                              accountId: activeAccountId,
                              accountName: activeAccountName,
                              associateId: associate.associateId,
                              associateName: associate.associateName,
                            },
                          }
                        )
                      }
                    >
                      <Search size={16} />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AssociateLevel;
