import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Container,
  Table,
  Alert,
  Spinner
} from 'react-bootstrap';
import { useLocation, useNavigate } from 'react-router-dom';
import Breadcrumbs from '../Breadcrumbs'; // Import the Breadcrumbs component

const SbuLevel = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { month, year } = location.state || {}; // Get month and year from the navigation state

  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true); // Start with loading state true
  const [error, setError] = useState('');

  // Function to convert month number to month name
  const getMonthName = (monthNumber) => {
    const date = new Date();
    date.setMonth(monthNumber - 1);
    return date.toLocaleString('en-US', { month: 'long' }).toLowerCase();
  };

  useEffect(() => {
    // Only fetch data if month and year are available
    if (month && year) {
      const fetchSbu = async () => {
        setLoading(true);
        setError('');
        try {
          const payload = {
            month: parseInt(month, 10),
            year: parseInt(year, 10),
            monthNameParam: getMonthName(month)
          };
          const resp = await axios.post(
            'http://localhost:8081/api/sbu',
            payload
          );
          setData(resp.data);
        } catch (err) {
          setError(err.response?.data || err.message);
        } finally {
          setLoading(false);
        }
      };
      fetchSbu();
    } else {
      // If no state is passed, show an error and stop loading
      setError("Month and year data are missing. Please go back and select a month.");
      setLoading(false);
    }
  }, [month, year]); // Re-run effect when month or year changes

  const breadcrumbPath = [
    { name: 'SBU Level', page: 'sbu' }
  ];

  return (
    <Container className="my-4">
      <Breadcrumbs path={breadcrumbPath} />
      <button onClick={() => navigate(-1)} className="btn btn-link text-start text-dark mb-4 p-0">
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-chevron-left me-2">
              <polyline points="15 18 9 12 15 6"></polyline>
          </svg>
          Back
      </button>
      <h2>SBU Revenue Summary for {getMonthName(month)} {year}</h2>
      
      {loading && (
        <div className="d-flex justify-content-center align-items-center my-5">
          <Spinner animation="border" role="status" className="me-2">
            <span className="visually-hidden">Loading...</span>
          </Spinner>
          <span>Loading data...</span>
        </div>
      )}

      {error && !loading && <Alert variant="danger">{error}</Alert>}

      {!loading && !error && data.length > 0 && (
        <Table striped bordered hover responsive>
          <thead>
            <tr>
              {Object.keys(data[0]).map(col => (
                <th key={col}>{col}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {data.map((row, i) => (
              <tr key={i}>
                {Object.values(row).map((val, j) => (
                  <td key={j}>{val}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </Table>
      )}

      {!loading && !error && data.length === 0 && (
          <Alert variant="info" className="mt-4">No data found for the selected period.</Alert>
      )}
    </Container>
  );
};

export default SbuLevel;
