// src/components/ProjectTypeLevel.js

import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import $ from 'jquery';
import 'datatables.net';
import 'datatables.net-dt/css/dataTables.dataTables.min.css';
import Breadcrumbs from '../Breadcrumbs';
import commonData from '../../data/commonData.json';

// Utility for currency formatting
const ProjectTypeLevel = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const tableRef = useRef(null);

  // grab state from navigate in AccountLevel
  const { accountId, month, year, monthName } = location.state || {};

  // local UI state
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // currency settings
  const {
    locale,
    currency,
    minimumFractionDigits,
    maximumFractionDigits,
  } = commonData.currencySettings;

  const formatCurrency = v =>
    v.toLocaleString(locale, {
      style: 'currency',
      currency,
      minimumFractionDigits,
      maximumFractionDigits,
    });

  // load Poppins
  useEffect(() => {
    const link = document.createElement('link');
    link.href =
      'https://fonts.googleapis.com/css2?family=Poppins:wght@400;600;700&display=swap';
    link.rel = 'stylesheet';
    document.head.appendChild(link);
    document.body.style.fontFamily = "'Poppins', sans-serif";
  }, []);

  // fetch data
  useEffect(() => {
    if (!accountId || !month || !year) {
      setError('Missing account or date info. Go back.');
      setLoading(false);
      return;
    }

    const fetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        const resp = await fetch(
          'http://localhost:8081/api/projectâ€typeâ€level',
          {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              accId: accountId,
              month,
              year,
            }),
          }
        );
        if (!resp.ok) {
          const text = await resp.text();
          throw new Error(text || resp.statusText);
        }
        const data = await resp.json();
        setRows(Array.isArray(data) ? data : []);
      } catch (e) {
        console.error(e);
        setError('Failed to load projectâ€type data: ' + e.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [accountId, month, year]);

  // initialize & destroy DataTable
  useEffect(() => {
    if (!loading && rows.length && tableRef.current) {
      const $tbl = $(tableRef.current);
      if ($.fn.DataTable.isDataTable($tbl)) {
        $tbl.DataTable().destroy();
      }
      $tbl.DataTable({
        paging: true,
        searching: true,
        ordering: true,
        info: true,
        autoWidth: false,
      });
    }
    return () => {
      if (tableRef.current) {
        const $tbl = $(tableRef.current);
        if ($.fn.DataTable.isDataTable($tbl)) {
          $tbl.DataTable().destroy();
        }
      }
    };
  }, [loading, rows]);

  const breadcrumbPath = [
    { name: 'PMO Dashboard', page: '' },
    { name: 'Revenue Forecast â€“ Early View', page: 'upload' },
    { name: 'Account Level', page: 'accounts' },
    { name: 'Project Type Level', page: '' },
  ];

  return (
    <div
      className="min-vh-100 d-flex flex-column align-items-center px-3 py-4"
      style={{
        background: 'linear-gradient(to bottom right, #f5f7fa, #e9ecef)',
      }}
    >
      <div
        className="card shadow p-4 p-md-5 w-100"
        style={{
          maxWidth: '1100px',
          backgroundColor: '#ffffffdd',
          borderRadius: '1rem',
        }}
      >
        <Breadcrumbs path={breadcrumbPath} />

        <h2 className="text-center mb-4 fw-semibold text-dark">
          ðŸ“Š Project Type Overview for {accountId}
          {monthName && year && (
            <span className="ms-2 text-primary">
              ({monthName} {year})
            </span>
          )}
        </h2>

        {loading ? (
          <div className="d-flex justify-content-center align-items-center">
            <div className="spinner-border text-primary" role="status" />
            <p className="ms-3 text-primary">Loading...</p>
          </div>
        ) : error ? (
          <div className="d-flex flex-column justify-content-center align-items-center text-danger">
            <p className="fs-4">{error}</p>
            <button
              className="btn btn-primary mt-3"
              onClick={() => navigate(-1)}
            >
              Go Back
            </button>
          </div>
        ) : (
          <div className="table-responsive rounded">
            <table
              ref={tableRef}
              className="display table table-hover align-middle table-bordered mb-0"
              style={{ width: '100%' }}
            >
              <thead
                className="text-white"
                style={{
                  background: 'linear-gradient(to right, #1d4ed8, #2563eb)',
                }}
              >
                <tr>
                  <th>Project Type</th>
                  <th>Total Revenue</th>
                  <th>Forecast Revenue</th>
                  <th>Difference</th>
                  <th className="text-center">Action</th>
                </tr>
              </thead>
              <tbody>
                {rows.map((r) => (
                  <tr key={r.projectType}>
                    <td>{r.projectType}</td>
                    <td>{formatCurrency(r.totalRevenue)}</td>
                    <td>{formatCurrency(r.forecastedRevenue)}</td>
                    <td
                      className={
                        r.difference < 0 ? 'text-danger' : 'text-success'
                      }
                    >
                      {r.difference > 0 ? '+' : ''}
                      {r.difference.toFixed(2)}
                    </td>
                    <td className="text-center">
                      <button
                        className="btn btn-sm btn-outline-primary rounded-pill px-3"
                        onClick={() =>
                          navigate(
                            `/accounts/${accountId}/projectâ€type/${r.projectType}/projects`,
                            {
                              state: {
                                accountId,
                                projectType: r.projectType,
                                month,
                                year,
                                monthName,
                              },
                            }
                          )
                        }
                      >
                        View Projects
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProjectTypeLevel;
