import React, { useRef, useEffect, useState } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import $ from 'jquery';
import 'datatables.net';
import 'datatables.net-dt/css/dataTables.dataTables.min.css';
import Breadcrumbs from '../Breadcrumbs';
import commonData from '../../data/commonData.json';

const ProjectLevel = () => {
  const navigate = useNavigate();
  const { accountId: urlAccountId } = useParams();
  const location = useLocation();
  const tableRef = useRef(null);

  const [projects, setProjects] = useState([]);
  const [filteredProjects, setFilteredProjects] = useState([]);
  const [columnFilters, setColumnFilters] = useState({});

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [activeMonth, setActiveMonth] = useState(null);
  const [activeYear, setActiveYear] = useState(null);
  const [activeAccountId, setActiveAccountId] = useState(null);
  const [activeAccountName, setActiveAccountName] = useState('Loading...');
  const [activeSbu, setActiveSbu] = useState(null);
  const [activeProjectType, setActiveProjectType] = useState(null);

  const BACKEND_URL = 'http://localhost:8081';

  // format numbers as currency using settings
  const formatCurrency = (value = 0) =>
    value.toLocaleString(commonData.currencySettings.locale, {
      style: 'currency',
      currency: commonData.currencySettings.currency,
      minimumFractionDigits: commonData.currencySettings.minimumFractionDigits,
      maximumFractionDigits: commonData.currencySettings.maximumFractionDigits
    });

  // convert month number to name
  const getMonthName = (monthNumber) => {
    if (!monthNumber) return '';
    const date = new Date();
    date.setMonth(monthNumber - 1);
    return date.toLocaleString('en-US', { month: 'long' });
  };

  // download CSV of current filteredProjects
  const handleDownloadData = () => {
    if (!filteredProjects.length) return;

    const headers = [
      'Project ID',
      'Project Name',
      'Total Associates',
      'Company Hours',
      'Client Hours',
      'Variance Hours',
      'Revenue'
    ];

    const csvRows = filteredProjects.map(proj => [
      `"${proj.projectId}"`,
      `"${proj.projectName}"`,
      proj.totalAssociatesCount,
      proj.totalCompanyHours,
      proj.totalClientHours,
      proj.varianceHours,
      `"${formatCurrency(proj.revenue)}"`
    ].join(','));

    const csvContent = [headers.join(','), ...csvRows].join('\n');
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });

    const link = document.createElement('a');
    const url = URL.createObjectURL(blob);
    const monthName = getMonthName(activeMonth);
    const fileName = `Projects_Account_${activeAccountId}_${monthName}_${activeYear}.csv`;

    link.setAttribute('href', url);
    link.setAttribute('download', fileName);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  // returns sorted dropdown options including "All"
  const getUniqueOptions = (columnKey) => {
    const items = projects.map(p => p[columnKey]);
    const uniques = Array.from(new Set(items)).sort();
    return ['All', ...uniques];
  };

  const handleFilterChange = (col, val) => {
    setColumnFilters(prev => ({
      ...prev,
      [col]: val === 'All' ? null : val
    }));
  };

  // enhanced filtering: numeric equality or substring match
  const applyFilters = (rows, filters) => {
    return rows.filter(row =>
      Object.entries(filters).every(([col, filterVal]) => {
        if (filterVal == null) return true;

        const cell = row[col];
        if (cell == null) return false;

        // numeric compare if both can parse as numbers
        if (typeof cell === 'number' && !isNaN(filterVal)) {
          return cell === Number(filterVal);
        }

        // otherwise case-insensitive substring match
        return String(cell)
          .toLowerCase()
          .includes(String(filterVal).toLowerCase());
      })
    );
  };

  // load Google Font once
  useEffect(() => {
    const link = document.createElement('link');
    link.href = 'https://fonts.googleapis.com/css2?family=Poppins:wght@400;600;700&display=swap';
    link.rel = 'stylesheet';
    document.head.appendChild(link);
    document.body.style.fontFamily = "'Poppins', sans-serif";
  }, []);

  // fetch data when parameters change
  useEffect(() => {
    const fetchProjectData = async (month, year, accId, projectType) => {
      setLoading(true);
      setError(null);

      try {
        const resp = await fetch(`${BACKEND_URL}/api/project`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ month, year, accId, projectType })
        });

        if (!resp.ok) {
          const msg = await resp.text();
          throw new Error(`status ${resp.status}: ${msg || resp.statusText}`);
        }

        const data = await resp.json();
        const list = Array.isArray(data) ? data : [data].filter(Boolean);

        console.log('üîç fetched projects:', list);
        setProjects(list);

        if (list[0]?.accountName) {
          setActiveAccountName(list[0].accountName);
        } else {
          setActiveAccountName(
            sessionStorage.getItem('lastFetchedProjectAccountName') || accId
          );
        }
      } catch (e) {
        console.error(e);
        setError(`Failed to load project data: ${e.message}`);
      } finally {
        setLoading(false);
      }
    };

    // derive parameters from location.state, URL, sessionStorage
    let m = location.state?.month || sessionStorage.getItem('lastFetchedProjectMonth');
    let y = location.state?.year || sessionStorage.getItem('lastFetchedProjectYear');
    let sbu = location.state?.sbu || sessionStorage.getItem('lastFetchedProjectSbu');
    let type = location.state?.projectType || sessionStorage.getItem('lastFetchedProjectType');
    let accName = location.state?.accountName || sessionStorage.getItem('lastFetchedProjectAccountName');

    m = m ? parseInt(m, 10) : null;
    y = y ? parseInt(y, 10) : null;

    if (m && y && urlAccountId) {
      // only fetch if any key param changed
      if (
        m !== activeMonth ||
        y !== activeYear ||
        urlAccountId !== activeAccountId ||
        sbu !== activeSbu ||
        type !== activeProjectType
      ) {
        setActiveMonth(m);
        setActiveYear(y);
        setActiveAccountId(urlAccountId);
        setActiveSbu(sbu);
        setActiveProjectType(type);
        if (accName) setActiveAccountName(accName);

        sessionStorage.setItem('lastFetchedProjectMonth', m.toString());
        sessionStorage.setItem('lastFetchedProjectYear', y.toString());
        sessionStorage.setItem('lastFetchedProjectAccountId', urlAccountId);
        if (accName) sessionStorage.setItem('lastFetchedProjectAccountName', accName);
        if (sbu) sessionStorage.setItem('lastFetchedProjectSbu', sbu);
        if (type) sessionStorage.setItem('lastFetchedProjectType', type);

        fetchProjectData(m, y, urlAccountId, type);
      } else {
        setLoading(false);
      }
    } else {
      setError('Missing month, year, or Account ID. Please navigate here with all parameters.');
      setLoading(false);
    }
  }, [
    location.state,
    urlAccountId,
    activeMonth,
    activeYear,
    activeAccountId,
    activeSbu,
    activeProjectType
  ]);

  // re-apply filters whenever projects or filters change
  useEffect(() => {
    setFilteredProjects(applyFilters(projects, columnFilters));
  }, [projects, columnFilters]);

  // initialize / destroy DataTable when filteredProjects update
  useEffect(() => {
    if (!loading && filteredProjects.length && tableRef.current) {
      const $tbl = $(tableRef.current);
      if ($.fn.DataTable.isDataTable($tbl)) {
        $tbl.DataTable().destroy();
      }
      $tbl.DataTable({
        paging: true,
        searching: true,
        ordering: false,
        info: true,
        autoWidth: false
      });
    }

    return () => {
      if (tableRef.current) {
        const $tbl = $(tableRef.current);
        if ($.fn.DataTable.isDataTable($tbl)) {
          $tbl.DataTable().destroy();
        }
      }
    };
  }, [loading, filteredProjects]);

  // build breadcrumb trail
  const breadcrumbPath = [
    { name: 'PMO Dashboard', page: '' },
    { name: 'Revenue Forecast - Early View', page: 'upload' },
    ...(activeSbu ? [{ name: `${activeSbu} SBU Level`, page: 'sbu', state: { month: activeMonth, year: activeYear, sbu: activeSbu } }] : []),
    { name: 'Account Level', page: 'accounts', state: { month: activeMonth, year: activeYear, sbu: activeSbu } },
    ...(activeProjectType ? [{ name: `${activeProjectType} Project Type`, page: `accounts/${activeAccountId}/project-types`, state: { month: activeMonth, year: activeYear, sbu: activeSbu, accId: activeAccountId } }] : []),
    { name: `Projects (${activeAccountName})`, page: `accounts/${activeAccountId}/projects` }
  ].filter(Boolean);

  if (loading) {
    return (
      <div className="min-vh-100 d-flex justify-content-center align-items-center">
        <div className="spinner-border text-primary" role="status" />
        <p className="ms-3 text-primary">Loading Project Data...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-vh-100 d-flex flex-column justify-content-center align-items-center text-danger">
        <p className="fs-4">Error: {error}</p>
        <button
          className="btn btn-primary mt-3"
          onClick={() =>
            navigate('/accounts', { state: { month: activeMonth, year: activeYear, sbu: activeSbu } })
          }
        >
          Go to Account Level
        </button>
      </div>
    );
  }

  return (
    <div
      className="min-vh-100 d-flex flex-column align-items-center px-3 py-4"
      style={{
        background: 'linear-gradient(to bottom right, #f5f7fa, #e9ecef)',
        fontFamily: "'Poppins', sans-serif'"
      }}
    >
      <div className="card shadow p-4 p-md-5 w-100" style={{ maxWidth: '1200px', backgroundColor: '#ffffffdd', borderRadius: '1rem' }}>
        <Breadcrumbs path={breadcrumbPath} />

        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2 className="mb-0 fw-semibold text-dark">
            üìÅ Project Level Overview {activeAccountName && `for ${activeAccountName}`}
          </h2>
          {projects.length > 0 && (
            <button
              className="btn btn-outline-success rounded-pill px-3"
              onClick={handleDownloadData}
              title="Download Project Data"
            >
              <i className="bi bi-download me-2" /> Download Data
            </button>
          )}
        </div>

        <div className="table-responsive rounded">
          <table
            key={JSON.stringify(columnFilters)}
            ref={tableRef}
            className="display table table-hover align-middle table-borderless mb-0"
            style={{ width: '100%' }}
          >
            <thead className="text-white" style={{ background: 'linear-gradient(to right, #3b82f6, #2563eb)' }}>
              <tr>
                {[
                  { label: 'Project ID', key: 'projectId' },
                  { label: 'Project Name', key: 'projectName' },
                  { label: 'Total Associates', key: 'totalAssociatesCount' },
                  { label: 'Company Hours', key: 'totalCompanyHours' },
                  { label: 'Client Hours', key: 'totalClientHours' },
                  { label: 'Variance Hours', key: 'varianceHours' },
                  { label: 'Revenue', key: 'revenue' }
                ].map(col => (
                  <th key={col.key}>
                    {col.label}
                    <select
                      className="form-select form-select-sm mt-1"
                      value={columnFilters[col.key] ?? 'All'}
                      onChange={e => handleFilterChange(col.key, e.target.value)}
                    >
                      {getUniqueOptions(col.key).map(opt => (
                        <option key={opt} value={opt}>{opt}</option>
                      ))}
                    </select>
                  </th>
                ))}
                <th className="text-center">View</th>
              </tr>
            </thead>
            <tbody>
              {filteredProjects.map(proj => (
                <tr key={proj.projectId + '-' + proj.totalCompanyHours}>
                  <td>{proj.projectId}</td>
                  <td>{proj.projectName}</td>
                  <td>{proj.totalAssociatesCount}</td>
                  <td>{proj.totalCompanyHours}</td>
                  <td>{proj.totalClientHours}</td>
                  <td>{proj.varianceHours}</td>
                  <td>{formatCurrency(proj.revenue)}</td>
                  <td className="text-center">
                    <button
                      className="btn btn-sm btn-outline-primary rounded-circle"
                      onClick={() =>
                        navigate(`/projects/${proj.projectId}/associates`, {
                          state: {
                            projectId: proj.projectId,
                            projectName: proj.projectName,
                            accountId: proj.accountId,
                            accountName: proj.accountName,
                            month: activeMonth,
                            year: activeYear,
                            sbu: activeSbu,
                            projectType: activeProjectType
                          }
                        })
                      }
                      title="View Associates"
                    >
                      üîç
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default ProjectLevel;
