import React, { useRef, useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import $ from 'jquery';
import 'datatables.net';
import 'datatables.net-dt/css/dataTables.dataTables.min.css';
import Breadcrumbs from '../Breadcrumbs';
import commonData from '../../data/commonData.json';

// Utility to convert month number to month name
const getMonthName = (monthNumber) => {
  const date = new Date();
  date.setMonth(monthNumber - 1);
  return date.toLocaleString('en-US', { month: 'long' });
};

const AccountLevel = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const tableRef = useRef(null);

  // UI state
  const [accounts, setAccounts] = useState([]);
  const [commentsMap, setCommentsMap] = useState({});
  const [modalVisible, setModalVisible] = useState(false);
  const [currentAccountId, setCurrentAccountId] = useState(null);
  const [commentInput, setCommentInput] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [notification, setNotification] = useState({ message: '', type: '' });

  // Active filters
  const [activeMonth, setActiveMonth] = useState(null);
  const [activeYear, setActiveYear] = useState(null);
  const [activeMonthName, setActiveMonthName] = useState(null);
  const [activeSbu, setActiveSbu] = useState(null);

  // Column filters & options
  const [columnFilters, setColumnFilters] = useState({
    accountId: new Set(),
    accountName: new Set(),
    totalProjects: new Set(),
    totalRevenue: new Set(),
    forecastNetRevenue: new Set(),
    difference: new Set(),
  });
  const [filterOptions, setFilterOptions] = useState({});
  const [visibleFilterDropdown, setVisibleFilterDropdown] = useState(null);

  const BACKEND_URL = 'http://localhost:8081';

  // Currency formatter
  const {
    locale,
    currency,
    minimumFractionDigits,
    maximumFractionDigits,
  } = commonData.currencySettings;
  const formatCurrency = (value = 0) =>
    value.toLocaleString(locale, {
      style: 'currency',
      currency,
      minimumFractionDigits,
      maximumFractionDigits,
    });

  // Load Poppins font
  useEffect(() => {
    const link = document.createElement('link');
    link.href =
      'https://fonts.googleapis.com/css2?family=Poppins:wght@400;600;700&display=swap';
    link.rel = 'stylesheet';
    document.head.appendChild(link);
    document.body.style.fontFamily = "'Poppins', sans-serif";
  }, []);

  // Fetch account data when filters change
  useEffect(() => {
    const fetchAccountData = async (month, year, monthNameParam, sbu) => {
      setLoading(true);
      setError(null);
      setAccounts([]);
      setFilterOptions({});
      setColumnFilters({
        accountId: new Set(),
        accountName: new Set(),
        totalProjects: new Set(),
        totalRevenue: new Set(),
        forecastNetRevenue: new Set(),
        difference: new Set(),
      });

      try {
        const resp = await fetch(`${BACKEND_URL}/api/account`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ month, year, monthNameParam, sbu }),
        });
        if (!resp.ok) {
          const txt = await resp.text();
          throw new Error(txt || resp.statusText);
        }
        const data = await resp.json();
        const list = Array.isArray(data) ? data : [data].filter(Boolean);
        setAccounts(list);

        // initialize comments map
        const initMap = {};
        list.forEach((acc) => {
          if (acc.accountComment) {
            initMap[acc.accountId] = acc.accountComment;
          }
        });
        setCommentsMap(initMap);
      } catch (e) {
        console.error(e);
        setError(`Failed to load accounts: ${e.message}`);
        setNotification({ message: `Failed to load accounts: ${e.message}`, type: 'error' });
      } finally {
        setLoading(false);
      }
    };

    // Read filters from state, sessionStorage or URL
    const qs = new URLSearchParams(location.search);
    let m = location.state?.month;
    let y = location.state?.year;
    let s = location.state?.sbu;

    if (!m) {
      m = sessionStorage.getItem('lastFetchedAccountMonth') ?? qs.get('month');
    }
    if (!y) {
      y = sessionStorage.getItem('lastFetchedAccountYear') ?? qs.get('year');
    }
    if (!s) {
      s = sessionStorage.getItem('lastFetchedAccountSbu') ?? qs.get('sbu');
    }

    m = m ? parseInt(m, 10) : null;
    y = y ? parseInt(y, 10) : null;

    if (m && y && s) {
      const mn = getMonthName(m);
      if (
        m !== activeMonth ||
        y !== activeYear ||
        s !== activeSbu ||
        accounts.length === 0
      ) {
        setActiveMonth(m);
        setActiveYear(y);
        setActiveMonthName(mn);
        setActiveSbu(s);

        sessionStorage.setItem('lastFetchedAccountMonth', m.toString());
        sessionStorage.setItem('lastFetchedAccountYear', y.toString());
        sessionStorage.setItem('lastFetchedAccountSbu', s);

        fetchAccountData(m, y, mn, s);
      }
    } else {
      setError(
        'Missing month, year or SBU. Please navigate here with those parameters.'
      );
      setLoading(false);
    }
  }, [location.state, location.search]);

  // Build filter options when data loads
  useEffect(() => {
    if (!accounts.length) return;

    const options = {};
    const cols = [
      'accountId',
      'accountName',
      'totalProjects',
      'totalRevenue',
      'forecastNetRevenue',
      'difference',
    ];

    cols.forEach((col) => {
      if (col === 'totalRevenue' || col === 'forecastNetRevenue' || col === 'difference') {
        options[col] = [
          ...new Set(accounts.map((acc) => formatCurrency(acc[col]))),
        ].sort();
      } else {
        options[col] = [...new Set(accounts.map((acc) => acc[col]))].sort();
      }
    });

    setFilterOptions(options);
  }, [accounts, formatCurrency]);

  // Initialize and destroy DataTable with custom filter logic
  useEffect(() => {
    const $tbl = $(tableRef.current);
    if (!loading && tableRef.current) {
      if ($.fn.DataTable.isDataTable($tbl)) {
        $tbl.DataTable().destroy();
        $.fn.dataTable.ext.search.pop();
      }

      // custom filter fn
      const customFilter = (settings, data, idx) => {
        const row = accounts[idx];
        for (const key in columnFilters) {
          const sel = columnFilters[key];
          if (sel.size) {
            let val = row[key];
            if (
              key === 'totalRevenue' ||
              key === 'forecastNetRevenue' ||
              key === 'difference'
            ) {
              val = formatCurrency(val);
            }
            if (!sel.has(val)) return false;
          }
        }
        return true;
      };

      $.fn.dataTable.ext.search.push(customFilter);

      const dt = $tbl.DataTable({
        paging: true,
        searching: false,
        ordering: false,
        info: true,
        autoWidth: false,
      });

      dt.draw();
    }

    return () => {
      if (tableRef.current && $.fn.DataTable.isDataTable($(tableRef.current))) {
        $(tableRef.current).DataTable().destroy();
        $.fn.dataTable.ext.search.pop();
      }
    };
  }, [loading, accounts, columnFilters, formatCurrency]);

  // Download current view as .xls
  const handleDownloadExcel = () => {
    const $tbl = $(tableRef.current);
    let headers = [];
    let rows = [];

    if ($.fn.DataTable.isDataTable($tbl)) {
      const dt = $tbl.DataTable();
      headers = $tbl
        .find('thead th')
        .slice(0, 6)
        .map(function () {
          return $(this).text().trim().replace(/‚â°/g, '').trim();
        })
        .get();

      const nodes = dt
        .rows({ search: 'applied', filter: 'applied' })
        .nodes()
        .toArray();

      rows = nodes.map((tr) =>
        Array.from(tr.cells)
          .slice(0, 6)
          .map((td) => td.textContent.trim())
      );
    } else {
      headers = [
        'Account ID',
        'Account Name',
        'Total Projects',
        'Total Revenue',
        'Forecast Revenue',
        'Revenue Difference',
      ];
      rows = accounts.map((acc) => [
        acc.accountId,
        acc.accountName,
        acc.totalProjects,
        formatCurrency(acc.totalRevenue),
        formatCurrency(acc.forecastNetRevenue),
        formatCurrency(acc.difference),
      ]);
    }

    const theadHtml =
      '<tr>' +
      headers
        .map((h) => `<th>${h.replace(/&/g, '&amp;').replace(/</g, '&lt;')}</th>`)
        .join('') +
      '</tr>';
    const tbodyHtml = rows
      .map(
        (r) =>
          '<tr>' +
          r
            .map((v) => `<td>${String(v).replace(/&/g, '&amp;').replace(/</g, '&lt;')}</td>`)
            .join('') +
          '</tr>'
      )
      .join('');

    const html =
      `<html xmlns:o="urn:schemas-microsoft-com:office:office" ` +
      `xmlns:x="urn:schemas-microsoft-com:office:excel" ` +
      `xmlns="http://www.w3.org/TR/REC-html40">` +
      `<head>` +
      `<!--[if gte mso 9]><xml><x:ExcelWorkbook><x:ExcelWorksheets>` +
      `<x:ExcelWorksheet><x:Name>AccountLevel</x:Name>` +
      `<x:WorksheetOptions><x:DisplayGridlines/></x:WorksheetOptions>` +
      `</x:ExcelWorksheet></x:ExcelWorksheets></x:ExcelWorkbook></xml><![endif]-->` +
      `</head><body><table border="1"><thead>${theadHtml}</thead><tbody>${tbodyHtml}</tbody></table></body></html>`;

    const blob = new Blob([html], { type: 'application/vnd.ms-excel;charset=utf-8;' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    const fileName = `AccountLevel_${activeSbu || 'All'}_${activeMonthName || ''}${activeYear || ''}.xls`;
    a.href = url;
    a.download = fileName.replace(/\s+/g, '_');
    document.body.appendChild(a);
    a.click();
    a.remove();
    URL.revokeObjectURL(url);
  };

  // Comment modal handlers
  const openCommentModal = (accId) => {
    setCurrentAccountId(accId);
    setCommentInput(commentsMap[accId] || '');
    setModalVisible(true);
  };
  const saveComment = async () => {
    try {
      const dto = {
        accId: currentAccountId,
        month: activeMonth,
        year: activeYear,
        comment: commentInput,
      };
      const resp = await fetch(`${BACKEND_URL}/api/account/comment`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(dto),
      });
      if (!resp.ok) throw new Error(await resp.text());
      setCommentsMap((m) => ({ ...m, [currentAccountId]: commentInput }));
      setAccounts((list) =>
        list.map((acc) =>
          acc.accountId === currentAccountId
            ? { ...acc, accountComment: commentInput }
            : acc
        )
      );
      setModalVisible(false);
      setNotification({ message: 'Comment saved successfully!', type: 'success' });
      setTimeout(() => setNotification({ message: '', type: '' }), 3000);
    } catch (e) {
      setNotification({ message: 'Error saving comment: ' + e.message, type: 'error' });
    }
  };

  // Column filter handlers
  const handleFilterChange = (col, val, checked) => {
    setColumnFilters((prev) => {
      const next = new Set(prev[col]);
      if (checked) next.add(val);
      else next.delete(val);
      return { ...prev, [col]: next };
    });
  };
  const toggleFilterDropdown = (col) =>
    setVisibleFilterDropdown((v) => (v === col ? null : col));
  const clearFilter = (col) =>
    setColumnFilters((prev) => ({ ...prev, [col]: new Set() }));

  // Breadcrumbs
  const breadcrumbPath = [
    { name: 'PMO Dashboard', page: '' },
    { name: 'Revenue Forecast - Early View', page: 'upload' },
    ...(activeSbu
      ? [
          {
            name: `${activeSbu} SBU Level`,
            page: 'sbu',
            state: {
              month: activeMonth,
              year: activeYear,
              monthName: activeMonthName,
              sbu: activeSbu,
            },
          },
        ]
      : []),
    { name: 'Account Level', page: 'accounts' },
  ].filter(Boolean);

  // Render header cell with filter
  const renderFilterableHeader = (columnKey, headerText) => (
    <th style={{ position: 'relative', paddingRight: '30px' }}>
      {headerText}
      <button
        className="btn btn-sm text-dark"
        onClick={() => toggleFilterDropdown(columnKey)}
        style={{
          position: 'absolute',
          right: '5px',
          top: '50%',
          transform: 'translateY(-50%)',
          fontSize: '1.2rem',
          padding: '0 5px',
          backgroundColor: 'transparent',
          border: 'none',
        }}
        title={`Filter by ${headerText}`}
      >
        ‚â°
      </button>

      {visibleFilterDropdown === columnKey && (
        <div
          className="filter-dropdown bg-white shadow rounded p-2"
          style={{
            position: 'absolute',
            zIndex: 1000,
            top: '100%',
            left: 0,
            width: '220px',
            maxHeight: '250px',
            overflowY: 'auto',
            border: '1px solid #ccc',
            boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
          }}
          onClick={(e) => e.stopPropagation()}
        >
          <div className="d-flex justify-content-between align-items-center mb-2">
            <span className="fw-bold">Filter {headerText}</span>
            <button
              className="btn btn-sm btn-outline-danger"
              onClick={() => clearFilter(columnKey)}
            >
              Clear
            </button>
          </div>

          {filterOptions[columnKey] && filterOptions[columnKey].length > 0 ? (
            filterOptions[columnKey].map((opt) => (
              <div key={opt} className="form-check">
                <input
                  type="checkbox"
                  className="form-check-input"
                  id={`filter-${columnKey}-${opt}`}
                  onChange={(e) =>
                    handleFilterChange(columnKey, opt, e.target.checked)
                  }
                  checked={columnFilters[columnKey].has(opt)}
                />
                <label
                  className="form-check-label"
                  htmlFor={`filter-${columnKey}-${opt}`}
                >
                  {opt}
                </label>
              </div>
            ))
          ) : (
            <p className="text-muted">No options available</p>
          )}
        </div>
      )}
    </th>
  );

  return (
    <div
      className="min-vh-100 d-flex flex-column align-items-center px-3 py-4"
      style={{
        background: 'linear-gradient(to bottom right, #f5f7fa, #e9ecef)',
        fontFamily: "'Poppins', sans-serif",
      }}
    >
      <div
        className="card shadow p-4 p-md-5 w-100"
        style={{
          maxWidth: '1100px',
          backgroundColor: '#ffffffdd',
          borderRadius: '1rem',
        }}
      >
        <Breadcrumbs path={breadcrumbPath} />

        <div className="d-flex flex-column flex-md-row justify-content-between align-items-md-center mb-3">
          <h2 className="mb-3 mb-md-0 fw-semibold text-dark">
            üè¢ Account Level Overview ‚Äì {activeSbu}
            {activeMonthName && activeYear && (
              <span className="ms-2 text-primary">
                ({activeMonthName} {activeYear})
              </span>
            )}
          </h2>
          <button
            type="button"
            onClick={handleDownloadExcel}
            disabled={loading || !!error}
            title="Download current table view as Excel"
            className="btn"
            style={{
              backgroundColor: '#ffffff',
              border: '1px solid #198754',
              color: '#198754',
            }}
          >
            Download
          </button>
        </div>

        {notification.message && (
          <div
            className={`alert alert-${
              notification.type === 'error' ? 'danger' : 'success'
            } mb-3`}
            role="alert"
          >
            {notification.message}
          </div>
        )}

        {loading ? (
          <div className="d-flex justify-content-center align-items-center">
            <div className="spinner-border text-primary" role="status" />
            <p className="ms-3 text-primary">Loading Account Data...</p>
          </div>
        ) : error ? (
          <div className="d-flex flex-column justify-content-center align-items-center text-danger">
            <p className="fs-4">Error: {error}</p>
            <button
              className="btn btn-primary mt-3"
              onClick={() => navigate('/upload')}
            >
              Go to Upload Page
            </button>
          </div>
        ) : (
          <div className="table-responsive rounded">
            <table
              ref={tableRef}
              className="display table table-hover align-middle table-bordered mb-0"
              style={{ width: '100%' }}
            >
              <thead
                className="text-white"
                style={{
                  background: 'linear-gradient(to right, #1d4ed8, #2563eb)',
                }}
              >
                <tr>
                  {renderFilterableHeader('accountId', 'Account ID')}
                  {renderFilterableHeader('accountName', 'Account Name')}
                  {renderFilterableHeader('totalProjects', 'Total Projects')}
                  {renderFilterableHeader('totalRevenue', 'Total Revenue')}
                  {renderFilterableHeader(
                    'forecastNetRevenue',
                    'Forecast Revenue'
                  )}
                  {renderFilterableHeader('difference', 'Revenue Difference')}
                  <th className="text-center">Comment</th>
                  <th className="text-center">View</th>
                </tr>
              </thead>
              <tbody>
                {accounts.map((acc) => (
                  <tr key={acc.accountId}>
                    <td>{acc.accountId}</td>
                    <td>{acc.accountName}</td>
                    <td>{acc.totalProjects}</td>
                    <td>{formatCurrency(acc.totalRevenue)}</td>
                    <td>{formatCurrency(acc.forecastNetRevenue)}</td>
                    <td>{formatCurrency(acc.difference)}</td>
                    <td className="text-center">
                      <button
                        className="btn btn-sm btn-outline-secondary"
                        onClick={() => openCommentModal(acc.accountId)}
                      >
                        üí¨
                      </button>
                    </td>
                    <td className="text-center">
                      <button
                        className="btn btn-sm btn-outline-primary rounded-circle"
                        onClick={() =>
                          navigate(
                            `/accounts/${acc.accountId}/project-types`,
                            {
                              state: {
                                accId: acc.accountId,
                                month: activeMonth,
                                year: activeYear,
                                monthName: activeMonthName,
                                sbu: activeSbu,
                              },
                            }
                          )
                        }
                        title="View Project Types"
                      >
                        üîç
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Comment Modal */}
      {modalVisible && (
        <div
          className="modal fade show d-block"
          style={{ backgroundColor: '#00000066' }}
        >
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">üí¨ Comment for {currentAccountId}</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setModalVisible(false)}
                />
              </div>
              <div className="modal-body">
                <textarea
                  className="form-control"
                  rows="4"
                  value={commentInput}
                  onChange={(e) => setCommentInput(e.target.value)}
                />
              </div>
              <div className="modal-footer">
                <button
                  className="btn btn-secondary"
                  onClick={() => setModalVisible(false)}
                >
                  Cancel
                </button>
                <button
                  className="btn btn-primary"
                  onClick={saveComment}
                  disabled={!commentInput.trim()}
                >
                  Save
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AccountLevel;
