import React, { useRef, useEffect, useState } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import $ from 'jquery';
import 'datatables.net';
import 'datatables.net-dt/css/dataTables.dataTables.min.css';
import Breadcrumbs from '../Breadcrumbs';
import commonData from '../../data/commonData.json';

const BACKEND_URL = 'http://localhost:8081';

const ProjectLevel = () => {
  const navigate = useNavigate();
  const { accountId: urlAccountId } = useParams();
  const location = useLocation();
  const tableRef = useRef(null);

  const [projects, setProjects] = useState([]);
  const [loading,  setLoading]  = useState(true);
  const [error,    setError]    = useState(null);
  const [activeMonth, setActiveMonth]       = useState(null);
  const [activeYear,  setActiveYear]        = useState(null);
  const [activeAccountId,   setActiveAccountId]   = useState(null);
  const [activeAccountName, setActiveAccountName] = useState('Loading...');
  const [activeSbu,         setActiveSbu]         = useState(null);
  const [activeProjectType, setActiveProjectType] = useState(null);
  const [columnFilters, setColumnFilters] = useState({});

  // Format a number as currency string
  const formatCurrency = (v = 0) =>
    v.toLocaleString(commonData.currencySettings.locale, {
      style:    'currency',
      currency: commonData.currencySettings.currency,
      minimumFractionDigits:
        commonData.currencySettings.minimumFractionDigits,
      maximumFractionDigits:
        commonData.currencySettings.maximumFractionDigits,
    });

  // Month name helper
  const getMonthName = (m) => {
    if (!m) return '';
    const d = new Date();
    d.setMonth(m - 1);
    return d.toLocaleString('en-US', { month: 'long' });
  };

  // Build unique options for a column‚Äôs <select>
  const getUniqueOptions = (columnKey) => {
    const raw = projects.map((p) =>
      columnKey === 'revenue' ? formatCurrency(p[columnKey]) : p[columnKey]
    );
    const uniq = Array.from(new Set(raw.map(String)));
    uniq.sort((a, b) => {
      const na = Number(a), nb = Number(b);
      return !isNaN(na) && !isNaN(nb)
        ? na - nb
        : a.localeCompare(b);
    });
    return ['All', ...uniq];
  };

  // Handle dropdown changes
  const handleFilterChange = (col, val) => {
    setColumnFilters((prev) => ({
      ...prev,
      [col]: val === 'All' ? null : val,
    }));
  };

  // Build and download CSV of currently filtered data
  const handleDownloadData = () => {
    if (!projects.length) return;
    const headers = [
      'Project ID',
      'Project Name',
      'Total Associates',
      'Company Hours',
      'Client Hours',
      'Variance Hours',
      'Revenue',
    ];
    // Apply same filters client‚Äêside
    const filtered = projects.filter((p) =>
      Object.entries(columnFilters).every(([col, val]) => {
        if (!val) return true;
        let cell = p[col];
        if (col === 'revenue') cell = formatCurrency(cell);
        return String(cell).trim().toLowerCase()
             === String(val).trim().toLowerCase();
      })
    );

    const rows = filtered.map((p) => [
      `"${p.projectId}"`,
      `"${p.projectName}"`,
      p.totalAssociatesCount,
      p.totalCompanyHours,
      p.totalClientHours,
      p.varianceHours,
      `"${formatCurrency(p.revenue)}"`,
    ].join(','));

    const csv = [headers.join(','), ...rows].join('\n');
    const blob = new Blob([csv], {
      type: 'text/csv;charset=utf-8;',
    });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = `Projects_Account_${activeAccountId}_${getMonthName(
      activeMonth
    )}_${activeYear}.csv`;
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  // Load Poppins font once
  useEffect(() => {
    const link = document.createElement('link');
    link.href =
      'https://fonts.googleapis.com/css2?family=Poppins:wght@400;600;700&display=swap';
    link.rel = 'stylesheet';
    document.head.appendChild(link);
    document.body.style.fontFamily = "'Poppins', sans-serif";
  }, []);

  // Fetch data whenever month/year/account change
  useEffect(() => {
    let m = location.state?.month;
    let y = location.state?.year;
    const acc = urlAccountId;
    let accName = location.state?.accountName;
    let sbu = location.state?.sbu;
    let pType = location.state?.projectType;

    // Fallback to sessionStorage
    if (!m)       m = sessionStorage.getItem('lastFetchedProjectMonth');
    if (!y)       y = sessionStorage.getItem('lastFetchedProjectYear');
    if (!accName) accName = sessionStorage.getItem('lastFetchedProjectAccountName');
    if (!sbu)     sbu = sessionStorage.getItem('lastFetchedProjectSbu');
    if (!pType)   pType = sessionStorage.getItem('lastFetchedProjectType');

    m = m ? parseInt(m, 10) : null;
    y = y ? parseInt(y, 10) : null;

    if (!(m && y && acc)) {
      setError(
        'Missing month, year, or Account ID. Please navigate here with all parameters.'
      );
      setLoading(false);
      return;
    }

    const shouldFetch =
      m !== activeMonth ||
      y !== activeYear ||
      acc !== activeAccountId ||
      sbu !== activeSbu ||
      pType !== activeProjectType;

    if (!shouldFetch) {
      setLoading(false);
      return;
    }

    // Sync state + sessionStorage
    setActiveMonth(m);
    setActiveYear(y);
    setActiveAccountId(acc);
    if (accName) setActiveAccountName(accName);
    if (sbu)     setActiveSbu(sbu);
    if (pType)   setActiveProjectType(pType);

    sessionStorage.setItem('lastFetchedProjectMonth', m);
    sessionStorage.setItem('lastFetchedProjectYear', y);
    sessionStorage.setItem('lastFetchedProjectAccountId', acc);
    if (accName) sessionStorage.setItem('lastFetchedProjectAccountName', accName);
    if (sbu)     sessionStorage.setItem('lastFetchedProjectSbu', sbu);
    if (pType)   sessionStorage.setItem('lastFetchedProjectType', pType);

    // Fire the API call
    (async () => {
      setLoading(true);
      setError(null);

      try {
        const resp = await fetch(`${BACKEND_URL}/api/project`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            month: m,
            year: y,
            accId: acc,
            projectType: pType,
          }),
        });
        if (!resp.ok) {
          const txt = await resp.text();
          throw new Error(`status: ${resp.status}, ${txt || resp.statusText}`);
        }

        const json = await resp.json();
        const list = Array.isArray(json) ? json : [json].filter(Boolean);
        setProjects(list);
        setColumnFilters({});

        if (list.length > 0 && list[0].accountName) {
          setActiveAccountName(list[0].accountName);
        }
      } catch (e) {
        setError(`Failed to fetch project data: ${e.message}`);
      } finally {
        setLoading(false);
      }
    })();
  }, [
    location.state,
    urlAccountId,
    activeMonth,
    activeYear,
    activeAccountId,
    activeSbu,
    activeProjectType,
  ]);

  // Initialize / re-init DataTable when data arrives
  useEffect(() => {
    if (loading || !projects.length || !tableRef.current) return;
    const $tbl = $(tableRef.current);

    // Destroy old instance
    if ($.fn.DataTable.isDataTable($tbl)) {
      $tbl.DataTable().destroy();
    }

    // Instantiate fresh
    setTimeout(() => {
      const dt = $tbl.DataTable({
        data: projects,
        columns: [
          { data: 'projectId',   title: 'Project ID' },
          { data: 'projectName', title: 'Project Name' },
          {
            data: 'totalAssociatesCount',
            title: 'Total Associates',
          },
          { data: 'totalCompanyHours', title: 'Company Hours' },
          { data: 'totalClientHours',  title: 'Client Hours' },
          { data: 'varianceHours',     title: 'Variance Hours' },
          {
            data:    'revenue',
            title:   'Revenue',
            render:  (v) => formatCurrency(v),
          },
          {
            data:       null,
            title:      'View',
            orderable:  false,
            className:  'text-center',
            defaultContent:
              '<button class="btn btn-sm btn-outline-primary rounded-circle view-btn" title="View Associates">üîç</button>',
          },
        ],
        paging:     true,
        searching:  true,
        ordering:   false,
        info:       true,
        autoWidth:  false,
        createdRow: (row, data) => {
          // Attach React navigate handler
          $(row)
            .find('button.view-btn')
            .on('click', () =>
              navigate(
                `/projects/${data.projectId}/associates`,
                {
                  state: {
                    projectId:   data.projectId,
                    projectName: data.projectName,
                    accountId:   data.accountId,
                    accountName: data.accountName,
                    month:       activeMonth,
                    year:        activeYear,
                    sbu:         activeSbu,
                    projectType: activeProjectType,
                  },
                }
              )
            );
        },
      });

      tableRef.current.dataTableInstance = dt;
    }, 0);

    // Cleanup on unmount / re-init
    return () => {
      const inst = tableRef.current?.dataTableInstance;
      if (inst) {
        inst.destroy();
        tableRef.current.dataTableInstance = null;
      }
    };
  }, [loading, projects]);

  // Apply columnFilters (via DataTables API) when dropdowns change
  useEffect(() => {
    const inst = tableRef.current?.dataTableInstance;
    if (!inst) return;

    // Clear existing column searches
    inst.columns().search('');

    // Map col-keys ‚Üí table indices
    const colMap = {
      projectId:             0,
      projectName:           1,
      totalAssociatesCount:  2,
      totalCompanyHours:     3,
      totalClientHours:      4,
      varianceHours:         5,
      revenue:               6,
    };

    Object.entries(columnFilters).forEach(([col, val]) => {
      if (val != null && colMap[col] != null) {
        const idx  = colMap[col];
        const term = String(val).trim();
        // Exact match, case-insensitive
        inst.column(idx).search(`^${term}$`, true, false);
      }
    });

    inst.draw();
  }, [columnFilters]);

  // Breadcrumb path
  const breadcrumbPath = [
    { name: 'PMO Dashboard', page: '' },
    {
      name: 'Revenue Forecast - Early View',
      page: 'upload',
    },
    activeSbu && {
      name: `${activeSbu} SBU Level`,
      page: 'sbu',
      state: { month: activeMonth, year: activeYear, sbu: activeSbu },
    },
    {
      name: 'Account Level',
      page: 'accounts',
      state: { month: activeMonth, year: activeYear, sbu: activeSbu },
    },
    activeProjectType && {
      name: `${activeProjectType} Project Type`,
      page: `accounts/${activeAccountId}/project-types`,
      state: {
        month: activeMonth,
        year: activeYear,
        sbu: activeSbu,
        accId: activeAccountId,
      },
    },
    {
      name: `Projects (${activeAccountName})`,
      page: `accounts/${activeAccountId}/projects`,
    },
  ].filter(Boolean);

  // Loading / Error UIs
  if (loading) {
    return (
      <div className="min-vh-100 d-flex justify-content-center align-items-center">
        <div className="spinner-border text-primary" role="status" />
        <p className="ms-3 text-primary">Loading Project Data...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-vh-100 d-flex flex-column justify-content-center align-items-center text-danger">
        <p className="fs-4">Error: {error}</p>
        <button
          className="btn btn-primary mt-3"
          onClick={() =>
            navigate('/accounts', {
              state: {
                month: activeMonth,
                year: activeYear,
                sbu: activeSbu,
              },
            })
          }
        >
          Go to Account Level
        </button>
      </div>
    );
  }

  // Main render
  return (
    <div
      className="min-vh-100 d-flex flex-column align-items-center px-3 py-4"
      style={{
        background:   'linear-gradient(to bottom right, #f5f7fa, #e9ecef)',
        fontFamily: "'Poppins', sans-serif",
      }}
    >
      <div
        className="card shadow p-4 p-md-5 w-100"
        style={{
          maxWidth:      '1200px',
          backgroundColor: '#ffffffdd',
          borderRadius:  '1rem',
        }}
      >
        <Breadcrumbs path={breadcrumbPath} />

        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2 className="mb-0 fw-semibold text-dark">
            üìÅ Project Level Overview{' '}
            {activeAccountId ? `for ${activeAccountName}` : ''}
          </h2>
          <button
            className="btn btn-outline-success rounded-pill px-3"
            onClick={handleDownloadData}
            title="Download Project Data"
          >
            <i className="bi bi-download me-2" /> Download Data
          </button>
        </div>

        <div className="table-responsive rounded">
          <table
            ref={tableRef}
            className="display table table-hover align-middle table-borderless mb-0"
            style={{ width: '100%' }}
          >
            <thead
              className="text-white"
              style={{
                background: 'linear-gradient(to right, #3b82f6, #2563eb)',
              }}
            >
              <tr>
                {[
                  { key: 'projectId',             label: 'Project ID' },
                  { key: 'projectName',           label: 'Project Name' },
                  { key: 'totalAssociatesCount',  label: 'Total Associates' },
                  { key: 'totalCompanyHours',     label: 'Company Hours' },
                  { key: 'totalClientHours',      label: 'Client Hours' },
                  { key: 'varianceHours',         label: 'Variance Hours' },
                  { key: 'revenue',               label: 'Revenue' },
                ].map(({ key, label }) => (
                  <th key={key}>
                    {label}
                    <select
                      className="form-select form-select-sm mt-1"
                      onChange={(e) =>
                        handleFilterChange(key, e.target.value)
                      }
                      value={columnFilters[key] ?? 'All'}
                    >
                      {getUniqueOptions(key).map((opt) => (
                        <option key={opt} value={opt}>
                          {opt}
                        </option>
                      ))}
                    </select>
                  </th>
                ))}
                <th className="text-center">View</th>
              </tr>
            </thead>
            {/* tbody is left empty‚ÄîDataTables will populate it */}
            <tbody />
          </table>
        </div>
      </div>
    </div>
  );
};

export default ProjectLevel;
